const structuralIntegrityScore = Math.max(1, Math.min(10, inputs.structuralIntegrityScore));
const accessControlPoints = Math.max(0, Math.min(10, inputs.accessControlPoints));
const surveillanceCoverage = Math.max(0, Math.min(100, inputs.surveillanceCoverage));
const alarmSystemGrade = Math.max(0, Math.min(3, inputs.alarmSystemGrade));
const geographicalRiskFactor = Math.max(1, Math.min(10, inputs.geographicalRiskFactor));
const perimeterSecurityRating = Math.max(0, Math.min(5, inputs.perimeterSecurityRating));
const responseTime = Math.max(1, Math.min(60, inputs.responseTime));

// 1. Normalize and Calculate Component Scores (out of 100 each)
const structScore = structuralIntegrityScore * 10;
const accessScore = accessControlPoints * 10;
const survScore = surveillanceCoverage;
const alarmScore = (alarmSystemGrade / 3) * 100;
const perimeterScore = (perimeterSecurityRating / 5) * 100;

// 2. Calculate Weighted Base Score
// Weights: Structural (0.25), Access (0.2), Surveillance (0.15), Alarm (0.15), Perimeter (0.1)
const weightedBaseSum = (
  structScore * 0.25 +
  accessScore * 0.2 +
  survScore * 0.15 +
  alarmScore * 0.15 +
  perimeterScore * 0.1
);

// Normalize weightedBaseSum back to a 0-100 scale based on max possible weighted sum
const maxWeightedSum = (100 * 0.25) + (100 * 0.2) + (100 * 0.15) + (100 * 0.15) + (100 * 0.1); // = 85
let normalizedBaseScore = (weightedBaseSum / maxWeightedSum) * 100;
normalizedBaseScore = Math.min(100, normalizedBaseScore);

// 3. Apply Risk Deductions
// Geographical Risk Deduction: Higher risk factor leads to higher deduction
// (1=Low, 10=High) -> 0 to 22.5 points deduction (2.5 points per step above 1)
const geoDeduction = (geographicalRiskFactor - 1) * 2.5;

// Response Time Deduction: Longer response time leads to higher deduction
// 0 deduction for <= 5 minutes. 1.5 points per minute above 5 minutes.
// Max deduction for response time capped at 30 points.
let responseDeduction = Math.max(0, (responseTime - 5) * 1.5);
responseDeduction = Math.min(responseDeduction, 30); // Cap response time deduction

// 4. Calculate Final Resilience Score
let resilienceScore = normalizedBaseScore - geoDeduction - responseDeduction;
resilienceScore = Math.max(0, Math.min(100, resilienceScore)); // Ensure score is between 0 and 100

// 5. Determine Risk Level
let riskLevel;
if (resilienceScore >= 85) {
  riskLevel = 'Excellent';
} else if (resilienceScore >= 70) {
  riskLevel = 'Good';
} else if (resilienceScore >= 50) {
  riskLevel = 'Moderate';
} else if (resilienceScore >= 30) {
  riskLevel = 'Poor';
} else {
  riskLevel = 'Critical';
}

return {
  resilienceScore: resilienceScore,
  riskLevel: riskLevel
};
