const BASE_DEFENSIBLE_SPACE_COST_PER_ACRE = 1500; 
const MATERIAL_UPGRADE_COST_PER_SQFT_ROOF = 7; 
const MATERIAL_UPGRADE_COST_PER_SQFT_SIDING = 5; 
const BASE_WILDFIRE_PROBABILITY = 0.003; 
const AVG_HOUSE_SQFT_PER_ACRE = 2000; 
const MAX_RISK_SCORE = 100; 

const WEIGHT_PROXIMITY = 0.25; 
const WEIGHT_SLOPE = 0.20; 
const WEIGHT_VEGETATION = 0.20; 
const WEIGHT_ROOF_FLAMMABILITY = 0.20; 
const WEIGHT_SIDING_FLAMMABILITY = 0.15; 

const DEF_SPACE_RISK_REDUCTION_FACTOR = 0.7; 
const ROOF_UPGRADE_RISK_REDUCTION_FACTOR = 0.8; 
const SIDING_UPGRADE_RISK_REDUCTION_FACTOR = 0.6; 

function calculate({
    property_size_acres,
    wildland_proximity_meters,
    roof_flammability_factor,
    siding_flammability_factor,
    slope_grade_percent,
    existing_defensible_space_percent,
    vegetation_density_factor,
    property_market_value
}) {

    let proximity_risk = Math.max(0, 1 - (wildland_proximity_meters / 500)); 
    proximity_risk = Math.min(1, proximity_risk); 

    let slope_risk = slope_grade_percent / 50; 
    slope_risk = Math.min(1, Math.max(0, slope_risk)); 

    let vegetation_risk = vegetation_density_factor; 

    let roof_risk = roof_flammability_factor; 
    let siding_risk = siding_flammability_factor; 

    let initial_risk_score_raw = 
        (proximity_risk * WEIGHT_PROXIMITY) + 
        (slope_risk * WEIGHT_SLOPE) + 
        (vegetation_risk * WEIGHT_VEGETATION) + 
        (roof_risk * WEIGHT_ROOF_FLAMMABILITY) + 
        (siding_risk * WEIGHT_SIDING_FLAMMABILITY); 

    let initial_wildfire_risk_score = Math.round(initial_risk_score_raw * MAX_RISK_SCORE); 
    initial_wildfire_risk_score = Math.max(0, Math.min(MAX_RISK_SCORE, initial_wildfire_risk_score)); 

    const annual_property_damage_risk = property_market_value * BASE_WILDFIRE_PROBABILITY * (initial_wildfire_risk_score / MAX_RISK_SCORE); 

    let defensible_space_cost_estimate = 0; 
    if (existing_defensible_space_percent < 100) {
        defensible_space_cost_estimate = property_size_acres * BASE_DEFENSIBLE_SPACE_COST_PER_ACRE * (100 - existing_defensible_space_percent) / 100; 
    }

    const assumed_house_sqft = property_size_acres > 0 ? property_size_acres * AVG_HOUSE_SQFT_PER_ACRE : AVG_HOUSE_SQFT_PER_ACRE; 
    const assumed_roof_sqft = assumed_house_sqft * 1.2; 
    const assumed_siding_sqft = assumed_house_sqft * 1.5; 

    let material_upgrade_cost_estimate = 0; 
    if (roof_flammability_factor > 0.2) { 
        material_upgrade_cost_estimate += assumed_roof_sqft * MATERIAL_UPGRADE_COST_PER_SQFT_ROOF; 
    }
    if (siding_flammability_factor > 0.2) { 
        material_upgrade_cost_estimate += assumed_siding_sqft * MATERIAL_UPGRADE_COST_PER_SQFT_SIDING; 
    }

    const total_mitigation_cost = defensible_space_cost_estimate + material_upgrade_cost_estimate; 

    let mitigated_risk_score_raw = initial_risk_score_raw; 

    if (existing_defensible_space_percent < 100) {
        const def_space_improvement_factor = (100 - existing_defensible_space_percent) / 100; 
        const proximity_risk_reduced = proximity_risk * WEIGHT_PROXIMITY * DEF_SPACE_RISK_REDUCTION_FACTOR * def_space_improvement_factor; 
        const vegetation_risk_reduced = vegetation_risk * WEIGHT_VEGETATION * DEF_SPACE_RISK_REDUCTION_FACTOR * def_space_improvement_factor; 
        mitigated_risk_score_raw -= (proximity_risk_reduced + vegetation_risk_reduced); 
    }

    if (roof_flammability_factor > 0.2) {
        const roof_risk_reduced = roof_risk * WEIGHT_ROOF_FLAMMABILITY * ROOF_UPGRADE_RISK_REDUCTION_FACTOR; 
        mitigated_risk_score_raw -= roof_risk_reduced; 
    }
    if (siding_flammability_factor > 0.2) {
        const siding_risk_reduced = siding_risk * WEIGHT_SIDING_FLAMMABILITY * SIDING_UPGRADE_RISK_REDUCTION_FACTOR; 
        mitigated_risk_score_raw -= siding_risk_reduced; 
    }

    mitigated_risk_score_raw = Math.max(0, mitigated_risk_score_raw); 

    const mitigated_wildfire_risk_score = Math.round(mitigated_risk_score_raw * MAX_RISK_SCORE); 

    let annual_risk_reduction_percent = 0; 
    if (initial_wildfire_risk_score > 0) {
        annual_risk_reduction_percent = ((initial_wildfire_risk_score - mitigated_wildfire_risk_score) / initial_wildfire_risk_score) * 100; 
        annual_risk_reduction_percent = Math.min(100, Math.max(0, annual_risk_reduction_percent)); 
    }

    let payback_period_years = 0; 
    const annual_risk_reduction_value = annual_property_damage_risk * (annual_risk_reduction_percent / 100); 

    if (annual_risk_reduction_value > 0) {
        payback_period_years = total_mitigation_cost / annual_risk_reduction_value; 
    } else if (total_mitigation_cost > 0) {
        payback_period_years = Infinity; 
    } else {
        payback_period_years = 0; 
    }
    if (payback_period_years > 200) payback_period_years = Infinity; 

    return {
        initial_wildfire_risk_score: initial_wildfire_risk_score,
        annual_property_damage_risk: annual_property_damage_risk,
        defensible_space_cost_estimate: defensible_space_cost_estimate,
        material_upgrade_cost_estimate: material_upgrade_cost_estimate,
        total_mitigation_cost: total_mitigation_cost,
        annual_risk_reduction_percent: annual_risk_reduction_percent,
        payback_period_years: payback_period_years
    };
}
