function calculate(inputs) {
    const { householdSize, grossMonthlyIncome, liquidAssets, stateOfResidence, isElderlyOrDisabled, monthlyChildcareCosts = 0, monthlyMedicalExpenses = 0, monthlyShelterCosts = 0 } = inputs;

    let snapEligibility = 'Likely Ineligible';
    let medicaidEligibility = 'Likely Ineligible';
    let tanfEligibility = 'Likely Ineligible';
    let estimatedAnnualBenefits = 0;
    let netAnnualEconomicCost = 0;
    let warningMessage = '';

    // --- Constants and FPL Data (Simplified for demonstration) ---
    // Annual Federal Poverty Levels (FPL) for 2024 (approximate)
    const fplAnnual = {
        1: 15060,
        2: 20440,
        3: 25820,
        4: 31200,
        5: 36580,
        6: 41960,
        7: 47340,
        8: 52720
    };
    // For household sizes > 8, add $5380 per person annually
    const getFplAnnual = (size) => {
        if (size <= 8) return fplAnnual[size];
        return fplAnnual[8] + (size - 8) * 5380;
    };

    const currentFplAnnual = getFplAnnual(householdSize);
    const currentFplMonthly = currentFplAnnual / 12;

    // Medicaid Expansion States (a subset for demonstration)
    const medicaidExpansionStates = ['california', 'new york', 'massachusetts', 'illinois', 'michigan', 'pennsylvania', 'washington', 'oregon', 'colorado', 'minnesota', 'vermont', 'maryland', 'new jersey', 'connecticut', 'rhode island', 'new hampshire', 'maine', 'hawaii', 'new mexico', 'arizona', 'nevada', 'utah', 'idaho', 'montana', 'north dakota', 'south dakota', 'nebraska', 'kansas', 'oklahoma', 'arkansas', 'missouri', 'louisiana', 'kentucky', 'ohio', 'indiana', 'west virginia', 'virginia', 'delaware', 'district of columbia'].map(s => s.toLowerCase());
    const isMedicaidExpansionState = medicaidExpansionStates.includes(stateOfResidence.toLowerCase());

    // SNAP Max Allotments (Simplified, monthly for FY2024)
    const snapMaxAllotments = {
        1: 291,
        2: 535,
        3: 766,
        4: 973,
        5: 1155,
        6: 1386,
        7: 1532,
        8: 1751
    };
    const getSnapMaxAllotment = (size) => {
        if (size <= 8) return snapMaxAllotments[size];
        // For sizes > 8, add approx. $219 per person (based on federal guideline)
        return snapMaxAllotments[8] + (size - 8) * 219;
    };

    // --- Program Eligibility Calculations ---

    // 1. SNAP (Supplemental Nutrition Assistance Program)
    let snapEligibleGrossIncome = currentFplMonthly * 1.30;
    let snapEligibleNetIncome = currentFplMonthly;
    let snapAssetLimit = (isElderlyOrDisabled === 1) ? 4250 : 2750;

    // SNAP Income Deductions (simplified)
    let earnedIncomeDeduction = grossMonthlyIncome * 0.20;
    let standardDeduction;
    if (householdSize === 1 || householdSize === 2) standardDeduction = 198;
    else if (householdSize === 3) standardDeduction = 198;
    else if (householdSize === 4) standardDeduction = 208;
    else if (householdSize === 5) standardDeduction = 242;
    else standardDeduction = 242; // For 6+ same as 5 for simplification

    let medicalDeduction = monthlyMedicalExpenses;
    if (medicalDeduction > 35) medicalDeduction -= 35; else medicalDeduction = 0; // Only amounts over $35 are deductible
    if (isElderlyOrDisabled !== 1) medicalDeduction = 0; // Only for elderly/disabled

    let childCareDeduction = monthlyChildcareCosts;

    let shelterDeduction = monthlyShelterCosts;
    let shelterExpenseLimit = 672; // Maximum shelter deduction for non-elderly/disabled
    if (isElderlyOrDisabled === 1) shelterExpenseLimit = Infinity; // No limit for elderly/disabled

    let grossIncomeMinusEarnedDeduction = grossMonthlyIncome - earnedIncomeDeduction;

    // Calculate Net Income for SNAP
    let netIncome = grossIncomeMinusEarnedDeduction - standardDeduction - childCareDeduction - medicalDeduction;
    // Shelter deduction logic (complex, simplified here for illustration)
    // Acknowledge this is a very simplified shelter deduction
    if (grossIncomeMinusEarnedDeduction > 0) {
        let shelterExcess = shelterDeduction - (netIncome * 0.50);
        if (shelterExcess > 0) {
            netIncome -= Math.min(shelterExcess, shelterExpenseLimit);
        }
    }

    // SNAP Eligibility Check
    let meetsGrossIncomeTest = grossMonthlyIncome <= snapEligibleGrossIncome;
    let meetsNetIncomeTest = netIncome <= snapEligibleNetIncome;
    let meetsAssetTest = liquidAssets <= snapAssetLimit;

    if (meetsAssetTest) {
        if (isElderlyOrDisabled === 1) {
            // Elderly/disabled only need to meet Net Income & Asset Test
            if (meetsNetIncomeTest) {
                snapEligibility = 'Likely Eligible';
            }
        } else {
            // Others need to meet Gross Income, Net Income, and Asset Test
            if (meetsGrossIncomeTest && meetsNetIncomeTest) {
                snapEligibility = 'Likely Eligible';
            }
        }
    }

    if (snapEligibility === 'Likely Eligible') {
        // Simplified SNAP benefit calculation
        let maxAllotment = getSnapMaxAllotment(householdSize);
        // A common calculation for benefit is Max Allotment - (Net Income * 0.30)
        // Ensuring the benefit doesn't go below 0
        let calculatedBenefit = Math.max(0, maxAllotment - (netIncome * 0.30));
        estimatedAnnualBenefits += calculatedBenefit * 12;
    }

    // 2. Medicaid
    const medicaidMAGIThreshold = currentFplMonthly * 1.38; // 138% FPL for expansion states

    if (isMedicaidExpansionState) {
        if (grossMonthlyIncome <= medicaidMAGIThreshold) {
            medicaidEligibility = 'Likely Eligible (Expansion State)';
            // Estimate annual Medicaid cost for an eligible household
            // Highly variable, using a simplified average based on household size
            if (householdSize === 1) estimatedAnnualBenefits += 8000;
            else if (householdSize === 2) estimatedAnnualBenefits += 14000;
            else estimatedAnnualBenefits += (8000 * Math.min(householdSize, 2)) + (4000 * Math.max(0, householdSize - 2)); // 2 adults, rest children approx.
        }
    } else {
        medicaidEligibility = 'Potentially Eligible (Non-Expansion State) - Eligibility highly restricted (e.g., specific categories like pregnant women, children, elderly, disabled, or very low income). Refer to state guidelines.';
        warningMessage += ' Medicaid eligibility in non-expansion states is much more restrictive. This calculator does not model specific categorical eligibility. ';
    }

    // 3. TANF (Temporary Assistance for Needy Families)
    // TANF is highly state-specific and generally has very low thresholds.
    // Using a very simplified ~50% FPL as a rough indicator.
    const tanfIncomeThreshold = currentFplMonthly * 0.50;
    const tanfAssetLimit = (isElderlyOrDisabled === 1) ? 3250 : 2000; // Even lower for TANF

    if (grossMonthlyIncome <= tanfIncomeThreshold && liquidAssets <= tanfAssetLimit) {
        tanfEligibility = 'Potentially Eligible (Very low income/assets required).';
        // Estimate annual TANF benefit (also highly variable)
        estimatedAnnualBenefits += 6000; // Simplified average annual household benefit
    } else if (grossMonthlyIncome > tanfIncomeThreshold || liquidAssets > tanfAssetLimit) {
        tanfEligibility = 'Likely Ineligible (Income/Assets typically too high).';
    }

    // --- Economic Impact Calculation ---
    // Net Annual Economic Cost is estimated as total benefits + a small administrative overhead.
    const adminOverheadRate = 0.05; // 5% administrative cost on benefits
    netAnnualEconomicCost = estimatedAnnualBenefits * (1 + adminOverheadRate);

    if (!warningMessage) {
        warningMessage = 'This is an estimate. Actual eligibility and benefit amounts vary by state, specific program rules, and individual circumstances. Consult official sources.';
    }

    return {
        snapEligibility,
        medicaidEligibility,
        tanfEligibility,
        estimatedAnnualBenefits,
        netAnnualEconomicCost,
        warningMessage
    };
}
