function calculate({
  expectedAttendance,
  eventDurationHours,
  activityRiskLevel,
  environmentalConditionsRisk,
  alcoholDrugFactor,
  accessibilityChallengesFactor,
  preExistingConditionPrevalence
}) {
  // 1. Determine Base Incident Rate (BIR) per 1000 attendees per hour
  let baseIncidentRatePer1000PerHour;
  switch (activityRiskLevel) {
    case 1: // Low: e.g., seated lecture, exhibition
      baseIncidentRatePer1000PerHour = 0.5;
      break;
    case 2: // Moderate-low: e.g., light public gathering, small market
      baseIncidentRatePer1000PerHour = 0.8;
      break;
    case 3: // Medium: e.g., concert, convention, moderate protest
      baseIncidentRatePer1000PerHour = 1.2;
      break;
    case 4: // High-medium: e.g., sporting event, active festival, larger protest
      baseIncidentRatePer1000PerHour = 1.8;
      break;
    case 5: // High: e.g., marathon, highly active protest, large outdoor festival
      baseIncidentRatePer1000PerHour = 2.5;
      break;
    default:
      baseIncidentRatePer1000PerHour = 1.0; // Default or error state
  }

  // 2. Apply general multipliers to incident rate
  let incidentMultiplier = 1.0;

  // Environmental conditions
  switch (environmentalConditionsRisk) {
    case 1: // Ideal
      incidentMultiplier *= 1.0;
      break;
    case 2: // Moderate
      incidentMultiplier *= 1.2;
      break;
    case 3: // High
      incidentMultiplier *= 1.5;
      break;
  }

  // Alcohol/Drug presence
  incidentMultiplier *= alcoholDrugFactor;

  // Pre-existing conditions: Each 1% adds 0.5% to incidents, up to a cap.
  const preExistingFactor = 1 + (Math.min(preExistingConditionPrevalence, 20) / 100 * 0.5);
  incidentMultiplier *= preExistingFactor;

  // 3. Calculate estimated total incidents
  const estimatedTotalMedicalIncidents = Math.max(1, Math.round(
    (expectedAttendance / 1000) * eventDurationHours * baseIncidentRatePer1000PerHour * incidentMultiplier
  ));

  // 4. Categorize incidents and adjust distribution based on specific risk factors
  let minorIncidents = estimatedTotalMedicalIncidents * 0.65; // Base 65%
  let moderateIncidents = estimatedTotalMedicalIncidents * 0.25; // Base 25%
  let severeIncidents = estimatedTotalMedicalIncidents * 0.10; // Base 10%

  // Adjustments based on activity risk (higher risk shifts towards more severe)
  if (activityRiskLevel >= 4) {
    minorIncidents -= estimatedTotalMedicalIncidents * 0.05; // -5%
    moderateIncidents += estimatedTotalMedicalIncidents * 0.03; // +3%
    severeIncidents += estimatedTotalMedicalIncidents * 0.02; // +2%
  }
  // Adjustments based on alcohol/drug factor (shifts towards moderate/severe trauma/intoxication)
  if (alcoholDrugFactor > 1) {
    const alcoholShift = estimatedTotalMedicalIncidents * (alcoholDrugFactor - 1) * 0.05; // 5% shift for every 0.1 factor above 1
    minorIncidents = Math.max(0, minorIncidents - alcoholShift);
    moderateIncidents += alcoholShift;
  }
  // Adjustments based on environmental conditions (shifts towards moderate/severe heat/cold related issues)
  if (environmentalConditionsRisk === 3) {
    minorIncidents -= estimatedTotalMedicalIncidents * 0.03; // -3%
    moderateIncidents += estimatedTotalMedicalIncidents * 0.03; // +3%
  }

  // Ensure non-negative incident counts and re-normalize if needed (simple sum check here)
  const currentTotal = minorIncidents + moderateIncidents + severeIncidents;
  if (currentTotal > estimatedTotalMedicalIncidents) {
      const overflow = currentTotal - estimatedTotalMedicalIncidents;
      minorIncidents = Math.max(0, minorIncidents - overflow * 0.5); // Distribute reduction
      moderateIncidents = Math.max(0, moderateIncidents - overflow * 0.3);
      severeIncidents = Math.max(0, severeIncidents - overflow * 0.2);
  }

  minorIncidents = Math.round(Math.max(0, minorIncidents));
  moderateIncidents = Math.round(Math.max(0, moderateIncidents));
  severeIncidents = Math.round(Math.max(0, severeIncidents));

  // Re-adjust total for rounding errors
  const adjustedTotalIncidents = minorIncidents + moderateIncidents + severeIncidents;


  // 5. Calculate personnel and resource requirements
  let firstAidPersonnelRequired = 0;
  let paramedicsNursesRequired = 0;
  let doctorsRequired = 0;
  let firstAidStationsRequired = 0;
  let ambulanceUnitsRequired = 0;

  // Minimum staffing based on attendance
  firstAidPersonnelRequired = Math.ceil(expectedAttendance / 500); // 1 per 500 attendees
  paramedicsNursesRequired = Math.ceil(expectedAttendance / 1500); // 1 per 1500 attendees
  doctorsRequired = Math.ceil(expectedAttendance / 5000); // 1 per 5000 attendees
  ambulanceUnitsRequired = Math.ceil(expectedAttendance / 7500); // 1 per 7500 attendees

  // Staffing based on incident load (add to minimums)
  firstAidPersonnelRequired += Math.ceil(minorIncidents / 10); // 1 per 10 minor incidents
  paramedicsNursesRequired += Math.ceil(moderateIncidents / 8); // 1 per 8 moderate incidents
  doctorsRequired += Math.ceil(severeIncidents / 3); // 1 per 3 severe incidents

  // First Aid Stations: Based on attendance and accessibility
  firstAidStationsRequired = Math.ceil(expectedAttendance / 2000); // 1 per 2000 attendees
  firstAidStationsRequired += Math.ceil((minorIncidents + moderateIncidents) / 25); // 1 per 25 minor/moderate incidents
  firstAidStationsRequired *= accessibilityChallengesFactor; // More stations for difficult access
  firstAidStationsRequired = Math.ceil(firstAidStationsRequired);

  // Ambulance units: Based on severe incidents and event duration
  ambulanceUnitsRequired += Math.ceil(severeIncidents / 2); // 1 per 2 severe incidents
  if (eventDurationHours > 8) {
      ambulanceUnitsRequired += Math.ceil(eventDurationHours / 8); // Additional unit for long events
  }

  // Ensure minimums for key resources
  firstAidPersonnelRequired = Math.max(1, firstAidPersonnelRequired);
  paramedicsNursesRequired = Math.max(1, paramedicsNursesRequired);
  doctorsRequired = Math.max(1, doctorsRequired === 0 && severeIncidents > 0 ? 1 : doctorsRequired); // At least 1 doctor if any severe incidents
  firstAidStationsRequired = Math.max(1, firstAidStationsRequired);
  ambulanceUnitsRequired = Math.max(1, ambulanceUnitsRequired);

  // 6. Calculate supply kits
  const basicMedicalSupplyKits = Math.ceil(firstAidStationsRequired * 5 + firstAidPersonnelRequired * 2); // 5 kits per station, 2 per basic staff
  const advancedMedicalSupplyKits = Math.ceil(paramedicsNursesRequired * 3 + doctorsRequired * 5); // 3 kits per paramedic/nurse, 5 per doctor

  return {
    estimatedTotalMedicalIncidents: adjustedTotalIncidents,
    firstAidPersonnelRequired: firstAidPersonnelRequired,
    paramedicsNursesRequired: paramedicsNursesRequired,
    doctorsRequired: doctorsRequired,
    firstAidStationsRequired: firstAidStationsRequired,
    ambulanceUnitsRequired: ambulanceUnitsRequired,
    basicMedicalSupplyKits: Math.max(1, basicMedicalSupplyKits),
    advancedMedicalSupplyKits: Math.max(1, advancedMedicalSupplyKits)
  };
}
