function calculateSafety() {
  const inputs = arguments[0];
  let totalRiskPoints = 0;
  const MAX_RISK_POINTS = 240; // Sum of max points from each factor

  // Points assigned for each input, higher points = higher risk
  const crowdDensityPoints = {
    1: 0, 2: 5, 3: 15, 4: 25, 5: 40
  }[inputs.crowd_density_perception] || 0;
  totalRiskPoints += crowdDensityPoints;

  const venueSecurityPoints = {
    1: 30, 2: 20, 3: 10, 4: 5, 5: 0
  }[inputs.venue_security_rating] || 0;
  totalRiskPoints += venueSecurityPoints;

  const exitVisibilityPoints = {
    1: 25, 2: 15, 3: 8, 4: 3, 5: 0
  }[inputs.exit_visibility_score] || 0;
  totalRiskPoints += exitVisibilityPoints;

  const communicationPlanPoints = {
    1: 20, 2: 12, 3: 7, 4: 3, 5: 0
  }[inputs.communication_plan_strength] || 0;
  totalRiskPoints += communicationPlanPoints;

  const personalPreparednessPoints = {
    1: 35, 2: 20, 3: 10, 4: 5, 5: 0
  }[inputs.personal_preparedness_level] || 0;
  totalRiskPoints += personalPreparednessPoints;

  const eventTypeRiskPoints = {
    1: 0, 2: 8, 3: 20, 4: 35, 5: 50
  }[inputs.event_type_risk] || 0;
  totalRiskPoints += eventTypeRiskPoints;

  const eventDurationPoints = Math.min(inputs.event_duration_hours * 1.5, 25);
  totalRiskPoints += eventDurationPoints;

  const firstAidAccessPoints = {
    1: 15, 2: 10, 3: 6, 4: 3, 5: 0
  }[inputs.first_aid_access_rating] || 0;
  totalRiskPoints += firstAidAccessPoints;

  // Calculate safety score (higher is safer)
  let safety_score = ((MAX_RISK_POINTS - totalRiskPoints) / MAX_RISK_POINTS) * 100;
  safety_score = Math.max(0, Math.min(100, safety_score)); // Cap between 0 and 100

  let risk_level = '';
  let precautions = [];

  // Determine Risk Level and general precautions
  if (safety_score >= 80) {
    risk_level = 'Low Risk';
    precautions.push('General vigilance is advised. Enjoy the event!');
  } else if (safety_score >= 60) {
    risk_level = 'Moderate Risk';
    precautions.push('Stay aware of your surroundings and have a meeting plan.');
  } else if (safety_score >= 40) {
    risk_level = 'Elevated Risk';
    precautions.push('Exercise increased caution. Familiarize yourself with exits and emergency procedures.');
  } else if (safety_score >= 20) {
    risk_level = 'High Risk';
    precautions.push('Significant vigilance is required. Consider if attending is truly necessary. Always have an exit strategy.');
  } else {
    risk_level = 'Critical Risk';
    precautions.push('Extreme caution. Strongly reconsider attending if conditions are as assessed. Prioritize personal safety above all else. Avoid unnecessary risks.');
  }

  // Add tailored precautions based on highest contributing risk factors
  const riskFactors = [
    { id: 'crowd_density_perception', value: inputs.crowd_density_perception, points: crowdDensityPoints, threshold: 25 }, // High risk starts at 4 or 5
    { id: 'venue_security_rating', value: inputs.venue_security_rating, points: venueSecurityPoints, threshold: 20 }, // High risk starts at 1 or 2
    { id: 'exit_visibility_score', value: inputs.exit_visibility_score, points: exitVisibilityPoints, threshold: 15 }, // High risk starts at 1 or 2
    { id: 'communication_plan_strength', value: inputs.communication_plan_strength, points: communicationPlanPoints, threshold: 12 }, // High risk starts at 1 or 2
    { id: 'personal_preparedness_level', value: inputs.personal_preparedness_level, points: personalPreparednessPoints, threshold: 20 }, // High risk starts at 1 or 2
    { id: 'event_type_risk', value: inputs.event_type_risk, points: eventTypeRiskPoints, threshold: 35 }, // High risk starts at 4 or 5
    { id: 'event_duration_hours', value: inputs.event_duration_hours, points: eventDurationPoints, threshold: 15 }, // High risk for >10 hours
    { id: 'first_aid_access_rating', value: inputs.first_aid_access_rating, points: firstAidAccessPoints, threshold: 10 } // High risk starts at 1 or 2
  ];

  // Sort by points to identify top contributors
  riskFactors.sort((a, b) => b.points - a.points);

  // Add specific recommendations for the top 2-3 most significant risk factors
  for (let i = 0; i < Math.min(riskFactors.length, 3); i++) {
    const factor = riskFactors[i];
    if (factor.points > 0 && factor.points >= factor.threshold) { // Only add if it's a significant contributor
      switch (factor.id) {
        case 'crowd_density_perception':
          precautions.push('Crowd Density: Actively plan escape routes. Avoid being crushed; try to move with the flow or find refuge.');
          break;
        case 'venue_security_rating':
          precautions.push('Venue Security: Be extra vigilant. Report any suspicious activities. Do not rely solely on official security.');
          break;
        case 'exit_visibility_score':
          precautions.push('Emergency Exits: Immediately identify multiple exits upon arrival. Keep them in mind and ensure they are unobstructed.');
          break;
        case 'communication_plan_strength':
          precautions.push('Communication: Establish a meeting point and communication plan with your group. Ensure your phone is charged and have backup methods.');
          break;
        case 'personal_preparedness_level':
          precautions.push('Personal Preparedness: Assemble a small emergency kit (water, snacks, first-aid basics). Inform someone of your whereabouts.');
          break;
        case 'event_type_risk':
          precautions.push('Event Type Risk: Monitor news and social media for real-time updates. Avoid engaging in confrontational situations. Consider leaving if tensions rise.');
          break;
        case 'event_duration_hours':
          precautions.push('Event Duration: Prepare for sustained physical and mental exertion. Stay hydrated and take breaks. Fatigue can reduce awareness.');
          break;
        case 'first_aid_access_rating':
          precautions.push('First Aid Access: Carry basic personal first aid. Know who to contact in an emergency and where medical points are, if available.');
          break;
      }
    }
  }

  // Ensure unique precautions
  precautions = [...new Set(precautions)];

  return {
    safety_score: Math.round(safety_score),
    risk_level: risk_level,
    tailored_precautions: precautions.join('\n- ')
  };
}
