function calculate({ initialSecurityInvestment, annualMaintenanceCost, currentAnnualLosses, securityEffectivenessReduction, publicTrustRatingIncreasePoints, monetaryValuePerPointOfTrustIncrease, projectLifespanYears, discountRate }) {

  // Input validation and sanitization
  initialSecurityInvestment = Math.max(0, initialSecurityInvestment || 0);
  annualMaintenanceCost = Math.max(0, annualMaintenanceCost || 0);
  currentAnnualLosses = Math.max(0, currentAnnualLosses || 0);
  securityEffectivenessReduction = Math.max(0, Math.min(100, securityEffectivenessReduction || 0));
  publicTrustRatingIncreasePoints = Math.max(0, Math.min(5, publicTrustRatingIncreasePoints || 0));
  monetaryValuePerPointOfTrustIncrease = Math.max(0, monetaryValuePerPointOfTrustIncrease || 0);
  projectLifespanYears = Math.max(1, projectLifespanYears || 1);
  discountRate = Math.max(0, Math.min(100, discountRate || 0));

  // Step 1: Calculate Annual Loss Prevention Savings
  const annualLossPreventionSavings = currentAnnualLosses * (securityEffectivenessReduction / 100);

  // Step 2: Calculate Annual Public Trust Monetary Benefit
  const annualPublicTrustMonetaryBenefit = publicTrustRatingIncreasePoints * monetaryValuePerPointOfTrustIncrease;

  // Step 3: Calculate Total Annual Benefit and Net Annual Operating Benefit
  const totalAnnualBenefit = annualLossPreventionSavings + annualPublicTrustMonetaryBenefit;
  const netAnnualOperatingBenefit = totalAnnualBenefit - annualMaintenanceCost;

  // Initialize outputs with default values
  let paybackPeriodYears = 'N/A';
  let roiPercentage = 'N/A';
  let netPresentValue = -initialSecurityInvestment; // Start NPV with initial outlay

  // Step 4: Calculate Payback Period
  if (netAnnualOperatingBenefit > 0) {
    paybackPeriodYears = initialSecurityInvestment / netAnnualOperatingBenefit;
    if (paybackPeriodYears <= 0) paybackPeriodYears = 0;
    if (paybackPeriodYears > projectLifespanYears) paybackPeriodYears = `> ${projectLifespanYears}`; // Exceeds project lifespan
  } else if (initialSecurityInvestment === 0 && netAnnualOperatingBenefit >= 0) {
    paybackPeriodYears = 0; // Immediate payback if no investment and non-negative benefit
  } else {
    paybackPeriodYears = 'Never'; // Or 'N/A'
  }

  // Step 5: Calculate Net Present Value (NPV)
  const rate = discountRate / 100;
  if (rate === 0) {
    netPresentValue = (netAnnualOperatingBenefit * projectLifespanYears) - initialSecurityInvestment;
  } else {
    for (let i = 1; i <= projectLifespanYears; i++) {
      netPresentValue += netAnnualOperatingBenefit / Math.pow(1 + rate, i);
    }
  }

  // Step 6: Calculate Return on Investment (ROI)
  // Calculate total net benefit over the lifespan for ROI
  const totalNetBenefitOverLifespan = (netAnnualOperatingBenefit * projectLifespanYears) - initialSecurityInvestment;

  if (initialSecurityInvestment > 0) {
    roiPercentage = (totalNetBenefitOverLifespan / initialSecurityInvestment) * 100;
  } else if (initialSecurityInvestment === 0 && netAnnualOperatingBenefit * projectLifespanYears > 0) {
    roiPercentage = 'Infinite'; // Positive benefit with no investment
  } else if (initialSecurityInvestment === 0 && netAnnualOperatingBenefit * projectLifespanYears <= 0) {
    roiPercentage = 0; // No investment, no positive benefit
  }

  return {
    annualLossPreventionSavings: annualLossPreventionSavings,
    annualPublicTrustMonetaryBenefit: annualPublicTrustMonetaryBenefit,
    totalAnnualBenefit: totalAnnualBenefit,
    netAnnualOperatingBenefit: netAnnualOperatingBenefit,
    paybackPeriodYears: paybackPeriodYears,
    roiPercentage: roiPercentage,
    netPresentValue: netPresentValue
  };
}
