function clamp(value, min, max) { return Math.max(min, Math.min(value, max)); }

// Input normalization and capping
const engRate = clamp(inputs.socialEngagementRate, 0, 100);
const socialSent = clamp(inputs.socialSentimentScore, 0, 100);
const mediaSent = clamp(inputs.mediaCoverageSentiment, 0, 100);
const msgConsist = clamp(inputs.messagingConsistencyScore, 0, 100);
const followGrowth = clamp(inputs.followerGrowthRate, 0, 500); // Capped at 500% growth
const mediaVolume = clamp(inputs.mediaMentionsVolume, 0, 20000); // Capped at 20,000 mentions
const scrutinyEvents = clamp(inputs.publicScrutinyEvents, 0, 10); // Capped at 10 events
const brandAlign = clamp(inputs.brandAlignmentScore, 0, 100);

// --- Calculate Authenticity Score ---
// Messaging Consistency: High weight, direct input
const wMsgConsist = msgConsist * 0.30;

// Actions-to-Words Alignment: High weight, direct input
const wBrandAlign = brandAlign * 0.30;

// Media Coverage Sentiment: Medium weight, reflects external perception
const wMediaSent_A = mediaSent * 0.20;

// Social Media Sentiment: Medium-low weight, reflects immediate audience perception
const wSocialSent_A = socialSent * 0.15;

// Public Scrutiny Events: Negative impact, scaled inversely
const scrutinyImpact = (10 - scrutinyEvents) / 10; // 10 events -> 0, 0 events -> 1
const wScrutiny = scrutinyImpact * 100 * 0.05; // Max 5 points deduction from potential 100

let authenticityScore = (
  wMsgConsist +
  wBrandAlign +
  wMediaSent_A +
  wSocialSent_A +
  wScrutiny
);
authenticityScore = clamp(authenticityScore, 0, 100);

// --- Calculate Engagement Score ---
// Social Media Engagement Rate: High weight, direct input
const wEngRate = engRate * 0.35;

// Annual Social Media Follower Growth Rate: Medium-high weight
// Normalize growth rate: 0% -> 0, 500% -> 100. (growth / 5)
const normalizedFollowGrowth = followGrowth / 5;
const wFollowGrowth = normalizedFollowGrowth * 0.25;

// Average Monthly Media Mentions Volume: Medium weight
// Normalize volume: 0 -> 0, 20000 -> 100. (volume / 200)
const normalizedMediaVolume = mediaVolume / 200;
const wMediaVolume = normalizedMediaVolume * 0.20;

// Aggregate Social Media Sentiment: Medium weight, positive sentiment drives engagement
const wSocialSent_E = socialSent * 0.20;

let engagementScore = (
  wEngRate +
  wFollowGrowth +
  wMediaVolume +
  wSocialSent_E
);
engagementScore = clamp(engagementScore, 0, 100);

// --- Calculate Overall Public Figure Authenticity & Engagement Score (PFAS) ---
// Weighted average: Authenticity often carries more long-term weight than pure engagement.
const overallPFAS = (authenticityScore * 0.60) + (engagementScore * 0.40);

return {
  authenticityScore: parseFloat(authenticityScore.toFixed(1)),
  engagementScore: parseFloat(engagementScore.toFixed(1)),
  overallPFAS: parseFloat(overallPFAS.toFixed(1))
};
