function calculate({
  dataExposureScore,
  associationRiskScore,
  negativeSentimentScore,
  controversialEngagementScore,
  historicalScrutinyMagnitude,
  digitalFootprintBreadth,
  networkInfluenceMultiplier
}) {
  // Input validation and clamping to ensure scores are within 0-10 and multiplier 1-5
  dataExposureScore = Math.max(0, Math.min(10, dataExposureScore));
  associationRiskScore = Math.max(0, Math.min(10, associationRiskScore));
  negativeSentimentScore = Math.max(0, Math.min(10, negativeSentimentScore));
  controversialEngagementScore = Math.max(0, Math.min(10, controversialEngagementScore));
  historicalScrutinyMagnitude = Math.max(0, Math.min(10, historicalScrutinyMagnitude));
  digitalFootprintBreadth = Math.max(0, Math.min(10, digitalFootprintBreadth));
  networkInfluenceMultiplier = Math.max(1, Math.min(5, networkInfluenceMultiplier));

  // Weights for reputation risk factors (sum to 1.0)
  const repWeightNegativeSentiment = 0.35;
  const repWeightAssociationRisk = 0.25;
  const repWeightControversialEngagement = 0.20;
  const repWeightHistoricalScrutiny = 0.20;

  // Weights for privacy risk factors (sum to 1.0)
  const privWeightDataExposure = 0.45;
  const privWeightFootprintBreadth = 0.35;
  const privWeightHistoricalScrutiny = 0.20;

  // Calculate base reputation risk score (0-10 scale)
  const baseReputationRisk = (
    negativeSentimentScore * repWeightNegativeSentiment +
    associationRiskScore * repWeightAssociationRisk +
    controversialEngagementScore * repWeightControversialEngagement +
    historicalScrutinyMagnitude * repWeightHistoricalScrutiny
  );

  // Calculate base privacy risk score (0-10 scale)
  const basePrivacyRisk = (
    dataExposureScore * privWeightDataExposure +
    digitalFootprintBreadth * privWeightFootprintBreadth +
    historicalScrutinyMagnitude * privWeightHistoricalScrutiny
  );

  // Apply network influence and scale to 0-100
  let reputationRiskScore = baseReputationRisk * networkInfluenceMultiplier * 10;
  let privacyRiskScore = basePrivacyRisk * networkInfluenceMultiplier * 10;

  // Ensure scores are within the 0-100 range
  reputationRiskScore = Math.min(100, Math.max(0, reputationRiskScore));
  privacyRiskScore = Math.min(100, Math.max(0, privacyRiskScore));

  // Determine overall digital risk level
  const averageRiskScore = (reputationRiskScore + privacyRiskScore) / 2;
  let overallDigitalRiskLevel;
  if (averageRiskScore < 20) {
    overallDigitalRiskLevel = 'Low';
  } else if (averageRiskScore < 50) {
    overallDigitalRiskLevel = 'Moderate';
  } else if (averageRiskScore < 80) {
    overallDigitalRiskLevel = 'High';
  } else {
    overallDigitalRiskLevel = 'Critical';
  }

  return {
    reputationRiskScore,
    privacyRiskScore,
    overallDigitalRiskLevel
  };
}
