function calculate({ association_severity, association_proximity, num_past_controversies, avg_past_controversy_severity, past_controversy_resolution, current_media_scrutiny, crisis_plan_effectiveness, public_trust_baseline }) {

  // Input validation and clamping
  association_severity = Math.max(1, Math.min(10, association_severity));
  association_proximity = Math.max(1, Math.min(5, association_proximity));
  num_past_controversies = Math.max(0, Math.min(10, num_past_controversies));
  avg_past_controversy_severity = Math.max(1, Math.min(10, avg_past_controversy_severity));
  past_controversy_resolution = Math.max(1, Math.min(5, past_controversy_resolution));
  current_media_scrutiny = Math.max(1, Math.min(10, current_media_scrutiny));
  crisis_plan_effectiveness = Math.max(1, Math.min(10, crisis_plan_effectiveness));
  public_trust_baseline = Math.max(1, Math.min(10, public_trust_baseline));

  // 1. Calculate Association Risk Score (0-25)
  // A weighted average of severity and proximity, scaled.
  const assoc_risk_base = (association_severity * 0.6 + association_proximity * 0.4);
  const assoc_risk = Math.min(25, assoc_risk_base * 2.5);

  // 2. Calculate Past Controversy Risk Score (0-25)
  // A weighted average of number and severity, then mitigated by resolution effectiveness.
  const controversy_risk_base = (num_past_controversies * 0.5 + avg_past_controversy_severity * 0.5);
  // Resolution factor: 1 (no mitigation for 1) to 0.3 (70% mitigation for 5)
  const resolution_factor = 1 - ((past_controversy_resolution - 1) / 4 * 0.7);
  const controversy_risk = Math.min(25, controversy_risk_base * resolution_factor * 2.5);

  // 3. Combine Inherent Risk (0-50)
  const inherent_risk = assoc_risk + controversy_risk;

  // 4. Apply Media Scrutiny Amplification (Multiplier 1.0 - 2.0)
  // Higher scrutiny significantly amplifies the base risk.
  const media_amplifier = 1 + (current_media_scrutiny - 1) / 9 * 1.0; // 1 -> 1.0, 10 -> 2.0
  const risk_after_media = inherent_risk * media_amplifier;

  // 5. Apply Crisis Communication Mitigation (Multiplier 0.5 - 1.0)
  // A robust plan can reduce risk significantly.
  const crisis_mitigator = 1 - ((crisis_plan_effectiveness - 1) / 9 * 0.5); // 1 -> 1.0, 10 -> 0.5 (up to 50% reduction)
  const risk_after_crisis_plan = risk_after_media * crisis_mitigator;

  // 6. Apply Public Trust Baseline Adjustment (Multiplier 0.7 - 1.3)
  // Low trust amplifies risk, high trust provides a buffer.
  const trust_adjuster = 1 + (5 - public_trust_baseline) / 5 * 0.3; // 1 -> 1.3, 5 -> 1.0, 10 -> 0.7
  let final_risk_score = risk_after_crisis_plan * trust_adjuster;

  // 7. Cap and Normalize to 0-100
  final_risk_score = Math.max(0, Math.min(100, final_risk_score));

  // 8. Determine Risk Category
  let risk_category;
  if (final_risk_score <= 30) {
    risk_category = 'Low Risk';
  } else if (final_risk_score <= 60) {
    risk_category = 'Medium Risk';
  } else {
    risk_category = 'High Risk';
  }

  return {
    reputation_risk_score: final_risk_score,
    risk_category: risk_category
  };
}
