const { affectedPopulation, projectedHospitalizationRate, projectedICURate, averageLOSGeneral, averageLOSICU, emergencyDurationDays, existingGeneralBeds, existingICUBeds, existingVentilators, ventilatorUsageRate } = inputs;

// --- Input Validation and Normalization ---
if (affectedPopulation <= 0 || projectedHospitalizationRate < 0 || projectedICURate < 0 ||
    averageLOSGeneral < 0 || averageLOSICU < 0 || emergencyDurationDays <= 0 ||
    existingGeneralBeds < 0 || existingICUBeds < 0 || existingVentilators < 0 ||
    ventilatorUsageRate < 0) {
    throw new Error("All input values must be positive, and rates cannot be negative.");
}

// Cap percentages at 100%
const hospRate = Math.min(100, projectedHospitalizationRate);
const icuRate = Math.min(100, projectedICURate);
const ventUsageRate = Math.min(100, ventilatorUsageRate);

// --- Core Calculations ---

// 1. Projected Patient Load (Cumulative over emergency duration)
const projectedHospitalizations = Math.ceil(affectedPopulation * (hospRate / 100));
const projectedICUAdmissions = Math.ceil(projectedHospitalizations * (icuRate / 100));
const projectedGeneralAdmissions = projectedHospitalizations - projectedICUAdmissions;

// Ensure at least one day for calculations to avoid division by zero
const effectiveEmergencyDays = Math.max(1, emergencyDurationDays);

// 2. Daily Admission Rates (Assumes even distribution for peak load calculation)
const dailyGeneralAdmissions = projectedGeneralAdmissions / effectiveEmergencyDays;
const dailyICUAdmissions = projectedICUAdmissions / effectiveEmergencyDays;

// 3. Peak Bed Requirements (Daily Census)
// This is a simplified model for peak concurrent bed demand, assuming a steady state over the LOS for those admitted daily.
const requiredGeneralBeds = Math.ceil(dailyGeneralAdmissions * averageLOSGeneral);
const requiredICUBeds = Math.ceil(dailyICUAdmissions * averageLOSICU);

// 4. Ventilator Requirements
// Ventilators are typically needed for a subset of ICU patients.
const requiredVentilators = Math.ceil(requiredICUBeds * (ventUsageRate / 100));

// 5. Healthcare Worker Requirements (Based on assumed standard ratios for peak demand)
// These are generally accepted standard recommended ratios (patients per worker) for acute care.
// Actual ratios can vary by specific clinical setting, severity, and local guidelines.
const defaultGeneralWorkerRatio = 6; // e.g., 6 patients per general ward worker
const defaultICUWorkerRatio = 2;     // e.g., 2 patients per ICU worker

const requiredGeneralWorkers = Math.ceil(requiredGeneralBeds / defaultGeneralWorkerRatio);
const requiredICUWorkers = Math.ceil(requiredICUBeds / defaultICUWorkerRatio);

// 6. Deficit/Surplus Calculation
const generalBedDeficit = requiredGeneralBeds - existingGeneralBeds;
const ICUBedDeficit = requiredICUBeds - existingICUBeds;
const ventilatorDeficit = requiredVentilators - existingVentilators;

return {
    projectedHospitalizations: projectedHospitalizations,
    projectedICUAdmissions: projectedICUAdmissions,
    requiredGeneralBeds: requiredGeneralBeds,
    generalBedDeficit: generalBedDeficit,
    requiredICUBeds: requiredICUBeds,
    ICUBedDeficit: ICUBedDeficit,
    requiredVentilators: requiredVentilators,
    ventilatorDeficit: ventilatorDeficit,
    requiredGeneralWorkers: requiredGeneralWorkers,
    requiredICUWorkers: requiredICUWorkers
};
