formula:
currentAnnualPHBudget = parseFloat(currentAnnualPHBudget) || 0;
fundingReductionPercentage = parseFloat(fundingReductionPercentage) || 0;
communityPopulation = parseInt(communityPopulation) || 0;
avgCostPerPreventiveService = parseFloat(avgCostPerPreventiveService) || 0;
baselinePreventiveServicesPerCapita = parseFloat(baselinePreventiveServicesPerCapita) || 0;
baselineImmunizationRate = parseFloat(baselineImmunizationRate) || 0;
immunizationImpactFactor = parseFloat(immunizationImpactFactor) || 0;
outbreakCostMultiplierPerPointDrop = parseFloat(outbreakCostMultiplierPerPointDrop) || 0;
criticalServicesThresholdPercentage = parseFloat(criticalServicesThresholdPercentage) || 0;
adminOverheadPercentage = parseFloat(adminOverheadPercentage) || 0;

// Input validation and bounds checking
fundingReductionPercentage = Math.max(0, Math.min(100, fundingReductionPercentage));
baselineImmunizationRate = Math.max(0, Math.min(100, baselineImmunizationRate));
criticalServicesThresholdPercentage = Math.max(0, Math.min(100, criticalServicesThresholdPercentage));
adminOverheadPercentage = Math.max(0, Math.min(99, adminOverheadPercentage)); // Overhead cannot be 100% or more

// Ensure no negative initial budget for calculations
if (currentAnnualPHBudget < 0) currentAnnualPHBudget = 0;
if (communityPopulation <= 0) communityPopulation = 1; // Avoid division by zero, represent a minimal community

// 1. Calculate Total Funding Reduction
const totalFundingReductionAmount = currentAnnualPHBudget * (fundingReductionPercentage / 100);
const newAnnualPHBudget = currentAnnualPHBudget - totalFundingReductionAmount;

// 2. Determine Effective Service Budget Reduction
// Assume administrative overhead is the last to be cut or fixed, making services bear the brunt first.
// The 'flexible budget' is what's available for services after fixed overhead.
const fixedOverheadAmount = currentAnnualPHBudget * (adminOverheadPercentage / 100);
const totalServiceBudget = currentAnnualPHBudget - fixedOverheadAmount;

let effectiveServiceBudgetReductionPercentage = 0;
let serviceFundingCutAmount = totalFundingReductionAmount; // Initial assumption: all cuts come from services

if (newAnnualPHBudget < fixedOverheadAmount) {
    // If the new budget is less than the fixed overhead, it means critical core functions are impacted,
    // and services are effectively cut by 100% as the department struggles to exist.
    serviceFundingCutAmount = totalServiceBudget;
    effectiveServiceBudgetReductionPercentage = 100; // Services are completely decimated
} else if (totalServiceBudget > 0) {
    // If the reduction primarily impacts the flexible service budget
    serviceFundingCutAmount = Math.min(totalFundingReductionAmount, totalServiceBudget); // Cannot cut more than available service budget
    effectiveServiceBudgetReductionPercentage = (serviceFundingCutAmount / totalServiceBudget) * 100;
    effectiveServiceBudgetReductionPercentage = Math.min(100, effectiveServiceBudgetReductionPercentage); // Cap at 100%
} else {
    // If totalServiceBudget is 0 or negative, it implies the entire budget is overhead or nonexistent
    effectiveServiceBudgetReductionPercentage = (totalFundingReductionAmount > 0) ? 100 : 0;
}

// 3. Estimate Impact on Preventive Services
const totalBaselinePreventiveServices = communityPopulation * baselinePreventiveServicesPerCapita;
// Assume reduction in services is proportional to the effective service budget reduction
const estimatedPreventiveServicesLost = totalBaselinePreventiveServices * (effectiveServiceBudgetReductionPercentage / 100);

// 4. Estimate Impact on Immunization Rate
// The immunization impact factor translates the overall budget cut percentage into a specific decline in coverage points.
const immunizationRateDropPoints = fundingReductionPercentage * immunizationImpactFactor;
const estimatedNewImmunizationRate = Math.max(0, baselineImmunizationRate - immunizationRateDropPoints);

// 5. Project Excess Outbreak Costs
let projectedExcessOutbreakCosts = 0;
const actualImmunizationRateDrop = baselineImmunizationRate - estimatedNewImmunizationRate; // Actual points dropped

if (actualImmunizationRateDrop > 0) {
    // Scale the multiplier by the actual population in 100k units
    const populationScaleFactor = communityPopulation / 100000;
    projectedExcessOutbreakCosts = actualImmunizationRateDrop * outbreakCostMultiplierPerPointDrop * populationScaleFactor;
}

// 6. Determine Population at Elevated Risk and Overall Risk Level
let populationAtElevatedRisk = 0;
// A simple model: population at risk is proportional to the lost services capacity
populationAtElevatedRisk = communityPopulation * (effectiveServiceBudgetReductionPercentage / 100);

let overallRiskLevel = "Low Risk";

// Define thresholds for risk levels based on funding reduction and immunization decline
const currentBudgetPercentage = (newAnnualPHBudget / currentAnnualPHBudget) * 100;

if (fundingReductionPercentage >= 5) overallRiskLevel = "Moderate Risk";
if (fundingReductionPercentage >= 15) overallRiskLevel = "High Risk";

// Critical risk if funding falls below critical services threshold or immunization rates drop dangerously low
if (currentBudgetPercentage < criticalServicesThresholdPercentage || estimatedNewImmunizationRate < (baselineImmunizationRate * 0.7)) { // 30% drop from baseline immunization is critical
    overallRiskLevel = "Critical Risk (Essential Services Threatened)";
}

// If the new budget is less than the fixed overhead, it's an existential threat
if (newAnnualPHBudget < fixedOverheadAmount && totalFundingReductionAmount > 0) {
    overallRiskLevel = "Catastrophic Risk (Departmental Collapse Imminent)";
}

return {
    totalFundingReductionAmount: totalFundingReductionAmount,
    newAnnualPHBudget: newAnnualPHBudget,
    effectiveServiceBudgetReductionPercentage: effectiveServiceBudgetReductionPercentage,
    estimatedPreventiveServicesLost: estimatedPreventiveServicesLost,
    estimatedNewImmunizationRate: estimatedNewImmunizationRate,
    projectedExcessOutbreakCosts: projectedExcessOutbreakCosts,
    populationAtElevatedRisk: Math.round(populationAtElevatedRisk),
    overallRiskLevel: overallRiskLevel
};
