function calculateROI(inputs) {
    const targetPopulationSize = Math.max(0, inputs.targetPopulationSize);
    const interventionCostPerPerson = Math.max(0, inputs.interventionCostPerPerson);
    const baselineDiseaseIncidenceRate = Math.max(0, inputs.baselineDiseaseIncidenceRate);
    const interventionEffectivenessRate = Math.max(0, Math.min(100, inputs.interventionEffectivenessRate)); // Cap at 100%
    const averageHealthcareCostPerCase = Math.max(0, inputs.averageHealthcareCostPerCase);
    const averageProductivityLossPerCase = Math.max(0, inputs.averageProductivityLossPerCase);
    const interventionDurationYears = Math.max(0, inputs.interventionDurationYears);
    const societalDiscountRate = Math.max(0, inputs.societalDiscountRate) / 100; // Convert to decimal

    if (targetPopulationSize === 0 || interventionDurationYears === 0) {
        return {
            totalInterventionCost: 0,
            preventedDiseaseCases: 0,
            totalSavedHealthcareCosts: 0,
            totalProductivityGains: 0,
            netSocietalBenefit: 0,
            returnOnInvestmentPercentage: 0
        };
    }

    // 1. Calculate Total Intervention Cost
    const totalInterventionCost = targetPopulationSize * interventionCostPerPerson;

    // 2. Calculate Annual Prevented Disease Cases
    const baselineCasesPerYear = targetPopulationSize * (baselineDiseaseIncidenceRate / 100000);
    const preventedCasesPerYear = baselineCasesPerYear * (interventionEffectivenessRate / 100);

    // Calculate total prevented cases over the duration (without discounting, for display purposes)
    const totalPreventedDiseaseCases = preventedCasesPerYear * interventionDurationYears;

    // 3. Calculate Annual Benefits
    const annualSavedHealthcareCosts = preventedCasesPerYear * averageHealthcareCostPerCase;
    const annualProductivityGains = preventedCasesPerYear * averageProductivityLossPerCase;
    const annualTotalBenefit = annualSavedHealthcareCosts + annualProductivityGains;

    // Function to calculate Present Value of an Annuity
    const calculatePVofAnnuity = (annuityPayment, rate, periods) => {
        if (periods === 0) return 0;
        if (rate === 0) return annuityPayment * periods;
        return annuityPayment * (1 - Math.pow(1 + rate, -periods)) / rate;
    };

    // 4. Calculate Total Benefits (Discounted Present Value)
    const totalSavedHealthcareCostsPV = calculatePVofAnnuity(annualSavedHealthcareCosts, societalDiscountRate, interventionDurationYears);
    const totalProductivityGainsPV = calculatePVofAnnuity(annualProductivityGains, societalDiscountRate, interventionDurationYears);
    const totalSocietalBenefitPV = totalSavedHealthcareCostsPV + totalProductivityGainsPV;

    // 5. Calculate Net Societal Benefit
    const netSocietalBenefit = totalSocietalBenefitPV - totalInterventionCost;

    // 6. Calculate Return on Investment (ROI)
    let returnOnInvestmentPercentage = 0;
    if (totalInterventionCost > 0) {
        returnOnInvestmentPercentage = (netSocietalBenefit / totalInterventionCost) * 100;
    } else if (netSocietalBenefit > 0) {
        returnOnInvestmentPercentage = Infinity; // Infinite ROI if cost is zero and benefit is positive
    } else {
        returnOnInvestmentPercentage = 0; // If both are zero or benefit is negative with zero cost
    }

    return {
        totalInterventionCost: totalInterventionCost,
        preventedDiseaseCases: totalPreventedDiseaseCases,
        totalSavedHealthcareCosts: totalSavedHealthcareCostsPV,
        totalProductivityGains: totalProductivityGainsPV,
        netSocietalBenefit: netSocietalBenefit,
        returnOnInvestmentPercentage: returnOnInvestmentPercentage
    };
}
