function calculate({
  interventionCost,
  targetPopulation,
  interventionDuration,
  baselineDiseasePrevalence,
  interventionEffectiveness,
  averageCostPerCase,
  averageDALYburdenPerCase,
  averageQALYgainPerCaseAverted,
  societalValuePerDALY,
  discountRate
}) {
  // Input validation and normalization
  interventionCost = Math.max(0, interventionCost || 0);
  targetPopulation = Math.max(0, targetPopulation || 0);
  interventionDuration = Math.max(1, interventionDuration || 1); // Duration must be at least 1 year
  baselineDiseasePrevalence = Math.min(100, Math.max(0, baselineDiseasePrevalence || 0));
  interventionEffectiveness = Math.min(100, Math.max(0, interventionEffectiveness || 0));
  averageCostPerCase = Math.max(0, averageCostPerCase || 0);
  averageDALYburdenPerCase = Math.max(0, averageDALYburdenPerCase || 0);
  averageQALYgainPerCaseAverted = Math.max(0, averageQALYgainPerCaseAverted || 0);
  societalValuePerDALY = Math.max(0, societalValuePerDALY || 0);
  discountRate = Math.max(0, discountRate || 0);

  const annualDiseasePrevalenceRate = baselineDiseasePrevalence / 100;
  const annualInterventionEffectivenessRate = interventionEffectiveness / 100;
  const annualDiscountFactor = discountRate / 100;

  let totalDiscountedHealthcareSavings = 0;
  let totalDiscountedMonetizedDALYBenefits = 0;
  let totalDiscountedAvertedDALYs = 0;
  let totalDiscountedQALYsGained = 0;

  // Calculate annual averted cases and benefits, then discount over duration
  for (let year = 1; year <= interventionDuration; year++) {
    const discountMultiplier = 1 / Math.pow(1 + annualDiscountFactor, year);

    // Annual cases averted calculation (assuming effectiveness is applied to the *prevalent* cases each year)
    // This simplifies annual disease burden reduction without complex epidemiological modeling.
    const annualCasesAverted = targetPopulation * annualDiseasePrevalenceRate * annualInterventionEffectivenessRate;

    const annualHealthcareSavings = annualCasesAverted * averageCostPerCase;
    const annualAvertedDALYs = annualCasesAverted * averageDALYburdenPerCase;
    const annualQALYsGained = annualCasesAverted * averageQALYgainPerCaseAverted;
    const annualMonetizedDALYBenefits = annualAvertedDALYs * societalValuePerDALY;

    totalDiscountedHealthcareSavings += annualHealthcareSavings * discountMultiplier;
    totalDiscountedMonetizedDALYBenefits += annualMonetizedDALYBenefits * discountMultiplier;
    totalDiscountedAvertedDALYs += annualAvertedDALYs * discountMultiplier;
    totalDiscountedQALYsGained += annualQALYsGained * discountMultiplier;
  }

  const totalBenefits = totalDiscountedHealthcareSavings + totalDiscountedMonetizedDALYBenefits;
  const netROI = totalBenefits - interventionCost;

  let roiPercentage = 0;
  if (interventionCost > 0) {
    roiPercentage = (netROI / interventionCost) * 100;
  } else if (totalBenefits > 0) {
    roiPercentage = Infinity; // Infinite ROI if cost is zero and benefits exist
  }

  let costPerDALY = Infinity;
  if (totalDiscountedAvertedDALYs > 0) {
    costPerDALY = interventionCost / totalDiscountedAvertedDALYs;
  }

  let costPerQALY = Infinity;
  if (totalDiscountedQALYsGained > 0) {
    costPerQALY = interventionCost / totalDiscountedQALYsGained;
  }

  return {
    netROI: netROI,
    roiPercentage: roiPercentage,
    totalAvertedDALYs: totalDiscountedAvertedDALYs,
    totalHealthcareSavings: totalDiscountedHealthcareSavings,
    totalQALYsGained: totalDiscountedQALYsGained,
    costPerDALY: costPerDALY,
    costPerQALY: costPerQALY
  };
}
