function calculate({
  initialInvestmentCost,
  projectDurationYears,
  populationServed,
  baselineDiseasePrevalence,
  interventionEffectivenessReduction,
  avgTreatmentCostPerCase,
  productivityLossPerCase,
  qualityAdjustedLifeYearsGainedPerCase,
  societalValuePerQALY,
  discountRate
}) {
  // Input validation and normalization
  initialInvestmentCost = Math.max(0, initialInvestmentCost || 0);
  projectDurationYears = Math.max(1, projectDurationYears || 1);
  populationServed = Math.max(1, populationServed || 1);
  baselineDiseasePrevalence = Math.max(0, Math.min(100, baselineDiseasePrevalence || 0));
  interventionEffectivenessReduction = Math.max(0, Math.min(100, interventionEffectivenessReduction || 0));
  avgTreatmentCostPerCase = Math.max(0, avgTreatmentCostPerCase || 0);
  productivityLossPerCase = Math.max(0, productivityLossPerCase || 0);
  qualityAdjustedLifeYearsGainedPerCase = Math.max(0, qualityAdjustedLifeYearsGainedPerCase || 0);
  societalValuePerQALY = Math.max(0, societalValuePerQALY || 0);
  discountRate = Math.max(0, Math.min(100, discountRate || 0)) / 100; // Convert to decimal

  let totalUndiscountedBenefits = 0;
  let totalAvoidedTreatmentCostsAccumulated = 0;
  let totalAvoidedProductivityLossAccumulated = 0;
  let totalSocietalValueFromQALYsAccumulated = 0;
  let discountedBenefits = 0;

  // Calculate annual avoided cases
  const baselineCasesAnnually = populationServed * (baselineDiseasePrevalence / 100);
  const casesAvoidedAnnually = baselineCasesAnnually * (interventionEffectivenessReduction / 100);

  if (casesAvoidedAnnually <= 0) {
      return {
          totalAvoidedTreatmentCosts: 0,
          totalAvoidedProductivityLoss: 0,
          totalSocietalValueFromQALYs: 0,
          totalProjectBenefits: 0,
          netPresentValue: -initialInvestmentCost,
          returnOnInvestment: (initialInvestmentCost > 0) ? -100 : 0, // If no benefits and no cost, ROI is 0
          benefitCostRatio: 0
      };
  }

  // Loop through each year of the project duration to apply discounting
  for (let year = 1; year <= projectDurationYears; year++) {
    // Annual benefits for this year (undiscounted)
    const annualAvoidedTreatmentCosts = casesAvoidedAnnually * avgTreatmentCostPerCase;
    const annualAvoidedProductivityLoss = casesAvoidedAnnually * productivityLossPerCase;
    const annualQALYsGained = casesAvoidedAnnually * qualityAdjustedLifeYearsGainedPerCase;
    const annualSocietalValueFromQALYs = annualQALYsGained * societalValuePerQALY;

    const annualTotalBenefits = annualAvoidedTreatmentCosts +
                                annualAvoidedProductivityLoss +
                                annualSocietalValueFromQALYs;

    // Accumulate total undiscounted benefits for output
    totalUndiscountedBenefits += annualTotalBenefits;
    totalAvoidedTreatmentCostsAccumulated += annualAvoidedTreatmentCosts;
    totalAvoidedProductivityLossAccumulated += annualAvoidedProductivityLoss;
    totalSocietalValueFromQALYsAccumulated += annualSocietalValueFromQALYs;

    // Discount factor for the current year
    const discountFactor = 1 / Math.pow(1 + discountRate, year);
    discountedBenefits += annualTotalBenefits * discountFactor;
  }

  const netPresentValue = discountedBenefits - initialInvestmentCost;

  let returnOnInvestment = 0;
  if (initialInvestmentCost > 0) {
    returnOnInvestment = (netPresentValue / initialInvestmentCost) * 100;
  } else if (netPresentValue > 0) { // If investment is 0 but there are benefits, ROI is effectively infinite
    returnOnInvestment = Infinity;
  } else { // If investment is 0 and no benefits, ROI is 0
    returnOnInvestment = 0;
  }

  let benefitCostRatio = 0;
  if (initialInvestmentCost > 0) {
    benefitCostRatio = discountedBenefits / initialInvestmentCost;
  } else if (discountedBenefits > 0) { // If investment is 0 but there are benefits, BCR is effectively infinite
    benefitCostRatio = Infinity;
  } else { // If investment is 0 and no benefits, BCR is 0
    benefitCostRatio = 0;
  }

  return {
    totalAvoidedTreatmentCosts: totalAvoidedTreatmentCostsAccumulated,
    totalAvoidedProductivityLoss: totalAvoidedProductivityLossAccumulated,
    totalSocietalValueFromQALYs: totalSocietalValueFromQALYsAccumulated,
    totalProjectBenefits: totalUndiscountedBenefits,
    netPresentValue: netPresentValue,
    returnOnInvestment: returnOnInvestment,
    benefitCostRatio: benefitCostRatio
  };
}
