function calculateFundingModel(inputs) {
    const {
        totalProjectCost,
        publicFundingPercentage,
        privateEquityPercentage,
        grantFundingAmount,
        debtFundingInterestRate,
        debtTermYears,
        annualProjectRevenue,
        annualOperatingMaintenanceCost
    } = inputs;

    const totalFundingRequired = totalProjectCost;

    // Input Validation and sanitization
    if (totalProjectCost <= 0) {
        return {
            totalFundingRequired: totalProjectCost,
            calculatedDebtFunding: 0,
            annualDebtService: 0,
            debtToEquityRatio: 0,
            annualNetCashFlow: 0,
            debtServiceCoverageRatio: 0,
            projectFeasibilityStatus: "Not Feasible (Invalid Project Cost)"
        };
    }

    const publicShare = Math.max(0, Math.min(1, publicFundingPercentage));
    const privateShare = Math.max(0, Math.min(1, privateEquityPercentage));
    const effectiveGrant = Math.max(0, grantFundingAmount);
    const effectiveDebtTermYears = Math.max(1, debtTermYears); // Minimum 1 year for debt term

    // 1. Calculate direct funding contributions
    const publicFundingAmount = totalProjectCost * publicShare;
    const privateEquityAmount = totalProjectCost * privateShare;

    // Total initial committed funding (excluding calculated debt, represents 'equity-like' contributions)
    const totalCommittedFunding = publicFundingAmount + privateEquityAmount + effectiveGrant;

    // 2. Determine required debt funding
    let calculatedDebtFunding = Math.max(0, totalProjectCost - totalCommittedFunding);

    // 3. Calculate annual debt service
    let annualDebtService = 0;
    if (calculatedDebtFunding > 0) {
        const monthlyInterestRate = debtFundingInterestRate / 12;
        const numberOfPayments = effectiveDebtTermYears * 12;

        if (monthlyInterestRate > 0) {
            annualDebtService = (calculatedDebtFunding * monthlyInterestRate) /
                                (1 - Math.pow(1 + monthlyInterestRate, -numberOfPayments)) * 12;
        } else {
            // 0 interest rate loan scenario
            annualDebtService = calculatedDebtFunding / effectiveDebtTermYears;
        }
    }
    if (!isFinite(annualDebtService)) annualDebtService = 0; // Handle potential infinities due to extreme inputs

    // 4. Calculate annual net cash flow
    const annualNetOperatingIncome = annualProjectRevenue - annualOperatingMaintenanceCost;
    const annualNetCashFlow = annualNetOperatingIncome - annualDebtService;

    // 5. Calculate Debt Service Coverage Ratio (DSCR)
    let debtServiceCoverageRatio = 0;
    if (annualDebtService > 0) {
        debtServiceCoverageRatio = annualNetOperatingIncome / annualDebtService;
    } else if (calculatedDebtFunding === 0) {
        debtServiceCoverageRatio = annualNetOperatingIncome >= 0 ? Infinity : 0; // Infinite if no debt and positive NOI, else 0
    }
    if (debtServiceCoverageRatio < 0) debtServiceCoverageRatio = 0; // DSCR should not be negative for practical interpretation
    if (!isFinite(debtServiceCoverageRatio)) debtServiceCoverageRatio = 0; // Sanitize potential non-finite values

    // 6. Calculate Debt-to-Equity-like Funding Ratio
    let debtToEquityRatio = 0;
    if (totalCommittedFunding > 0) {
        debtToEquityRatio = calculatedDebtFunding / totalCommittedFunding;
    } else if (calculatedDebtFunding > 0) {
        debtToEquityRatio = Infinity; // All debt, no 'equity-like' funding
    } else {
        debtToEquityRatio = 0; // No debt, no 'equity-like' funding
    }
    if (!isFinite(debtToEquityRatio)) debtToEquityRatio = 0; // Sanitize potential non-finite values

    // 7. Determine feasibility status
    let projectFeasibilityStatus = "Not Feasible (Critical Issues)";
    if (calculatedDebtFunding > 0 && annualDebtService === 0 && debtTermYears > 0) { // Indicates 0% interest and very short term leading to huge single payment, or if debtTermYears is 0 implicitly
        projectFeasibilityStatus = "Not Feasible (Debt Calculation Issue)";
    } else if (calculatedDebtFunding === totalProjectCost && annualProjectRevenue < annualOperatingMaintenanceCost) {
        projectFeasibilityStatus = "Not Feasible (Fully Debt-Funded, Negative NOI)";
    } else if (debtServiceCoverageRatio >= 1.35 && annualNetCashFlow >= 0) { 
        projectFeasibilityStatus = "Feasible (Strong Cash Flow & Debt Coverage)";
    } else if (debtServiceCoverageRatio >= 1.15 && annualNetCashFlow >= 0) {
        projectFeasibilityStatus = "Potentially Feasible (Standard Debt Coverage)";
    } else if (calculatedDebtFunding === 0 && annualNetOperatingIncome >= 0) {
        projectFeasibilityStatus = "Feasible (No Debt Burden & Positive Operations)";
    } else if (annualNetCashFlow < 0) {
        projectFeasibilityStatus = "Not Feasible (Negative Annual Cash Flow)";
    } else {
        projectFeasibilityStatus = "Not Feasible (DSCR Below Threshold)";
    }

    return {
        totalFundingRequired: totalFundingRequired,
        calculatedDebtFunding: calculatedDebtFunding,
        annualDebtService: annualDebtService,
        debtToEquityRatio: debtToEquityRatio,
        annualNetCashFlow: annualNetCashFlow,
        debtServiceCoverageRatio: debtServiceCoverageRatio,
        projectFeasibilityStatus: projectFeasibilityStatus
    };
}
