formula:
{
  const total_estimated_cost = inputs.base_project_cost_usd * (1 + inputs.contingency_percent / 100);
  const total_available_funding = inputs.committed_public_funds_usd + inputs.secured_grants_partnerships_usd + inputs.projected_revenue_streams_usd + inputs.potential_future_financing_usd;
  const funding_gap_surplus = total_available_funding - total_estimated_cost;

  let funding_gap_percentage = 0;
  if (total_estimated_cost > 0) {
    funding_gap_percentage = (funding_gap_surplus / total_estimated_cost) * 100;
  }

  let funding_status = '';
  if (funding_gap_surplus >= 0) {
    funding_status = 'Project fully funded or has a surplus';
  } else if (Math.abs(funding_gap_surplus) / total_estimated_cost <= 0.10) {
    funding_status = 'Moderate funding gap identified (up to 10% of total cost)';
  } else {
    funding_status = 'Significant funding gap identified (over 10% of total cost)';
  }

  return {
    total_estimated_cost: total_estimated_cost,
    total_available_funding: total_available_funding,
    funding_gap_surplus: funding_gap_surplus,
    funding_gap_percentage: funding_gap_percentage,
    funding_status: funding_status
  };
}
