function calculate({
    initialProjectBudget,
    initialProjectDuration,
    dailyProjectOverhead,
    dailyIdleCost,
    estimatedDelayDurationMonths,
    politicalRiskMultiplier,
    costEscalationRateAnnual,
    indirectCostMultiplier,
    mitigationSavingsRatePerMonth
}) {
    const DAYS_PER_MONTH = 30.4375; // Average days per month for consistency

    // Input validation for robustness
    if (initialProjectBudget <= 0 || initialProjectDuration <= 0) {
        throw new Error("Initial project budget and duration must be positive values.");
    }
    if (estimatedDelayDurationMonths < 0) {
        throw new Error("Estimated delay duration cannot be negative.");
    }
    if (dailyProjectOverhead < 0 || dailyIdleCost < 0) {
        throw new Error("Daily overhead and idle costs cannot be negative.");
    }
    if (politicalRiskMultiplier < 1.0) {
        throw new Error("Political risk multiplier cannot be less than 1.0 (it implies an amplification of risk).");
    }
    if (costEscalationRateAnnual < 0 || indirectCostMultiplier < 0 || mitigationSavingsRatePerMonth < 0) {
        throw new Error("Rates and multipliers cannot be negative.");
    }
    let effectiveMitigationSavingsRate = mitigationSavingsRatePerMonth;
    if (effectiveMitigationSavingsRate > 100) {
        effectiveMitigationSavingsRate = 100; // Cap mitigation savings at 100%
    }

    // Step 1: Calculate the daily direct costs during the delay period.
    // This involves ongoing overheads and idle costs, adjusted for annual escalation.
    const monthlyEscalationFactor = 1 + (costEscalationRateAnnual / 100 / 12);
    // To apply a consistent daily escalation for the delay duration:
    // We calculate a daily factor that, when compounded for 30.4375 days, equals the monthly factor.
    const dailyEscalationFactor = Math.pow(monthlyEscalationFactor, 1 / DAYS_PER_MONTH);

    // Base daily costs combine overhead (for maintaining some presence/administration) and idle costs (for stalled work).
    const baseDailyDirectCost = dailyProjectOverhead + dailyIdleCost;

    // Apply daily escalation to the base direct cost. This reflects rising costs of labor, materials, equipment.
    const escalatedDailyDirectCost = baseDailyDirectCost * dailyEscalationFactor; 

    // Apply the political risk multiplier. This factor accounts for increased costs due to uncertainty,
    // bureaucratic hurdles, increased security, legal fees, or complex negotiations arising from political issues.
    const dailyDirectDelayCost = escalatedDailyDirectCost * politicalRiskMultiplier;

    // Step 2: Determine Total Direct and Indirect Delay Costs.
    const estimatedDelayDurationDays = estimatedDelayDurationMonths * DAYS_PER_MONTH;
    const directDelayCost = dailyDirectDelayCost * estimatedDelayDurationDays;

    // Indirect costs cover non-quantifiable or less direct financial impacts such as 
    // reputational damage, social impact, lost economic opportunity, extended legal disputes,
    // and potential contractual penalties not explicitly covered by direct overheads.
    const indirectDelayCost = directDelayCost * indirectCostMultiplier;

    // Total estimated delay cost before considering any mitigation strategies.
    const estimatedTotalDelayCost = directDelayCost + indirectDelayCost;

    // Step 3: Estimate Mitigation Savings and Final Cost.
    // Mitigation savings are applied as a percentage of the total estimated delay cost.
    // This reflects the effectiveness of proactive strategies like contract renegotiations, 
    // resource reallocation, alternative funding, or accelerated recovery plans.
    const mitigationSavings = estimatedTotalDelayCost * (effectiveMitigationSavingsRate / 100);

    // The final delay cost after accounting for successful mitigation efforts.
    const mitigatedDelayCost = estimatedTotalDelayCost - mitigationSavings;

    // Step 4: Project New Timeline.
    const newProjectDuration = initialProjectDuration + estimatedDelayDurationMonths;

    return {
        estimatedTotalDelayCost,
        mitigatedDelayCost,
        mitigationSavings,
        newProjectDuration,
        directDelayCost,
        indirectDelayCost
    };
}
