function calculate({ projectBudget, averageAnnualFunding, projectDurationYears, legislativeStabilityIndex, economicOutlookIndex, historicalFundingDeviation, politicalEventRiskFactor, escalationRate }) {

    const MAX_RISK_SCORE = 100;
    const MIN_RISK_SCORE = 1;

    // 1. Funding Stability Sub-score (FS_Score)
    // Higher historical deviation -> higher risk
    // Inadequate annual funding -> higher risk
    let fundingAdequacyRatio = (averageAnnualFunding * projectDurationYears) / projectBudget;
    let fsDeviationImpact = historicalFundingDeviation / 100; // Convert percentage to decimal

    // If funding is significantly less than project needs, impact is higher
    let fundingGapPenalty = 0;
    if (fundingAdequacyRatio < 1) {
        fundingGapPenalty = (1 - fundingAdequacyRatio) * 0.4; // 40% penalty for each 100% funding gap below 100%
    }
    if (fundingAdequacyRatio < 0.5) fundingGapPenalty = 0.5; // Cap penalty for very severe gaps

    let fsScore = (fsDeviationImpact * 0.6) + (fundingGapPenalty * 0.4); // Weighted average
    fsScore = Math.min(Math.max(fsScore, 0), 1); // Cap fsScore between 0 and 1

    // 2. Legislative & Economic Volatility Sub-score (LEV_Score)
    // Lower stability/outlook -> higher risk
    // Higher political event risk -> higher risk
    let legislativeRisk = (10 - legislativeStabilityIndex) / 9; // Invert: 10->0 (stable), 1->1 (unstable)
    let economicRisk = (10 - economicOutlookIndex) / 9; // Invert: 10->0 (excellent), 1->1 (poor)

    let levScore = (legislativeRisk * 0.4) + (economicRisk * 0.3) + (politicalEventRiskFactor * 0.3); // Weighted average
    levScore = Math.min(Math.max(levScore, 0), 1); // Cap levScore between 0 and 1

    // 3. Project Exposure Sub-score (PE_Score)
    // Larger/longer projects inherently have more exposure to volatility
    // We'll normalize project budget and duration to some scale, e.g., relative to typical project sizes
    // Assuming average public infrastructure project around $50M and 5 years for normalization
    const TYPICAL_BUDGET = 50000000; 
    const TYPICAL_DURATION = 5; 

    let budgetExposure = Math.log10(projectBudget / TYPICAL_BUDGET) / Math.log10(100); // Scale logarithmically, assume 100x typical budget is max exposure
    budgetExposure = Math.min(Math.max(budgetExposure, 0), 1); // Cap between 0 and 1

    let durationExposure = (projectDurationYears - 1) / (50 - 1); // Scale duration linearly from 1 to 50 years
    durationExposure = Math.min(Math.max(durationExposure, 0), 1); // Cap between 0 and 1

    let peScore = (budgetExposure * 0.6) + (durationExposure * 0.4); // Weighted average
    peScore = Math.min(Math.max(peScore, 0), 1); // Cap peScore between 0 and 1

    // 4. Composite Financial Risk Score
    // Weights: Funding Stability (0.4), Legislative & Economic (0.4), Project Exposure (0.2)
    let financialRiskScore = (fsScore * 0.4) + (levScore * 0.4) + (peScore * 0.2);
    financialRiskScore = financialRiskScore * (MAX_RISK_SCORE - MIN_RISK_SCORE) + MIN_RISK_SCORE; // Scale to 1-100
    financialRiskScore = Math.round(financialRiskScore * 10) / 10; // One decimal place

    // Ensure score is within bounds
    financialRiskScore = Math.min(Math.max(financialRiskScore, MIN_RISK_SCORE), MAX_RISK_SCORE);

    // 5. Potential Delay Months
    // Delay is proportional to risk score and project duration
    // A risk score of 100 could lead to significant delays, e.g., 20% of project duration for extreme risk.
    // A score of 1 might be minimal, e.g., 0.5% of project duration.
    let maxDelayPercentage = 0.20; // 20% of duration for highest risk
    let minDelayPercentage = 0.005; // 0.5% of duration for lowest risk

    let delayFactor = (financialRiskScore - MIN_RISK_SCORE) / (MAX_RISK_SCORE - MIN_RISK_SCORE); // Normalized risk (0 to 1)
    let potentialDelayMonths = (minDelayPercentage + (maxDelayPercentage - minDelayPercentage) * delayFactor) * projectDurationYears * 12;
    potentialDelayMonths = Math.round(potentialDelayMonths * 10) / 10; // One decimal place
    potentialDelayMonths = Math.max(potentialDelayMonths, 0); // Cannot be negative

    // 6. Estimated Cost Overrun
    // Cost overrun is a function of risk score, potential delay, project budget, and escalation rate.
    // For every month of delay, costs escalate by (escalationRate / 12) + direct risk impact.
    let annualEscalationFactor = (1 + escalationRate);
    let totalEscalationOverDelayMonths = Math.pow(annualEscalationFactor, potentialDelayMonths / 12) - 1;

    // Also, a direct cost impact from the risk itself (e.g., re-bidding, administrative costs, increased financing costs)
    // This direct impact is applied to a portion of the project budget, scaled by risk.
    let directRiskCostImpact = (financialRiskScore / MAX_RISK_SCORE) * 0.10 * projectBudget; // Up to 10% of budget for highest risk due to direct administrative/rework/financing costs

    let estimatedCostOverrun = (projectBudget * totalEscalationOverDelayMonths) + directRiskCostImpact;
    estimatedCostOverrun = Math.round(estimatedCostOverrun);
    estimatedCostOverrun = Math.max(estimatedCostOverrun, 0); // Cannot be negative

    // 7. Volatility Risk Category
    let volatilityRiskCategory = "Low";
    if (financialRiskScore > 40) volatilityRiskCategory = "Medium";
    if (financialRiskScore > 70) volatilityRiskCategory = "High";

    return {
        financialRiskScore: financialRiskScore,
        potentialDelayMonths: potentialDelayMonths,
        estimatedCostOverrun: estimatedCostOverrun,
        volatilityRiskCategory: volatilityRiskCategory
    };
}
