function calculateROI(inputs) {
  const programCostPerParticipant = Math.max(0, inputs.programCostPerParticipant || 0);
  const numParticipants = Math.max(0, inputs.numParticipants || 0);
  const benefitDurationYears = Math.max(0, inputs.benefitDurationYears || 0);
  const avgAnnualHealthcareSavings = Math.max(0, inputs.avgAnnualHealthcareSavings || 0);
  const avgAnnualProductivityGain = Math.max(0, inputs.avgAnnualProductivityGain || 0);
  const avgAnnualJusticeSystemSavings = Math.max(0, inputs.avgAnnualJusticeSystemSavings || 0);
  const avgAnnualIndirectSocialSavings = Math.max(0, inputs.avgAnnualIndirectSocialSavings || 0);
  const discountRate = Math.max(0, Math.min(1, inputs.discountRate || 0)); // Cap discount rate between 0 and 1

  // Calculate Total Program Cost
  const totalProgramCost = programCostPerParticipant * numParticipants;

  // Calculate Annual Savings Per Participant
  const annualSavingsPerParticipant = 
    avgAnnualHealthcareSavings +
    avgAnnualProductivityGain +
    avgAnnualJusticeSystemSavings +
    avgAnnualIndirectSocialSavings;

  // Calculate Total Annual Savings for all participants
  const totalAnnualSavings = annualSavingsPerParticipant * numParticipants;

  // Calculate Present Value of Future Savings (benefits)
  let totalPresentValueSavings = 0;
  if (numParticipants > 0 && benefitDurationYears > 0) {
    for (let year = 1; year <= benefitDurationYears; year++) {
      if (discountRate === 0) {
        totalPresentValueSavings += totalAnnualSavings; // No discounting
      } else {
        totalPresentValueSavings += totalAnnualSavings / Math.pow(1 + discountRate, year);
      }
    }
  }

  // Calculate Net Present Value (NPV)
  const netPresentValue = totalPresentValueSavings - totalProgramCost;

  // Calculate Return on Investment (ROI)
  let returnOnInvestment = 0;
  if (totalProgramCost > 0) {
    returnOnInvestment = (netPresentValue / totalProgramCost) * 100;
  } else if (totalPresentValueSavings > 0) {
    returnOnInvestment = Infinity; // Infinite ROI if cost is zero and benefits exist
  } else {
    returnOnInvestment = 0; // If both cost and savings are zero, ROI is 0 (or undefined, but 0 is safer here)
  }

  // Calculate Benefit-Cost Ratio (BCR)
  let benefitCostRatio = 0;
  if (totalProgramCost > 0) {
    benefitCostRatio = totalPresentValueSavings / totalProgramCost;
  } else if (totalPresentValueSavings > 0) {
    benefitCostRatio = Infinity; // Infinite BCR if cost is zero and benefits exist
  } else {
    benefitCostRatio = 1; // If both cost and savings are zero, BCR is 1 (or undefined, but 1 is safer for break-even)
  }

  return {
    totalProgramCost: totalProgramCost,
    totalAnnualSavings: totalAnnualSavings,
    totalPresentValueSavings: totalPresentValueSavings,
    netPresentValue: netPresentValue,
    returnOnInvestment: returnOnInvestment,
    benefitCostRatio: benefitCostRatio
  };
}
