function calculate({
  studentEnrollment,
  localPropertyTaxRevenue,
  stateAidAllocation,
  federalAidAllocation,
  studentsPovertyLevelPercent,
  studentsSpecialEducationPercent,
  districtCostOfLivingIndex
}) {
  // Constants for adjustment factors and benchmark (can be dynamic in real systems)
  const PER_POVERTY_STUDENT_ADDITIONAL_FUNDING_FACTOR = 0.25; // 25% additional funding relative to base for each student in poverty
  const PER_SPECIAL_ED_STUDENT_ADDITIONAL_COST = 12500; // Flat additional cost per special education student
  const STATE_ADEQUACY_BENCHMARK_PER_STUDENT_BASE = 18500; // Hypothetical base adequacy for average district (COL=1, 0% poverty/SPED)

  // Input validation
  if (studentEnrollment <= 0) {
    return {
      totalDistrictFunding: 0,
      baseFundingPerStudent: 0,
      needsAdjustedFundingPerStudent: 0,
      costOfLivingAdjustedFundingPerStudent: 0,
      deviationFromAdequacyBenchmark: 0,
      equityGapAnalysis: "Error: Student enrollment must be greater than zero."
    };
  }

  // Step 1: Calculate total district funding from all sources
  const totalDistrictFunding = localPropertyTaxRevenue + stateAidAllocation + federalAidAllocation;

  // Step 2: Calculate base funding per student (unadjusted)
  const baseFundingPerStudent = totalDistrictFunding / studentEnrollment;

  // Step 3: Adjust funding for specific student needs
  const numStudentsPoverty = (studentsPovertyLevelPercent / 100) * studentEnrollment;
  const numStudentsSpecialEducation = (studentsSpecialEducationPercent / 100) * studentEnrollment;

  // Calculate needs-based additional funding
  const additionalFundingForPoverty = numStudentsPoverty * baseFundingPerStudent * PER_POVERTY_STUDENT_ADDITIONAL_FUNDING_FACTOR;
  const additionalFundingForSpecialEd = numStudentsSpecialEducation * PER_SPECIAL_ED_STUDENT_ADDITIONAL_COST;

  // The needs-adjusted funding per student effectively accounts for higher costs associated with these demographics.
  // This can be interpreted as the total funding capacity needed per student to meet these needs.
  const needsAdjustedTotalFunding = totalDistrictFunding + additionalFundingForPoverty + additionalFundingForSpecialEd;
  const needsAdjustedFundingPerStudent = needsAdjustedTotalFunding / studentEnrollment;

  // Step 4: Adjust funding for district's cost of living
  // A higher COL index means the same dollar amount has less purchasing power, so effective funding is lower.
  const costOfLivingAdjustedFundingPerStudent = needsAdjustedFundingPerStudent / districtCostOfLivingIndex;

  // Step 5: Determine the State Adequacy Benchmark adjusted for this district's needs and COL for comparison
  // This benchmark should ideally reflect the *adequate* funding for a district with these specific characteristics.
  const adjustedBenchmarkPerStudentForDistrict = (
    STATE_ADEQUACY_BENCHMARK_PER_STUDENT_BASE * 
    (1 + (studentsPovertyLevelPercent / 100) * PER_POVERTY_STUDENT_ADDITIONAL_FUNDING_FACTOR) + 
    ((studentsSpecialEducationPercent / 100) * PER_SPECIAL_ED_STUDENT_ADDITIONAL_COST)
  ) * districtCostOfLivingIndex;

  // Step 6: Calculate deviation from the adjusted adequacy benchmark
  let deviationFromAdequacyBenchmark = 0;
  let equityGapAnalysis = "";

  if (adjustedBenchmarkPerStudentForDistrict > 0) {
    deviationFromAdequacyBenchmark = ((costOfLivingAdjustedFundingPerStudent - adjustedBenchmarkPerStudentForDistrict) / adjustedBenchmarkPerStudentForDistrict) * 100;

    if (deviationFromAdequacyBenchmark < -10) {
      equityGapAnalysis = "Significant Funding Deficit: This district's adjusted funding is substantially below the state's adequacy benchmark, indicating a significant equity gap and potential under-resourcing.";
    } else if (deviationFromAdequacyBenchmark < -2) {
      equityGapAnalysis = "Funding Deficit: This district's adjusted funding is below the state's adequacy benchmark, suggesting a notable equity gap.";
    } else if (deviationFromAdequacyBenchmark >= -2 && deviationFromAdequacyBenchmark <= 2) {
      equityGapAnalysis = "Adequate Funding: This district's adjusted funding is very close to the state's adequacy benchmark, indicating relative funding equity based on current metrics.";
    } else if (deviationFromAdequacyBenchmark > 2 && deviationFromAdequacyBenchmark <= 10) {
      equityGapAnalysis = "Slightly Above Adequacy: This district's adjusted funding is modestly above the state's adequacy benchmark.";
    } else {
      equityGapAnalysis = "Significantly Above Adequacy: This district's adjusted funding is substantially above the state's adequacy benchmark, potentially indicating a resource advantage compared to other districts with similar needs.";
    }
  } else {
      equityGapAnalysis = "Benchmark calculation error: Adequacy benchmark is zero or negative.";
  }

  return {
    totalDistrictFunding: totalDistrictFunding,
    baseFundingPerStudent: baseFundingPerStudent,
    needsAdjustedFundingPerStudent: needsAdjustedFundingPerStudent,
    costOfLivingAdjustedFundingPerStudent: costOfLivingAdjustedFundingPerStudent,
    deviationFromAdequacyBenchmark: deviationFromAdequacyBenchmark,
    equityGapAnalysis: equityGapAnalysis
  };
}
