function calculateROI({
    initial_project_cost,
    annual_maintenance_cost_percent,
    current_annual_operational_cost,
    efficiency_gain_percent,
    fraud_reduction_potential_percent,
    annual_fraud_losses_current,
    service_delivery_improvement_value_per_citizen,
    affected_citizen_base,
    project_lifespan_years
}) {
    // Ensure all inputs are numbers and handle potential non-numeric inputs gracefully
    initial_project_cost = parseFloat(initial_project_cost) || 0;
    annual_maintenance_cost_percent = parseFloat(annual_maintenance_cost_percent) || 0;
    current_annual_operational_cost = parseFloat(current_annual_operational_cost) || 0;
    efficiency_gain_percent = parseFloat(efficiency_gain_percent) || 0;
    fraud_reduction_potential_percent = parseFloat(fraud_reduction_potential_percent) || 0;
    annual_fraud_losses_current = parseFloat(annual_fraud_losses_current) || 0;
    service_delivery_improvement_value_per_citizen = parseFloat(service_delivery_improvement_value_per_citizen) || 0;
    affected_citizen_base = parseFloat(affected_citizen_base) || 0;
    project_lifespan_years = Math.max(1, parseFloat(project_lifespan_years) || 1); // Minimum 1 year

    // Cap percentages to ensure they are within reasonable bounds [0, 100]
    efficiency_gain_percent = Math.max(0, Math.min(efficiency_gain_percent, 100));
    fraud_reduction_potential_percent = Math.max(0, Math.min(fraud_reduction_potential_percent, 100));
    annual_maintenance_cost_percent = Math.max(0, Math.min(annual_maintenance_cost_percent, 100));

    // 1. Calculate Annual Costs
    const annual_maintenance_cost = initial_project_cost * (annual_maintenance_cost_percent / 100);
    const total_cost_over_lifespan = initial_project_cost + (annual_maintenance_cost * project_lifespan_years);

    // 2. Calculate Annual Benefits
    const annual_efficiency_savings = current_annual_operational_cost * (efficiency_gain_percent / 100);
    const annual_fraud_reduction_savings = annual_fraud_losses_current * (fraud_reduction_potential_percent / 100);
    const annual_service_improvement_value = service_delivery_improvement_value_per_citizen * affected_citizen_base;

    const total_annual_benefits = annual_efficiency_savings + annual_fraud_reduction_savings + annual_service_improvement_value;

    // 3. Calculate Total Benefits over Project Lifespan
    const total_benefits_over_lifespan = total_annual_benefits * project_lifespan_years;

    // 4. Calculate Net Project Benefit
    const net_project_benefit = total_benefits_over_lifespan - total_cost_over_lifespan;

    // 5. Calculate ROI
    let roi_percent = 0;
    if (total_cost_over_lifespan > 0) {
        roi_percent = (net_project_benefit / total_cost_over_lifespan) * 100;
    } else if (initial_project_cost === 0 && net_project_benefit > 0) {
        // If initial cost is 0 but there are benefits, ROI is considered infinite or very high
        roi_percent = Infinity; 
    } else if (initial_project_cost === 0 && net_project_benefit <= 0) {
        roi_percent = 0;
    }

    // 6. Calculate Payback Period
    let payback_period_years = Infinity; 
    const annual_net_cash_flow_after_initial = total_annual_benefits - annual_maintenance_cost;

    if (annual_net_cash_flow_after_initial > 0) {
        payback_period_years = initial_project_cost / annual_net_cash_flow_after_initial;
        payback_period_years = Math.max(0, payback_period_years); // Payback cannot be negative
    } else if (initial_project_cost === 0) {
        payback_period_years = 0;
    }

    return {
        total_project_cost: total_cost_over_lifespan,
        total_cost_savings: (annual_efficiency_savings + annual_fraud_reduction_savings) * project_lifespan_years, 
        total_service_improvement_value: annual_service_improvement_value * project_lifespan_years,
        net_project_benefit: net_project_benefit,
        roi_percent: roi_percent,
        payback_period_years: payback_period_years
    };
}
