const { currentPopulation, projectedPopulationShiftPercent, averageFederalFundingPerCapita, averageStateFundingPerCapita, federalFundingFormulaSensitivity, stateFundingFormulaSensitivity, proportionToSchools, proportionToHealthcare, proportionToInfrastructure } = inputs;

// Input validation and normalization
const currentPop = Math.max(0, currentPopulation);
const projectedShiftDecimal = projectedPopulationShiftPercent / 100;
const avgFedPerCapita = Math.max(0, averageFederalFundingPerCapita);
const avgStatePerCapita = Math.max(0, averageStateFundingPerCapita);

const fedSensitivity = federalFundingFormulaSensitivity;
const stateSensitivity = stateFundingFormulaSensitivity;

// Convert proportions from percentage to decimal and ensure they are within [0, 1]
let propSchools = Math.max(0, Math.min(1, proportionToSchools / 100));
let propHealthcare = Math.max(0, Math.min(1, proportionToHealthcare / 100));
let propInfrastructure = Math.max(0, Math.min(1, proportionToInfrastructure / 100));

// 1. Calculate Estimated New Population
const estimatedNewPopulation = currentPop * (1 + projectedShiftDecimal);

// 2. Calculate Current Total Funding from Federal and State sources
const currentTotalFederalFunding = currentPop * avgFedPerCapita;
const currentTotalStateFunding = currentPop * avgStatePerCapita;

// 3. Calculate New Per-Capita Funding Rates based on formula sensitivity
// A positive sensitivity means per-capita funding changes in the same direction as population shift.
// A negative sensitivity means per-capita funding changes in the opposite direction.
// e.g., if population decreases, and sensitivity is positive, per-capita funding also decreases.
const federalPerCapitaAdjustmentFactor = 1 + (projectedShiftDecimal * fedSensitivity);
const statePerCapitaAdjustmentFactor = 1 + (projectedShiftDecimal * stateSensitivity);

const newAverageFederalFundingPerCapita = avgFedPerCapita * federalPerCapitaAdjustmentFactor;
const newAverageStateFundingPerCapita = avgStatePerCapita * statePerCapitaAdjustmentFactor;

// Ensure per-capita funding doesn't become negative (though highly unlikely for base rates)
const effectiveNewFederalFundingPerCapita = Math.max(0, newAverageFederalFundingPerCapita);
const effectiveNewStateFundingPerCapita = Math.max(0, newAverageStateFundingPerCapita);

// 4. Calculate New Total Funding (New Population * Effective New Per-Capita Rate)
const newTotalFederalFunding = estimatedNewPopulation * effectiveNewFederalFundingPerCapita;
const newTotalStateFunding = estimatedNewPopulation * effectiveNewStateFundingPerCapita;

// 5. Calculate Change in Funding
const changeInFederalFunding = newTotalFederalFunding - currentTotalFederalFunding;
const changeInStateFunding = newTotalStateFunding - currentTotalStateFunding;
const totalChangeInFunding = changeInFederalFunding + changeInStateFunding;

// 6. Distribute Change in Funding to Services
// Sum of explicitly defined proportions. If it exceeds 1, normalize them.
let sumExplicitProportions = propSchools + propHealthcare + propInfrastructure;
if (sumExplicitProportions > 1.0001) { // Use a small epsilon for floating point comparison
    const normalizationFactor = 1 / sumExplicitProportions;
    propSchools *= normalizationFactor;
    propHealthcare *= normalizationFactor;
    propInfrastructure *= normalizationFactor;
    sumExplicitProportions = propSchools + propHealthcare + propInfrastructure; // Recalculate sum after normalization
}
const proportionToOtherServices = Math.max(0, 1 - sumExplicitProportions);

const changeForSchools = totalChangeInFunding * propSchools;
const changeForHealthcare = totalChangeInFunding * propHealthcare;
const changeForInfrastructure = totalChangeInFunding * propInfrastructure;
const changeForOtherServices = totalChangeInFunding * proportionToOtherServices;

return {
    estimatedNewPopulation: estimatedNewPopulation,
    changeInFederalFunding: changeInFederalFunding,
    changeInStateFunding: changeInStateFunding,
    totalChangeInFunding: totalChangeInFunding,
    changeForSchools: changeForSchools,
    changeForHealthcare: changeForHealthcare,
    changeForInfrastructure: changeForInfrastructure,
    changeForOtherServices: changeForOtherServices
};
