const venue_area_sqm = inputs.venue_area_sqm;
const max_occupancy_ppl = inputs.max_occupancy_ppl;
const num_exits = inputs.num_exits;
const avg_exit_width_m = inputs.avg_exit_width_m;
const ped_flow_rate_pp_m_s = inputs.ped_flow_rate_pp_m_s;
const pre_evacuation_delay_s = inputs.pre_evacuation_delay_s;
const bottleneck_factor = inputs.bottleneck_factor;

const MIN_AREA = 10;
const MIN_OCCUPANCY = 1;
const MIN_EXITS = 1;
const MIN_EXIT_WIDTH = 0.5;
const MIN_PED_FLOW_RATE = 0.1;
const MAX_PED_FLOW_RATE = 2.0;
const MIN_BOTTLENECK_FACTOR = 0.1;
const MAX_BOTTLENECK_FACTOR = 1.0;
const MAX_CROWD_DENSITY = 8; // Max density for standing to prevent unrealistic results
const MAX_EVAC_TIME_MIN = 720; // Cap evacuation time at 12 hours

const area = Math.max(venue_area_sqm, MIN_AREA);
const occupancy = Math.max(max_occupancy_ppl, MIN_OCCUPANCY);
const exits = Math.max(num_exits, MIN_EXITS);
const exitWidth = Math.max(avg_exit_width_m, MIN_EXIT_WIDTH);
const flowRate = Math.min(Math.max(ped_flow_rate_pp_m_s, MIN_PED_FLOW_RATE), MAX_PED_FLOW_RATE);
const preEvacDelay = Math.max(pre_evacuation_delay_s, 0);
const effectiveBottleneck = Math.min(Math.max(bottleneck_factor, MIN_BOTTLENECK_FACTOR), MAX_BOTTLENECK_FACTOR);

let crowd_density_pp_sqm = occupancy / area;
crowd_density_pp_sqm = Math.min(crowd_density_pp_sqm, MAX_CROWD_DENSITY);

const individual_exit_capacity_ps = flowRate * exitWidth * effectiveBottleneck;
const total_exit_capacity_ps = exits * individual_exit_capacity_ps;
const total_exit_capacity_pp_min = total_exit_capacity_ps * 60;

let evacuation_time_seconds = 0;
if (total_exit_capacity_ps > 0) {
    evacuation_time_seconds = (occupancy / total_exit_capacity_ps) + preEvacDelay;
} else {
    evacuation_time_seconds = Infinity; 
}

const estimated_evacuation_time_min = evacuation_time_seconds / 60;
const final_evacuation_time_min = Math.min(estimated_evacuation_time_min, MAX_EVAC_TIME_MIN);

return {
    crowd_density_pp_sqm: crowd_density_pp_sqm,
    total_exit_capacity_pp_min: total_exit_capacity_pp_min,
    estimated_evacuation_time_min: final_evacuation_time_min
};
