const flowRate = inputs.flowRate;
const liquidTemperature = inputs.liquidTemperature;
const fluidSpecificGravity = inputs.fluidSpecificGravity;
const atmosphericPressure = inputs.atmosphericPressure;
const staticSuctionElevation = inputs.staticSuctionElevation;
const staticDischargeElevation = inputs.staticDischargeElevation;
const suctionLineFrictionLoss = inputs.suctionLineFrictionLoss;
const dischargeLineFrictionLoss = inputs.dischargeLineFrictionLoss;

// Constants
const g = 9.81; // Acceleration due to gravity in m/s²

// Vapor pressure data for water (kPa) based on temperature (°C)
// This is an approximate lookup table for common temperatures.
const temp_vp_data = [
    {temp: 0, vp: 0.61}, {temp: 5, vp: 0.87}, {temp: 10, vp: 1.23},
    {temp: 15, vp: 1.71}, {temp: 20, vp: 2.34}, {temp: 25, vp: 3.17},
    {temp: 30, vp: 4.25}, {temp: 35, vp: 5.62}, {temp: 40, vp: 7.38},
    {temp: 45, vp: 9.59}, {temp: 50, vp: 12.35}, {temp: 55, vp: 15.75},
    {temp: 60, vp: 19.92}, {temp: 65, vp: 25.02}, {temp: 70, vp: 31.16},
    {temp: 75, vp: 38.56}, {temp: 80, vp: 47.36}, {temp: 85, vp: 57.80},
    {temp: 90, vp: 70.11}, {temp: 95, vp: 84.53}, {temp: 100, vp: 101.325}
];

// Function to interpolate vapor pressure based on liquid temperature
function interpolateVaporPressure(temp) {
    if (temp <= temp_vp_data[0].temp) return temp_vp_data[0].vp;
    if (temp >= temp_vp_data[temp_vp_data.length - 1].temp) return temp_vp_data[temp_vp_data.length - 1].vp;

    for (let i = 0; i < temp_vp_data.length - 1; i++) {
        const p1 = temp_vp_data[i];
        const p2 = temp_vp_data[i+1];
        if (temp >= p1.temp && temp <= p2.temp) {
            return p1.vp + (p2.vp - p1.vp) * ((temp - p1.temp) / (p2.temp - p1.temp));
        }
    }
    return 0; // Should not be reached
}

// Calculate Vapor Pressure (kPa) using interpolation
const vaporPressure = interpolateVaporPressure(liquidTemperature);

// Convert pressures from kPa to meters of fluid head
// Pressure Head (m) = Pressure (kPa) / (Fluid Specific Gravity * g)
// Note: The factor of 1000 for kPa to Pa and 1000 for density of water cancels out if using kg/m^3
const atmosphericHead = atmosphericPressure / (fluidSpecificGravity * g);
const vaporPressureHead = vaporPressure / (fluidSpecificGravity * g);

// NPSHa Calculation (Net Positive Suction Head Available)
// NPSHa = Atmospheric Pressure Head - Vapor Pressure Head + Static Suction Elevation - Suction Line Friction Loss
const NPSHa = atmosphericHead - vaporPressureHead + staticSuctionElevation - suctionLineFrictionLoss;

// Ensure NPSHa is not negative (physical limitation: fluid would be flashing before pump inlet)
const finalNPSHa = Math.max(NPSHa, 0);

// System Head Calculation at the given flow rate
// System Head = (Static Discharge Elevation - Static Suction Elevation) + Total Friction Losses
// Total Friction Losses = Suction Line Friction Loss + Discharge Line Friction Loss
const systemHeadAtFlow = (staticDischargeElevation - staticSuctionElevation) + suctionLineFrictionLoss + dischargeLineFrictionLoss;

// Example of a conditional logic/ratio, though not strictly required for output values:
// const isNPSHaCritical = finalNPSHa < 3 ? 'Caution: NPSHa is low.' : 'NPSHa is adequate.';

return {
    NPSHa: finalNPSHa,
    systemHeadAtFlow: systemHeadAtFlow
};
