function calculate({ roofArea, roofAreaUnit, averageAnnualRainfall, rainfallUnit, runoffCoefficient, desiredStorageCapacity, dryPeriodDays }) {

  // Constants for conversion
  const CUBIC_FEET_PER_GALLON = 7.48052;
  const CUBIC_METERS_PER_GALLON = 264.172;
  const INCHES_PER_FOOT = 12;
  const MM_PER_INCH = 25.4;
  const DAYS_PER_YEAR = 365;

  let effectiveRoofAreaSqFt = roofArea;
  if (roofAreaUnit === 'sqm') {
    effectiveRoofAreaSqFt = roofArea * 10.7639; // 1 sq m = 10.7639 sq ft
  }

  let annualRainfallInches = averageAnnualRainfall;
  if (rainfallUnit === 'mm') {
    annualRainfallInches = averageAnnualRainfall / MM_PER_INCH;
  }

  // Calculate potential annual collection in gallons
  // Formula: Area (sq ft) * Rainfall (ft) * Conversion (gallons/cu ft) * Runoff Coefficient
  // Rainfall (ft) = Rainfall (inches) / 12
  const potentialAnnualCollection = effectiveRoofAreaSqFt * (annualRainfallInches / INCHES_PER_FOOT) * CUBIC_FEET_PER_GALLON * runoffCoefficient;

  // Calculate average daily collection
  const potentialDailyCollection = potentialAnnualCollection / DAYS_PER_YEAR;

  // Calculate recommended storage capacity for dry periods
  const recommendedStorageCapacity = dryPeriodDays ? potentialDailyCollection * dryPeriodDays : 0;

  // Calculate storage efficiency if desired storage is provided
  let storageToPotentialRatio = 0;
  if (desiredStorageCapacity && potentialAnnualCollection > 0) {
    storageToPotentialRatio = Math.min(100, (desiredStorageCapacity / potentialAnnualCollection) * 100);
  } else if (desiredStorageCapacity && potentialAnnualCollection === 0) {
    storageToPotentialRatio = 0; // Can't store anything if nothing collected
  }

  return {
    potentialAnnualCollection: potentialAnnualCollection,
    potentialDailyCollection: potentialDailyCollection,
    recommendedStorageCapacity: recommendedStorageCapacity,
    storageToPotentialRatio: storageToPotentialRatio
  };
}
