formula:
const { roofArea, annualRainfall, runoffCoefficient, dailyWaterDemand, drySpellDuration } = inputs;

// Input validation
if (roofArea <= 0 || annualRainfall <= 0 || runoffCoefficient <= 0 || runoffCoefficient > 1) {
    throw new Error("Please ensure 'Total Roof Area', 'Average Annual Rainfall', and 'Runoff Coefficient' are positive values, and 'Runoff Coefficient' is between 0.01 and 1.");
}

// Calculate Annual Harvestable Volume (in Liters)
// Formula: Volume (liters) = Roof Area (m²) * Annual Rainfall (mm) * Runoff Coefficient
// This works because 1 m² of area receiving 1 mm of rain yields 1 liter of water.
const annualHarvestableVolume = roofArea * annualRainfall * runoffCoefficient;

let requiredStorageCapacity = null;
// Calculate Required Storage Capacity only if daily demand and dry spell duration are provided and positive
if (dailyWaterDemand !== null && dailyWaterDemand > 0 && drySpellDuration !== null && drySpellDuration > 0) {
    // Capacity needed to cover demand during the longest dry period
    let calculatedCapacity = dailyWaterDemand * drySpellDuration;
    // The storage capacity should not exceed the total annual harvestable volume, as you can't store more than what falls.
    requiredStorageCapacity = Math.min(calculatedCapacity, annualHarvestableVolume);
} else if ((dailyWaterDemand === null || dailyWaterDemand <= 0) && (drySpellDuration === null || drySpellDuration <= 0)) {
    // If neither is provided or both are zero/negative, don't calculate storage capacity
    requiredStorageCapacity = null;
} else {
    // If one is provided and positive, but the other is missing/zero/negative, we can't calculate meaningful storage.
    requiredStorageCapacity = null;
}

// Equivalent Annual Water Savings (in cubic meters)
const annualSavingsM3 = annualHarvestableVolume / 1000; // 1 m³ = 1000 liters

return {
    annualHarvestableVolume: annualHarvestableVolume,
    requiredStorageCapacity: requiredStorageCapacity,
    annualSavings: annualSavingsM3
};
