const roofAreaM2 = inputs.roofArea;
const avgAnnualRainfallMM = inputs.avgAnnualRainfall;
const runoffCoefficient = inputs.runoffCoefficient;
const dailyWaterDemandL = inputs.dailyWaterDemand;
const drySpellDurationDays = inputs.drySpellDuration;
const safetyFactor = Math.max(1, inputs.safetyFactor); // Ensure safety factor is at least 1

// Calculate potential annual harvestable volume (1 m² * 1 mm = 1 Liter)
const potentialAnnualHarvest = roofAreaM2 * avgAnnualRainfallMM * runoffCoefficient;

let requiredStorageForDemand = 0;
if (dailyWaterDemandL > 0 && drySpellDurationDays > 0) {
    requiredStorageForDemand = dailyWaterDemandL * drySpellDurationDays * safetyFactor;
}

// Determine recommended storage volume
// 1. Basic demand coverage during dry spell
// 2. Minimum buffer (e.g., 5% of annual harvest to always have some capacity)
// 3. Capped by annual potential harvest (storing more than you can collect annually is often not practical)
let recommendedStorageVolume = Math.max(requiredStorageForDemand, potentialAnnualHarvest * 0.05);
recommendedStorageVolume = Math.min(recommendedStorageVolume, potentialAnnualHarvest);

let daysOfSupply = 0;
if (dailyWaterDemandL > 0) {
    daysOfSupply = recommendedStorageVolume / dailyWaterDemandL;
} else if (potentialAnnualHarvest > 0) {
    daysOfSupply = Infinity; // Infinite supply if no demand but there's harvest potential
} else {
    daysOfSupply = 0; // No harvest, no demand, so 0 days supply
}

// Check if annual demand can be met by annual harvest
const annualDemand = dailyWaterDemandL * 365;
let sustainabilityNote = '';
if (dailyWaterDemandL > 0 && annualDemand > potentialAnnualHarvest) {
    sustainabilityNote = `Warning: Your estimated annual water demand (${annualDemand.toFixed(0)} L) exceeds the potential annual rainwater harvest (${potentialAnnualHarvest.toFixed(0)} L). Rainwater alone may not be sufficient to meet your full annual needs sustainably.`;
} else if (dailyWaterDemandL > 0 && annualDemand * 0.8 > potentialAnnualHarvest) {
    sustainabilityNote = `Note: Your estimated annual water demand (${annualDemand.toFixed(0)} L) is close to your potential annual rainwater harvest (${potentialAnnualHarvest.toFixed(0)} L). Consider additional water sources or conservation measures for long-term sustainability.`;
}

return {
    potentialAnnualHarvest: potentialAnnualHarvest,
    recommendedStorageVolume: recommendedStorageVolume,
    daysOfSupply: daysOfSupply,
    sustainabilityNote: sustainabilityNote
};
