const bedVolumeCubicFeet = inputs.bedLength * inputs.bedWidth * inputs.bedDepth;
const totalVolumeCubicFeet = bedVolumeCubicFeet * inputs.numBeds;
const totalVolumeCubicYards = totalVolumeCubicFeet / 27; // 1 cubic yard = 27 cubic feet

let costPerCubicFoot = inputs.costPerUnit;
if (inputs.costUnitType.toLowerCase() === 'cubic yard') {
    costPerCubicFoot = inputs.costPerUnit / 27; // Convert cubic yard cost to cubic foot cost
} else if (inputs.costUnitType.toLowerCase() === 'cubic foot') {
    costPerCubicFoot = inputs.costPerUnit;
} else {
    // Default to assuming cubic foot if unit type is unrecognized
    // Or handle as an error/warning if strict validation is needed
    costPerCubicFoot = inputs.costPerUnit; 
}

const totalCost = totalVolumeCubicFeet * costPerCubicFoot;

const totalWeight = totalVolumeCubicFeet * inputs.avgSoilDensity;

return {
    totalVolumeCubicFeet: totalVolumeCubicFeet,
    totalVolumeCubicYards: totalVolumeCubicYards,
    totalCost: totalCost,
    totalWeight: totalWeight
};
