function calculateRisk(inputs) {
    const {
        materialCriticality,
        numPrimarySuppliers,
        topSupplierConcentration,
        avgGeopoliticalStability,
        marketPriceVolatility,
        inventoryBufferDays,
        substituteAvailability,
        logisticsRiskScore
    } = inputs;

    // --- Input Validation and Clamping --- 
    const clamp = (value, min, max) => Math.max(min, Math.min(value, max));

    const mc = clamp(materialCriticality, 1, 5);
    const nps = clamp(numPrimarySuppliers, 1, 10); // Cap at 10 for risk calculation scaling
    const tsc = clamp(topSupplierConcentration, 0, 100);
    const ags = clamp(avgGeopoliticalStability, 1, 5);
    const mpv = clamp(marketPriceVolatility, 1, 5);
    const ibd = clamp(inventoryBufferDays, 0, 365);
    const sa = clamp(substituteAvailability, 1, 5);
    const lrs = clamp(logisticsRiskScore, 1, 5);

    // --- Step 1: Normalize and Invert Risk Components (scale to 0-5 where 5 is highest risk) ---

    // Material Criticality (Higher is worse, already 1-5)
    const risk_mc = mc; // Range 1-5

    // Number of Primary Suppliers (More suppliers = lower risk)
    // From (1-10) to (5-0) risk. 1 supplier = 5 risk, 10 suppliers = 0 risk.
    const risk_nps = Math.max(0, (1 - ((nps - 1) / 9)) * 5);

    // Top Supplier Concentration (Higher concentration = higher risk)
    // From (0-100%) to (0-5) risk. 0% = 0 risk, 100% = 5 risk.
    const risk_tsc = tsc / 20;

    // Average Geopolitical Stability (Higher stability = lower risk)
    // From (1-5) to (4-0) risk. 1 stability = 4 risk, 5 stability = 0 risk.
    const risk_ags = 5 - ags;

    // Market Price Volatility (Higher is worse, already 1-5)
    const risk_mpv = mpv; // Range 1-5

    // Inventory Buffer Days (More days = lower risk)
    // From (0-365) to (5-0) risk. 0 days = 5 risk, 150 days buffer (5 units of 30 days) means 0 risk.
    const risk_ibd = Math.max(0, 5 - (ibd / 30));

    // Substitute Availability (Higher availability = lower risk)
    // From (1-5) to (4-0) risk. 1 availability = 4 risk, 5 availability = 0 risk.
    const risk_sa = 5 - sa;

    // Logistics Risk Score (Higher is worse, already 1-5)
    const risk_lrs = lrs; // Range 1-5

    // --- Step 2: Calculate Weighted Raw Risk Score ---
    // Weights chosen based on perceived impact on supply chain risk severity
    const WEIGHT_MC = 2.5;
    const WEIGHT_NPS = 1.5;
    const WEIGHT_TSC = 2.0;
    const WEIGHT_AGS = 1.8;
    const WEIGHT_MPV = 1.2;
    const WEIGHT_IBD = 1.0;
    const WEIGHT_SA = 1.3;
    const WEIGHT_LRS = 1.0;

    const rawRiskScore =
        (risk_mc * WEIGHT_MC) +
        (risk_nps * WEIGHT_NPS) +
        (risk_tsc * WEIGHT_TSC) +
        (risk_ags * WEIGHT_AGS) +
        (risk_mpv * WEIGHT_MPV) +
        (risk_ibd * WEIGHT_IBD) +
        (risk_sa * WEIGHT_SA) +
        (risk_lrs * WEIGHT_LRS);

    // --- Step 3: Scale Raw Risk Score to Overall Risk Score (0-100) ---
    // Calculate maximum possible raw score based on worst-case inputs and applied clamping
    const maxRawRiskScore =
        (5 * WEIGHT_MC) +   // Max risk_mc
        (5 * WEIGHT_NPS) +  // Max risk_nps (1 supplier)
        (5 * WEIGHT_TSC) +  // Max risk_tsc (100% concentration)
        (4 * WEIGHT_AGS) +  // Max risk_ags (1 stability)
        (5 * WEIGHT_MPV) +  // Max risk_mpv
        (5 * WEIGHT_IBD) +  // Max risk_ibd (0 days buffer)
        (4 * WEIGHT_SA) +   // Max risk_sa (1 substitute availability)
        (5 * WEIGHT_LRS);   // Max risk_lrs

    const overallRiskScore = Math.min(100, Math.max(0, (rawRiskScore / maxRawRiskScore) * 100));

    // --- Step 4: Determine Vulnerability Level ---
    let vulnerabilityLevel = "Low";
    if (overallRiskScore > 75) {
        vulnerabilityLevel = "Critical";
    } else if (overallRiskScore > 50) {
        vulnerabilityLevel = "High";
    } else if (overallRiskScore > 25) {
        vulnerabilityLevel = "Moderate";
    }

    // --- Step 5: Calculate Mitigation Priority Score (0-100) ---
    // Emphasize mitigation for higher criticality materials. Linear increase up to 20% for highest criticality.
    const mitigationPriority = Math.min(100, Math.max(0, overallRiskScore * (1 + (mc - 1) * 0.05)));

    return {
        overallRiskScore: parseFloat(overallRiskScore.toFixed(1)),
        vulnerabilityLevel: vulnerabilityLevel,
        mitigationPriority: parseFloat(mitigationPriority.toFixed(1))
    };
}
