function calculate({ fc_prime, fy, bar_size, cover, clear_spacing, rebar_coating_type, concrete_placement_type, lightweight_concrete_type, splice_class }) {

  // Input validation and constraints
  if (fc_prime < 2500 || fc_prime > 10000) throw new Error("f'c must be between 2500 and 10000 psi.");
  if (fy < 40000 || fy > 80000) throw new Error("fy must be between 40000 and 80000 psi.");
  if (bar_size < 3 || bar_size > 18 || [12,13,15,16,17].includes(bar_size)) throw new Error("Invalid rebar bar size. Please use standard bar sizes #3 through #11, #14, or #18.");
  if (cover < 0.75) throw new Error("Concrete cover must be at least 0.75 inches.");
  if (clear_spacing < 0.75) throw new Error("Clear spacing must be at least 0.75 inches.");

  // 1. Determine bar diameter (db) based on bar_size
  let db;
  switch (bar_size) {
    case 3: db = 0.375; break;
    case 4: db = 0.500; break;
    case 5: db = 0.625; break;
    case 6: db = 0.750; break;
    case 7: db = 0.875; break;
    case 8: db = 1.000; break;
    case 9: db = 1.128; break;
    case 10: db = 1.270; break;
    case 11: db = 1.410; break;
    case 14: db = 1.693; break;
    case 18: db = 2.257; break;
    default: throw new Error("Unsupported bar size.");
  }

  // 2. Determine modification factors (psi_t, psi_e, psi_s, lambda)
  let psi_t; // Concrete placement factor (ACI 318-19, Table 25.4.2.4)
  if (concrete_placement_type === 'top_bars') {
    psi_t = 1.3;
  } else {
    psi_t = 1.0; // Other bars
  }

  let psi_e; // Epoxy coating factor (ACI 318-19, Table 25.4.2.4)
  // ACI 318-19, Table 25.4.2.4 conditions for epoxy:
  // 1.2 if cover >= db and clear spacing >= 3db
  // 1.5 if cover < db or clear spacing < 3db
  const isStandardEpoxyCondition = (clear_spacing >= (3 * db)) && (cover >= db);

  if (rebar_coating_type === 'uncoated') {
    psi_e = 1.0;
  } else if (rebar_coating_type === 'epoxy_standard') {
      // User explicitly selected standard, but ensure conditions are met for consistency
      if (isStandardEpoxyCondition) {
          psi_e = 1.2;
      } else {
          // If conditions not met, override to higher factor as per ACI
          psi_e = 1.5;
      }
  } else if (rebar_coating_type === 'epoxy_reduced') {
      psi_e = 1.5;
  } else {
      // Fallback for unexpected selection, should ideally not be reached with dropdowns
      psi_e = 1.0; 
  }

  // ACI 318-19 25.4.2.4(c): (ψt * ψe) shall not exceed 1.7
  let product_psi_te = psi_t * psi_e;
  if (product_psi_te > 1.7) {
    product_psi_te = 1.7;
  }

  let psi_s; // Bar size factor (ACI 318-19, Table 25.4.2.4)
  if (bar_size <= 6) {
    psi_s = 0.8; // #6 and smaller bars
  } else {
    psi_s = 1.0; // #7 and larger bars
  }

  let lambda; // Lightweight concrete factor (ACI 318-19, Table 25.4.2.4)
  if (lightweight_concrete_type === 'lightweight') {
    lambda = 1.3;
  } else {
    lambda = 1.0; // Normalweight concrete
  }

  // 3. Calculate c_b and the (c_b + Ktr) / db ratio
  // Ktr is assumed to be 0 for this simplified calculation per ACI 318-19, R25.4.2.3
  const Ktr = 0;

  // c_b is the lesser of:
  // (a) distance from the center of the bar to the nearest concrete surface
  // (b) one-half the center-to-center spacing of the bars being developed.
  // ACI 318-19, 25.4.2.3
  const cb_to_surface = cover + (db / 2);
  const cb_half_spacing = clear_spacing / 2 + (db / 2);
  const c_b = Math.min(cb_to_surface, cb_half_spacing);

  // The term (c_b + Ktr) / db shall not be taken greater than 2.5 (ACI 318-19, 25.4.2.3)
  let cb_ktr_over_db = (c_b + Ktr) / db;
  if (cb_ktr_over_db > 2.5) {
    cb_ktr_over_db = 2.5;
  }

  // 4. Calculate Development Length (Ld)
  // ACI 318-19, Eq. (25.4.2.3a) simplified with Ktr=0:
  // Ld = (3/40) * (fy * ψt * ψe * ψs * λ) / (sqrt(f'c)) * (db / ((c_b + Ktr) / db))
  let ld_numerator = 0.75 * fy * product_psi_te * psi_s * lambda * db;
  let ld_denominator = Math.sqrt(fc_prime) * cb_ktr_over_db;

  let ld = ld_numerator / ld_denominator;

  // Minimum Development Length (ACI 318-19, 25.4.2.1)
  const ld_min = 12; // inches
  ld = Math.max(ld, ld_min);

  // 5. Calculate Splice Length (Ls) (ACI 318-19, 25.5.2.1)
  let ls;
  if (splice_class === 'class_a') {
    ls = 1.0 * ld; // Class A: min 1.0 Ld (req'd Ast / prov Ast <= 2 and max 50% bars spliced)
  } else if (splice_class === 'class_b') {
    ls = 1.3 * ld; // Class B: min 1.3 Ld (all other cases, especially if > 50% bars spliced)
  } else {
    throw new Error("Invalid splice class selection.");
  }

  return {
    ld: ld,
    ls: ls
  };
}
