const q1 = Math.max(0, inputs.ing1Quantity);
const c1 = Math.max(0, inputs.ing1CostPerUnit);
const q2 = Math.max(0, inputs.ing2Quantity);
const c2 = Math.max(0, inputs.ing2CostPerUnit);
const q3 = Math.max(0, inputs.ing3Quantity);
const c3 = Math.max(0, inputs.ing3CostPerUnit);
let servings = Math.max(0, inputs.servings);

const cost1 = q1 * c1;
const cost2 = q2 * c2;
const cost3 = q3 * c3;

const totalRecipeCost = cost1 + cost2 + cost3;

// Prevent division by zero. If servings is 0, cost per serving is effectively undefined or infinite, but for practical output, we'll cap at 0.
const costPerServing = servings > 0 ? totalRecipeCost / servings : 0;

return { totalRecipeCost, costPerServing };
