const { originalCaloriesTotal, originalProteinTotal, originalCarbsTotal, originalFatTotal, substituteCaloriesTotal, substituteProteinTotal, substituteCarbsTotal, substituteFatTotal } = inputs;

// Calculate net changes
const netCaloriesChange = substituteCaloriesTotal - originalCaloriesTotal;
const netProteinChange = substituteProteinTotal - originalProteinTotal;
const netCarbsChange = substituteCarbsTotal - originalCarbsTotal;
const netFatChange = substituteFatTotal - originalFatTotal;

// Helper function to calculate percentage change, with caps for extreme values
const calculatePercentageChange = (change, original) => {
    if (original === 0) {
        return change === 0 ? 0 : (change > 0 ? 10000 : -10000); // Cap at +/- 10,000% if original is zero
    }
    const percent = (change / original) * 100;
    return Math.max(Math.min(percent, 10000), -10000); // Cap actual percentage to avoid display issues
};

const percentageCalorieChange = calculatePercentageChange(netCaloriesChange, originalCaloriesTotal);
const percentageProteinChange = calculatePercentageChange(netProteinChange, originalProteinTotal);
const percentageCarbsChange = calculatePercentageChange(netCarbsChange, originalCarbsTotal);
const percentageFatChange = calculatePercentageChange(netFatChange, originalFatTotal);

return {
    netCaloriesChange,
    netProteinChange,
    netCarbsChange,
    netFatChange,
    percentageCalorieChange,
    percentageProteinChange,
    percentageCarbsChange,
    percentageFatChange
};
