let { originalServings, desiredServings, originalQuantity, originalUnit, wasteFactor, minBatchQuantity } = inputs;

originalServings = parseFloat(originalServings) || 1;
desiredServings = parseFloat(desiredServings) || 1;
originalQuantity = parseFloat(originalQuantity) || 0;
wasteFactor = parseFloat(wasteFactor) / 100 || 0;
minBatchQuantity = parseFloat(minBatchQuantity) || 0;

// Ensure inputs are positive for sensible calculations
originalServings = Math.max(1, originalServings); 
desiredServings = Math.max(1, desiredServings);   
originalQuantity = Math.max(0, originalQuantity); 

let scalingFactor = desiredServings / originalServings;
let scaledQuantity = originalQuantity * scalingFactor;

// Apply waste factor
scaledQuantity = scaledQuantity * (1 + wasteFactor);

// Apply minimum batch quantity cap
scaledQuantity = Math.max(minBatchQuantity, scaledQuantity);

return {
    scaledQuantity: scaledQuantity,
    scaledUnit: originalUnit 
};
