const { system_capacity_btuh, refrigerant_type, line_type, line_length_ft, design_temp_f, target_pressure_drop_psi, max_velocity_fpm } = inputs;

const PI = Math.PI;

// Simplified refrigerant properties and sizing factors (for R-410A as a base example)
// In a real application, these would come from a comprehensive database or more complex calculation.
let specific_enthalpy_diff_btu_lb = 60; // Typical for cooling effect in vapor compression cycle
let base_density_lb_ft3, base_specific_volume_ft3_lb, k_dp_factor_per_100ft_lb_hr_ft_in_5;

// Adjust properties based on line type
// Constants are empirical and tuned for reasonable results for R-410A
switch (line_type.toLowerCase().trim()) {
    case 'suction':
        base_density_lb_ft3 = 1.5; // R-410A @ 40F
        base_specific_volume_ft3_lb = 1 / base_density_lb_ft3;
        // Empirical pressure drop constant. P_drop_psi = K_dp * (L_ft/100) * (mass_flow_lb_hr^2 * specific_volume_ft3_lb) / (di_in^5)
        k_dp_factor_per_100ft_lb_hr_ft_in_5 = 0.000000001; 
        break;
    case 'liquid':
        base_density_lb_ft3 = 65; // R-410A @ 90F (saturated liquid)
        base_specific_volume_ft3_lb = 1 / base_density_lb_ft3;
        k_dp_factor_per_100ft_lb_hr_ft_in_5 = 0.000000000000005; 
        break;
    case 'hot gas':
        base_density_lb_ft3 = 3; // R-410A @ 120F superheated gas
        base_specific_volume_ft3_lb = 1 / base_density_lb_ft3;
        k_dp_factor_per_100ft_lb_hr_ft_in_5 = 0.0000000005; 
        break;
    default:
        return {
            recommended_diameter_in: null,
            actual_pressure_drop_psi: null,
            actual_velocity_fpm: null,
            error: "Invalid line type. Choose Suction, Liquid, or Hot Gas (case-insensitive)."
        };
}

// Simple temperature adjustment for specific volume (assuming ideal gas behavior approximation for vapor)
// Liquid specific volume is less sensitive to temperature, so a simpler adjustment for vapor lines.
let specific_volume_ft3_lb = base_specific_volume_ft3_lb;
if (line_type.toLowerCase().trim() !== 'liquid') {
    // Base temperatures for R-410A for the density constants
    let base_temp_f = (line_type.toLowerCase().trim() === 'suction') ? 40 : 120;
    specific_volume_ft3_lb *= (460 + design_temp_f) / (460 + base_temp_f); // Absolute temperature conversion
}

// 1. Calculate mass flow rate (lb/hr)
const mass_flow_rate_lb_hr = system_capacity_btuh / specific_enthalpy_diff_btu_lb;

// 2. Calculate volumetric flow rate (ft^3/min)
const volumetric_flow_rate_ft3_min = mass_flow_rate_lb_hr * specific_volume_ft3_lb / 60; // convert hr to min

// Standard internal copper tubing diameters (in inches) - common sizes
// These are typical IDs, actual IDs vary slightly by wall thickness.
const pipe_di_inches = [
    0.250, 0.315, 0.375, 0.430, 0.500, 0.625, 0.750, 0.875, 1.000, 1.125, 1.250,
    1.375, 1.500, 1.625, 1.750, 2.000, 2.125, 2.250, 2.500, 2.625, 3.000, 3.125, 3.500, 4.000
];

let best_di = null;
let actual_pressure_drop = null;
let actual_velocity = null;

// Iterate through standard pipe sizes to find the smallest one that meets criteria
for (const di_in of pipe_di_inches) {
    if (di_in === 0) continue;

    const area_ft2 = PI * Math.pow(di_in / 12, 2) / 4; // Area in square feet
    if (area_ft2 === 0) continue;

    const current_velocity_fpm = volumetric_flow_rate_ft3_min / area_ft2;

    // Simplified pressure drop formula (empirical) - based on common sizing charts
    // P_drop_psi = K_dp * (L_ft/100) * (mass_flow_rate_lb_hr^2 * specific_volume_ft3_lb) / (di_in^5)
    const current_pressure_drop_psi = k_dp_factor_per_100ft_lb_hr_ft_in_5 * (line_length_ft / 100) *
                                      (Math.pow(mass_flow_rate_lb_hr, 2) * specific_volume_ft3_lb) / Math.pow(di_in, 5);

    const meets_velocity = current_velocity_fpm <= max_velocity_fpm;
    const meets_pressure_drop = current_pressure_drop_psi <= target_pressure_drop_psi;

    if (meets_velocity && meets_pressure_drop) {
        best_di = di_in;
        actual_pressure_drop = current_pressure_drop_psi;
        actual_velocity = current_velocity_fpm;
        break; // Found the smallest suitable size, exit loop
    }
}

let warning_message = "";

if (best_di === null) {
    // If no size met the criteria, provide details for the largest available pipe and issue a warning
    const largest_di_in = pipe_di_inches[pipe_di_inches.length - 1];
    const area_ft2_largest = PI * Math.pow(largest_di_in / 12, 2) / 4;
    actual_velocity = volumetric_flow_rate_ft3_min / area_ft2_largest;
    actual_pressure_drop = k_dp_factor_per_100ft_lb_hr_ft_in_5 * (line_length_ft / 100) *
                            (Math.pow(mass_flow_rate_lb_hr, 2) * specific_volume_ft3_lb) / Math.pow(largest_di_in, 5);

    best_di = largest_di_in;
    warning_message = `No standard pipe size met all specified criteria. The largest available size (${largest_di_in} in) is shown, but may not be adequate. Please review your inputs (especially capacity, target pressure drop, or velocity limits).`;
}

return {
    recommended_diameter_in: best_di,
    actual_pressure_drop_psi: actual_pressure_drop,
    actual_velocity_fpm: actual_velocity,
    warning: warning_message.length > 0 ? warning_message : undefined
};
