const actual_suction_line_temp = inputs.actual_suction_line_temp;
const saturated_suction_temp = inputs.saturated_suction_temp;
const actual_liquid_line_temp = inputs.actual_liquid_line_temp;
const saturated_liquid_temp = inputs.saturated_liquid_temp;

// Calculate Superheat
const calculated_superheat = actual_suction_line_temp - saturated_suction_temp;

// Calculate Subcooling
const calculated_subcooling = saturated_liquid_temp - actual_liquid_line_temp;

// Determine Charge Status (simplified for demonstration - always refer to manufacturer specs)
let charge_status = "Undetermined";

// Example target ranges (these are highly generalized and depend on many factors, e.g., TXV vs fixed orifice, ambient temps)
const ideal_superheat_min = 8; // °F
const ideal_superheat_max = 12; // °F
const ideal_subcooling_min = 8; // °F
const ideal_subcooling_max = 12; // °F

if (isNaN(calculated_superheat) || isNaN(calculated_subcooling)) {
    charge_status = "Invalid Input";
} else if (calculated_superheat < ideal_superheat_min && calculated_subcooling < ideal_subcooling_min) {
    charge_status = "Likely Undercharged (Low SH, Low SC)";
} else if (calculated_superheat > ideal_superheat_max && calculated_subcooling > ideal_subcooling_max) {
    charge_status = "Likely Overcharged (High SH, High SC)";
} else if (calculated_superheat >= ideal_superheat_min && calculated_superheat <= ideal_superheat_max &&
           calculated_subcooling >= ideal_subcooling_min && calculated_subcooling <= ideal_subcooling_max) {
    charge_status = "Likely Correctly Charged (within general ideal range)";
} else if (calculated_superheat < ideal_superheat_min) {
    charge_status = "Low Superheat (Potential overcharge, low load, or airflow issues)";
} else if (calculated_superheat > ideal_superheat_max) {
    charge_status = "High Superheat (Potential undercharge, low refrigerant flow, or airflow issues)";
} else if (calculated_subcooling < ideal_subcooling_min) {
    charge_status = "Low Subcooling (Potential undercharge or liquid line restriction)";
} else if (calculated_subcooling > ideal_subcooling_max) {
    charge_status = "High Subcooling (Potential overcharge or non-condensables)";
} else {
    charge_status = "Charge Status Complex (requires expert diagnosis and specific manufacturer data)";
}

return {
    calculated_superheat: calculated_superheat,
    calculated_subcooling: calculated_subcooling,
    charge_status: charge_status
};
