function calculate({
    population_total,
    vulnerable_population_percent,
    expected_disruption_duration_days,
    healthcare_facility_resilience_score,
    food_cold_chain_reliance_percent,
    extreme_temp_vulnerability_index,
    critical_infra_backup_percent,
    public_health_preparedness_index
}) {
    // Input validation and normalization
    population_total = Math.max(0, population_total || 0);
    vulnerable_population_percent = Math.max(0, Math.min(100, vulnerable_population_percent || 0));
    expected_disruption_duration_days = Math.max(0, expected_disruption_duration_days || 0);
    healthcare_facility_resilience_score = Math.max(1, Math.min(5, healthcare_facility_resilience_score || 1));
    food_cold_chain_reliance_percent = Math.max(0, Math.min(100, food_cold_chain_reliance_percent || 0));
    extreme_temp_vulnerability_index = Math.max(1, Math.min(5, extreme_temp_vulnerability_index || 1));
    critical_infra_backup_percent = Math.max(0, Math.min(100, critical_infra_backup_percent || 0));
    public_health_preparedness_index = Math.max(1, Math.min(5, public_health_preparedness_index || 1));

    if (population_total === 0 || expected_disruption_duration_days === 0) {
        return {
            overall_health_risk_score: 0,
            healthcare_impact_severity: 0,
            food_security_risk_level: 0,
            vulnerable_population_exposure_index: 0,
            estimated_daily_health_economic_cost_usd: 0
        };
    }

    const vulnerable_population_factor = vulnerable_population_percent / 100;
    const disruption_intensity_factor = Math.log1p(expected_disruption_duration_days) / Math.log1p(30); // Max 30 days for scaling roughly to 1
    // log1p(x) = log(1+x). This makes short durations less impactful and long durations plateau the rate of increase.

    // 1. Healthcare System Impact Severity (0-100)
    // Resilience: 5 (highly resilient) -> 0 risk contribution, 1 (not resilient) -> 1 risk contribution
    const healthcare_resilience_inverted = (5 - healthcare_facility_resilience_score) / 4; // Normalized 0-1
    let healthcare_impact_severity = disruption_intensity_factor * 0.4 * (1 + vulnerable_population_factor * 0.5) * (1 + healthcare_resilience_inverted * 0.8) * 100; // Increased impact with vulnerability and low resilience
    healthcare_impact_severity = Math.min(100, Math.max(0, healthcare_impact_severity));

    // 2. Food Security Disruption Risk Level (0-100)
    const food_cold_chain_risk_factor = food_cold_chain_reliance_percent / 100; // 0-1
    let food_security_risk_level = disruption_intensity_factor * 0.5 * (food_cold_chain_risk_factor * 0.7 + vulnerable_population_factor * 0.3) * 100; // Cold chain is a major driver, plus vulnerable pop
    food_security_risk_level = Math.min(100, Math.max(0, food_security_risk_level));

    // 3. Vulnerable Population Exposure Index (Climate & Basic Needs) (0-100)
    // Extreme temperature vulnerability: 5 -> 1 (very vulnerable), 1 -> 0 (not vulnerable)
    const extreme_temp_vulnerability_factor = (extreme_temp_vulnerability_index - 1) / 4; // Normalized 0-1
    // Critical infrastructure backup: 0% -> 1 (no backup = high risk), 100% -> 0 (full backup = low risk)
    const critical_infra_risk_factor = (100 - critical_infra_backup_percent) / 100; // Normalized 0-1
    let vulnerable_population_exposure_index = disruption_intensity_factor * 0.6 * (extreme_temp_vulnerability_factor * 0.6 + critical_infra_risk_factor * 0.4) * vulnerable_population_factor * 100;
    vulnerable_population_exposure_index = Math.min(100, Math.max(0, vulnerable_population_exposure_index));

    // 4. Overall Public Health Risk Score (Composite) (0-100)
    // Public health preparedness: 5 (highly prepared) -> 0 mitigation factor, 1 (not prepared) -> 1 mitigation factor (increases risk)
    const preparedness_mitigation_factor = (5 - public_health_preparedness_index) / 4; // Normalized 0-1 for risk increase contribution

    // Base overall risk combines weighted individual risks
    let overall_health_risk_score = (
        healthcare_impact_severity * 0.35 + 
        food_security_risk_level * 0.30 + 
        vulnerable_population_exposure_index * 0.35
    );

    // Apply overall preparedness as a dampening or amplifying factor (higher preparedness reduces overall score, lower increases)
    // The factor ranges from 0 (perfect preparedness, no additional risk) to 1 (no preparedness, maximum additional risk).
    // We apply it such that a preparedness score of 5 means preparedness_mitigation_factor is 0, so overall_risk is multiplied by 1.
    // A preparedness score of 1 means preparedness_mitigation_factor is 1, so overall_risk is multiplied by (1 + 0.5) = 1.5, increasing it by 50%.
    overall_health_risk_score = overall_health_risk_score * (1 + preparedness_mitigation_factor * 0.5); // Max increase 50% for lowest preparedness
    overall_health_risk_score = Math.min(100, Math.max(0, overall_health_risk_score));

    // 5. Estimated Daily Health-Related Economic Cost (USD)
    // This is a rough estimation. Assume a baseline cost per person per day of disruption, 
    // multiplied by population affected and the risk score. 
    // Placeholder: A base cost of $10 per person, scaled by risk and vulnerability. 
    // This is not a direct healthcare cost, but a broader socio-economic burden due to health impacts.
    const base_daily_health_impact_cost_per_capita = 12; // Hypothetical daily cost due to health system strain/poor health outcomes per person
    // The effective affected population considers general population but weighs vulnerable population more heavily.
    const affected_population_multiplier = 0.2 + (vulnerable_population_factor * 0.5); // At least 20% of pop, plus 50% of vulnerable part
    const effective_affected_population = population_total * affected_population_multiplier;

    let estimated_daily_health_economic_cost_usd = 
        effective_affected_population * 
        base_daily_health_impact_cost_per_capita * 
        (overall_health_risk_score / 100) * // Scale by overall risk (0-1)
        disruption_intensity_factor; // Scale by disruption duration (longer = higher daily cost effect)

    estimated_daily_health_economic_cost_usd = Math.max(0, estimated_daily_health_economic_cost_usd);

    return {
        overall_health_risk_score: parseFloat(overall_health_risk_score.toFixed(2)),
        healthcare_impact_severity: parseFloat(healthcare_impact_severity.toFixed(2)),
        food_security_risk_level: parseFloat(food_security_risk_level.toFixed(2)),
        vulnerable_population_exposure_index: parseFloat(vulnerable_population_exposure_index.toFixed(2)),
        estimated_daily_health_economic_cost_usd: Math.round(estimated_daily_health_economic_cost_usd)
    };
}
