/**
 * Calculates the Regional Fuel Supply Chain Resilience Score.
 * @param {object} inputs - The input values from the form.
 * @param {number} inputs.localProductionShare - Share of local fuel production (%).
 * @param {number} inputs.strategicReserveDays - Strategic fuel reserves (days).
 * @param {number} inputs.importDependencyShare - Import dependency (%).
 * @param {number} inputs.importSourceDiversification - Number of primary import sources.
 * @param {number} inputs.alternativeEnergyContribution - Alternative energy contribution (%).
 * @param {number} inputs.infrastructureVulnerability - Critical infrastructure vulnerability index (0-1).
 * @param {number} inputs.demandGrowthForecast - Annual fuel demand growth forecast (%).
 * @returns {object} An object containing the resilience score and contributing factors.
 */
function calculateResilience(inputs) {
    const {
        localProductionShare,
        strategicReserveDays,
        importDependencyShare,
        importSourceDiversification,
        alternativeEnergyContribution,
        infrastructureVulnerability,
        demandGrowthForecast
    } = inputs;

    // --- Step 1: Normalize input values and calculate component scores ---

    // A. Local Production & Reserves (Positive impact on resilience)
    // Max local production = 100%, Max reserves = 365 days (1 year)
    const localProductionFactor = Math.min(localProductionShare / 100, 1);
    const reserveFactor = Math.min(strategicReserveDays / 180, 1); // 180 days (6 months) considered a strong buffer, cap at 1
    const bufferCapacityScore = (localProductionFactor * 0.4 + reserveFactor * 0.6) * 100; // Weighted average

    // B. Import Dependency & Diversification (Negative impact on resilience)
    // Higher dependency, lower diversification -> higher risk.
    const dependencyFactorRaw = importDependencyShare / 100;
    // Diversification score: 1 source is max vulnerability, 10+ sources is low vulnerability.
    const diversificationScore = Math.min(importSourceDiversification / 5, 1); // 5+ sources is considered good diversification, cap at 1
    const importRisk = (dependencyFactorRaw * 0.7) + ((1 - diversificationScore) * 0.3); // Higher importRisk = worse
    const dependencyRiskFactor = Math.min(importRisk * 100, 100); // Scale to 0-100

    // C. Alternative Energy & Demand Growth (Future-proofing, Positive impact)
    // Higher alt energy, lower demand growth -> better resilience.
    const altEnergyFactor = Math.min(alternativeEnergyContribution / 100, 1);
    const demandPressureFactor = Math.max(0, 1 - (demandGrowthForecast / 5)); // 0% growth is ideal (score 1), 5% growth is max pressure (score 0)
    const futureReadinessFactor = (altEnergyFactor * 0.6 + demandPressureFactor * 0.4);

    // D. Infrastructure Vulnerability (Negative impact)
    const vulnerabilityPenalty = infrastructureVulnerability; // Directly use index as a penalty (0-1)

    // --- Step 2: Combine component scores with weights ---
    // Weights sum to 1.0
    const weightBufferCapacity = 0.30; // Local production and reserves
    const weightImportDependency = 0.35; // Import risk and diversification
    const weightFutureReadiness = 0.25; // Alternative energy and demand growth
    const weightInfrastructureVulnerability = 0.10; // Direct infrastructure risk

    // Base Resilience Score (before penalties)
    let resilience = 
        (bufferCapacityScore / 100 * weightBufferCapacity) + 
        ((100 - dependencyRiskFactor) / 100 * weightImportDependency) + 
        (futureReadinessFactor * weightFutureReadiness);

    // Apply infrastructure vulnerability as a direct reduction to the overall score
    resilience -= (vulnerabilityPenalty * weightInfrastructureVulnerability);

    // --- Step 3: Final Score Calculation and Bounding ---
    let overallResilienceScore = resilience * 100; // Scale to 0-100

    // Ensure the score is within 0-100 range
    overallResilienceScore = Math.max(0, Math.min(100, overallResilienceScore));

    return {
        resilienceScore: parseFloat(overallResilienceScore.toFixed(1)),
        dependencyRiskFactor: parseFloat(dependencyRiskFactor.toFixed(1)),
        bufferCapacityScore: parseFloat(bufferCapacityScore.toFixed(1))
    };
}
