function calculate() {
    const renewableCapacityMW = parseFloat(inputs.renewableCapacityMW);
    const capexPerMWGen = parseFloat(inputs.capexPerMWGen);
    const opexPerMWhGen = parseFloat(inputs.opexPerMWhGen);
    const storageCapacityMWh = parseFloat(inputs.storageCapacityMWh);
    const capexPerMWhStorage = parseFloat(inputs.capexPerMWhStorage);
    const totalGridUpgradeCost = parseFloat(inputs.totalGridUpgradeCost);
    const avgEnergyPriceMWh = parseFloat(inputs.avgEnergyPriceMWh);
    let capacityFactor = parseFloat(inputs.capacityFactor) / 100; // Convert to decimal
    const projectLifetimeYears = parseFloat(inputs.projectLifetimeYears);
    let discountRate = parseFloat(inputs.discountRate) / 100; // Convert to decimal

    // Input validation and clamping
    if (isNaN(renewableCapacityMW) || renewableCapacityMW <= 0) return { error: 'Renewable Capacity must be a positive number.' };
    if (isNaN(capexPerMWGen) || capexPerMWGen < 0) return { error: 'Generation CAPEX per MW must be non-negative.' };
    if (isNaN(opexPerMWhGen) || opexPerMWhGen < 0) return { error: 'O&M Cost per MWh must be non-negative.' };
    if (isNaN(storageCapacityMWh) || storageCapacityMWh < 0) return { error: 'Storage Capacity must be non-negative.' };
    if (isNaN(capexPerMWhStorage) || capexPerMWhStorage < 0) return { error: 'Storage CAPEX per MWh must be non-negative.' };
    if (isNaN(totalGridUpgradeCost) || totalGridUpgradeCost < 0) return { error: 'Grid Upgrade Cost must be non-negative.' };
    if (isNaN(avgEnergyPriceMWh) || avgEnergyPriceMWh < 0) return { error: 'Average Energy Sales Price must be non-negative.' };
    capacityFactor = Math.max(0, Math.min(1, capacityFactor)); // Clamp capacity factor between 0 and 1
    if (isNaN(projectLifetimeYears) || projectLifetimeYears <= 0) return { error: 'Project Lifetime must be a positive number.' };
    if (isNaN(discountRate) || discountRate < 0) return { error: 'Discount Rate must be non-negative.' };

    // 1. Calculate Total Capital Expenditure (CAPEX)
    const generationCapex = renewableCapacityMW * capexPerMWGen;
    const storageCapex = storageCapacityMWh * capexPerMWhStorage;
    const totalCapex = generationCapex + storageCapex + totalGridUpgradeCost;

    // Handle case where capex is 0 (though unlikely for real projects)
    if (totalCapex === 0) {
        return {
            totalCapex: 0,
            annualEnergyGeneration: 0,
            annualOpex: 0,
            annualRevenue: 0,
            annualNetCashFlow: 0,
            npv: 0,
            irr: 0,
            paybackPeriod: 0
        };
    }

    // 2. Estimate Annual Energy Generation
    const operatingHoursPerYear = 8760;
    const annualEnergyGeneration = renewableCapacityMW * operatingHoursPerYear * capacityFactor;

    // 3. Determine Annual O&M Costs and Revenue
    const annualOpex = annualEnergyGeneration * opexPerMWhGen;
    const annualRevenue = annualEnergyGeneration * avgEnergyPriceMWh;

    // 4. Compute Annual Net Cash Flow
    // Assuming constant cash flow for simplicity over project life, for initial calculation.
    const annualNetCashFlow = annualRevenue - annualOpex;

    // Ensure positive cash flow for meaningful NPV/IRR/Payback. If not, results will be negative or infinite.
    if (annualNetCashFlow <= 0 && projectLifetimeYears > 0) {
         return {
            totalCapex: totalCapex,
            annualEnergyGeneration: annualEnergyGeneration,
            annualOpex: annualOpex,
            annualRevenue: annualRevenue,
            annualNetCashFlow: annualNetCashFlow,
            npv: -totalCapex,
            irr: -100, // Represents a very poor return
            paybackPeriod: Infinity
        };
    }

    // 5. Calculate NPV (Net Present Value)
    let npv = -totalCapex; // Initial investment
    if (discountRate < 0) discountRate = 0; // Prevent negative discount rates for calculation sanity

    if (discountRate === 0) {
        npv = annualNetCashFlow * projectLifetimeYears - totalCapex;
    } else {
        for (let year = 1; year <= projectLifetimeYears; year++) {
            npv += annualNetCashFlow / Math.pow(1 + discountRate, year);
        }
    }

    // 6. Calculate Simple Payback Period
    let paybackPeriod = totalCapex / annualNetCashFlow;

    // 7. Calculate IRR (Internal Rate of Return)
    // A simplified iterative method for IRR, as full financial library integration is not feasible here.
    // This uses a bisection method-like approach.
    const calculateNPVForRate = (rate) => {
        let currentNPV = -totalCapex;
        for (let year = 1; year <= projectLifetimeYears; year++) {
            currentNPV += annualNetCashFlow / Math.pow(1 + rate, year);
        }
        return currentNPV;
    };

    let irrLowerBound = -0.99; // -99% (prevent division by zero at -100%)
    let irrUpperBound = 10;   // 1000%
    let irrIterations = 100;
    let irrResult = NaN;

    if (annualNetCashFlow > 0 && totalCapex > 0) {
        for (let i = 0; i < irrIterations; i++) {
            let midRate = (irrLowerBound + irrUpperBound) / 2;
            if (midRate <= -1) { // Prevent rates that make (1+rate) <= 0
                irrLowerBound = midRate;
                continue;
            }
            let npvAtMidRate = calculateNPVForRate(midRate);

            if (Math.abs(npvAtMidRate) < 0.001) { // Found a sufficiently close IRR
                irrResult = midRate;
                break;
            } else if (npvAtMidRate > 0) {
                irrLowerBound = midRate;
            } else {
                irrUpperBound = midRate;
            }
        }
    }

    // If no IRR found, or cash flow isn't positive, it might indicate a non-viable project or approximation limits
    if (isNaN(irrResult) || irrResult === -0.99 || irrResult >= 9.99) {
        if (annualNetCashFlow > 0 && totalCapex > 0 && npv > 0) {
             // If NPV is positive but IRR approximation struggled, assume a positive IRR
            irrResult = (discountRate > 0) ? discountRate * 1.5 : 0.10; // Heuristic: higher than discount rate or a default 10%
        } else {
            irrResult = -1; // Represents very poor or undefined IRR for non-positive cash flows
        }
    }

    // Cap IRR at 1000% for display purposes if it's extremely high
    irrResult = Math.min(irrResult, 10);

    return {
        totalCapex: totalCapex,
        annualEnergyGeneration: annualEnergyGeneration,
        annualOpex: annualOpex,
        annualRevenue: annualRevenue,
        annualNetCashFlow: annualNetCashFlow,
        npv: npv,
        irr: irrResult * 100, // Convert to percentage
        paybackPeriod: paybackPeriod
    };
}
