const EF_COAL_GWH = 900; // ton CO2e/GWh
const EF_NATGAS_GWH = 450; // ton CO2e/GWh
const EF_RENEWABLE_GWH = 20; // ton CO2e/GWh (lifecycle average for renewables like wind, solar, hydro)
const EF_OTHER_ELECTRICITY_GWH = 300; // ton CO2e/GWh (generic factor for unspecified grid mix)

const EF_DIRECT_FUEL_TJ = 70; // ton CO2e/TJ (average for industrial fuels like natural gas, heating oil)

const EF_INDUSTRIAL_WASTE_TON = 0.8; // ton CO2e/ton (simplified average for waste to landfill, includes methane)

const EF_DIESEL_LITER = 0.00268; // ton CO2e/liter
const EF_GASOLINE_LITER = 0.00231; // ton CO2e/liter

// Ensure all inputs are non-negative, defaulting to 0 if undefined
const safeInputs = {};
for (const key in inputs) {
    safeInputs[key] = Math.max(0, inputs[key] || 0);
}

const {
    gridElectricityConsumptionGWh,
    gridElectricityMixCoalPercent,
    gridElectricityMixNaturalGasPercent,
    gridElectricityMixRenewablePercent,
    directFuelConsumptionTJ,
    processEmissionsTonCO2e: inputProcessEmissionsTonCO2e, // Rename to avoid conflict
    industrialWasteToLandfillTon,
    transportDieselLiter,
    transportGasolineLiter
} = safeInputs;

// 1. Calculate Electricity Emissions
let electricityEmissionsTonCO2e = 0;
let totalInputMixPercent = gridElectricityMixCoalPercent + gridElectricityMixNaturalGasPercent + gridElectricityMixRenewablePercent;

let adjustedCoalPercent = gridElectricityMixCoalPercent;
let adjustedNaturalGasPercent = gridElectricityMixNaturalGasPercent;
let adjustedRenewablePercent = gridElectricityMixRenewablePercent;
let adjustedOtherPercent = 0; // Represents the percentage of grid electricity from unspecified sources

if (totalInputMixPercent > 100) {
    // If percentages sum to more than 100, normalize them proportionally to ensure they sum to 100.
    const normalizationFactor = 100 / totalInputMixPercent;
    adjustedCoalPercent *= normalizationFactor;
    adjustedNaturalGasPercent *= normalizationFactor;
    adjustedRenewablePercent *= normalizationFactor;
    adjustedOtherPercent = 0; // No 'other' if normalized percentages already sum to 100
} else if (totalInputMixPercent < 100) {
    // If percentages sum to less than 100, attribute the remainder to 'other' unspecified sources.
    adjustedOtherPercent = 100 - totalInputMixPercent;
}

electricityEmissionsTonCO2e =
    (gridElectricityConsumptionGWh * (adjustedCoalPercent / 100) * EF_COAL_GWH) +
    (gridElectricityConsumptionGWh * (adjustedNaturalGasPercent / 100) * EF_NATGAS_GWH) +
    (gridElectricityConsumptionGWh * (adjustedRenewablePercent / 100) * EF_RENEWABLE_GWH) +
    (gridElectricityConsumptionGWh * (adjustedOtherPercent / 100) * EF_OTHER_ELECTRICITY_GWH);

// 2. Calculate Direct Fuel Emissions
const directFuelEmissionsTonCO2e = directFuelConsumptionTJ * EF_DIRECT_FUEL_TJ;

// 3. Process Emissions (already provided in ton CO2e)
const processEmissionsTonCO2e = inputProcessEmissionsTonCO2e;

// 4. Waste Emissions
const wasteEmissionsTonCO2e = industrialWasteToLandfillTon * EF_INDUSTRIAL_WASTE_TON;

// 5. Transportation Emissions
const transportEmissionsTonCO2e =
    (transportDieselLiter * EF_DIESEL_LITER) +
    (transportGasolineLiter * EF_GASOLINE_LITER);

// Total Emissions
const totalGHGEmissionsTonCO2e =
    electricityEmissionsTonCO2e +
    directFuelEmissionsTonCO2e +
    processEmissionsTonCO2e +
    wasteEmissionsTonCO2e +
    transportEmissionsTonCO2e;

return {
    totalGHGEmissionsTonCO2e,
    electricityEmissionsTonCO2e,
    directFuelEmissionsTonCO2e,
    processEmissionsTonCO2e,
    wasteEmissionsTonCO2e,
    transportEmissionsTonCO2e
};
