function calculate({ baselineIrradiance, irradianceFluctuationPercentage, aerosolOpticalDepth, cloudCoverPercentage, atmosphericPressure, ambientTemperature, solarPanelEfficiency }) {
  // Input validation and normalization
  baselineIrradiance = Math.max(0, baselineIrradiance || 0);
  irradianceFluctuationPercentage = Math.max(-50, Math.min(50, irradianceFluctuationPercentage || 0)); // Cap fluctuation range for realism
  aerosolOpticalDepth = Math.max(0, Math.min(2, aerosolOpticalDepth || 0.1));
  cloudCoverPercentage = Math.max(0, Math.min(100, cloudCoverPercentage || 0));
  atmosphericPressure = Math.max(900, Math.min(1050, atmosphericPressure || 1013.25));
  ambientTemperature = Math.max(-40, Math.min(50, ambientTemperature || 20));
  solarPanelEfficiency = Math.max(5, Math.min(25, solarPanelEfficiency || 18));

  // Step 1: Calculate Effective Solar Irradiance after fluctuation
  const effectiveIrradiance = baselineIrradiance * (1 + irradianceFluctuationPercentage / 100);

  // Step 2: Model Atmospheric Attenuation and Absorption
  // Simplified model for atmospheric attenuation based on typical ranges and effects
  // AOD contribution: Each 0.1 AOD reduces irradiance by approx 2.5% (factor of 0.25 for AOD 1.0 -> 25% reduction)
  // Cloud cover contribution: Each 10% cloud cover reduces irradiance by approx 7% (factor of 0.7 for 100% cloud -> 70% reduction)
  // Pressure effect: Minor, for every 100hPa difference from standard (1013.25hPa), a +/- 2% attenuation effect

  const attenuationAOD = aerosolOpticalDepth * 0.25;
  const attenuationCloud = cloudCoverPercentage / 100 * 0.7;
  const attenuationPressure = (1013.25 - atmosphericPressure) / 100 * 0.02; // Positive if pressure is lower than standard

  let totalAttenuationFactor = (attenuationAOD + attenuationCloud + attenuationPressure);
  totalAttenuationFactor = Math.max(0, Math.min(0.95, totalAttenuationFactor)); // Cap total attenuation to avoid unrealistic results

  const attenuatedIrradiance = effectiveIrradiance * (1 - totalAttenuationFactor);
  const absorbedIrradiance = effectiveIrradiance * totalAttenuationFactor; // Energy absorbed by atmosphere/clouds

  // Step 3: Calculate Solar Power Output Fluctuation
  // Panel efficiency adjusted for temperature (typical degradation ~0.4% per °C above 25°C)
  const tempAdjustedEfficiency = Math.max(5, solarPanelEfficiency - (0.4 * Math.max(0, ambientTemperature - 25)));
  const actualPowerEfficiency = tempAdjustedEfficiency / 100;

  const baselinePowerOutput = baselineIrradiance * (solarPanelEfficiency / 100);
  const predictedPowerOutput = attenuatedIrradiance * actualPowerEfficiency;
  const predictedPowerFluctuation = predictedPowerOutput - baselinePowerOutput;

  // Step 4: Calculate Regional Temperature Anomaly
  // Portion of absorbed irradiance contributes to local heating.
  // Simplified heat capacity model: ~0.004 °C per 1 W/m² absorbed, modified by cloud cover (less direct ground heating under clouds)
  const tempAnomalyFactor = 0.004;
  const cloudCoolingEffect = cloudCoverPercentage / 100 * 0.8; // Max 0.8°C reduction in ground heating from full cloud cover

  let regionalTemperatureAnomaly = (absorbedIrradiance * tempAnomalyFactor) - cloudCoolingEffect;
  regionalTemperatureAnomaly = parseFloat(regionalTemperatureAnomaly.toFixed(2)); // Round for precision

  // Step 5: Calculate Atmospheric Stability Index
  // Higher temperature anomaly and lower pressure typically indicate higher instability.
  // AOD can also contribute to stability via heating/cooling effects (direct vs indirect radiation).
  const pressureEffect = (1013.25 - atmosphericPressure) / 50; // Every 50hPa difference from standard adds/subtracts 1 point
  const tempEffect = regionalTemperatureAnomaly * 0.5; // Every 1°C anomaly adds 0.5 point
  const aodEffect = aerosolOpticalDepth * 1.5; // Higher AOD, more scattering/absorption, can increase stability index (e.g., by creating inversion layers)

  let atmosphericStabilityIndex = pressureEffect + tempEffect + aodEffect;
  atmosphericStabilityIndex = Math.max(0.1, Math.min(5, atmosphericStabilityIndex)); // Cap between 0.1 and 5 for meaningful range
  atmosphericStabilityIndex = parseFloat(atmosphericStabilityIndex.toFixed(2)); // Round for precision

  return {
    predictedPowerFluctuation,
    regionalTemperatureAnomaly,
    atmosphericStabilityIndex
  };
}
