function (inputs) {
  const regionAreaSqKm = Math.max(0, inputs.regionAreaSqKm);
  const avgWaterIntensityPerSqKmPerYearM3 = Math.max(0, inputs.avgWaterIntensityPerSqKmPerYearM3);

  let waterDemandAgriPercent = Math.max(0, inputs.waterDemandAgriPercent);
  let waterDemandInduPercent = Math.max(0, inputs.waterDemandInduPercent);
  let waterDemandResiPercent = Math.max(0, inputs.waterDemandResiPercent);

  const waterSupplyDeficitPercent = Math.min(100, Math.max(0, inputs.waterSupplyDeficitPercent));

  const agriEconomicValuePerM3 = Math.max(0, inputs.agriEconomicValuePerM3);
  const induEconomicValuePerM3 = Math.max(0, inputs.induEconomicValuePerM3);
  const resiEconomicValuePerM3 = Math.max(0, inputs.resiEconomicValuePerM3);

  const environmentalSensitivityFactor = Math.min(10, Math.max(0, inputs.environmentalSensitivityFactor));

  // Input validation and normalization for percentages
  const totalDemandPercentage = waterDemandAgriPercent + waterDemandInduPercent + waterDemandResiPercent;
  if (totalDemandPercentage === 0) {
      return {
          totalBaselineWaterDemandM3: 0,
          waterSupplyShortfallM3: 0,
          economicLossAgri: 0,
          economicLossIndu: 0,
          economicLossResi: 0,
          totalEconomicLoss: 0,
          environmentalImpactScore: 0
      };
  }

  waterDemandAgriPercent /= (totalDemandPercentage / 100);
  waterDemandInduPercent /= (totalDemandPercentage / 100);
  waterDemandResiPercent /= (totalDemandPercentage / 100);

  // 1. Calculate Total Baseline Water Demand
  const totalBaselineWaterDemandM3 = regionAreaSqKm * avgWaterIntensityPerSqKmPerYearM3;

  if (totalBaselineWaterDemandM3 === 0) {
      return {
          totalBaselineWaterDemandM3: 0,
          waterSupplyShortfallM3: 0,
          economicLossAgri: 0,
          economicLossIndu: 0,
          economicLossResi: 0,
          totalEconomicLoss: 0,
          environmentalImpactScore: 0
      };
  }

  // 2. Calculate Sectoral Baseline Demands
  const agriDemand = totalBaselineWaterDemandM3 * (waterDemandAgriPercent / 100);
  const induDemand = totalBaselineWaterDemandM3 * (waterDemandInduPercent / 100);
  const resiDemand = totalBaselineWaterDemandM3 * (waterDemandResiPercent / 100);

  // 3. Calculate Available Water Supply and Total Shortfall
  const availableWaterSupply = totalBaselineWaterDemandM3 * (1 - waterSupplyDeficitPercent / 100);
  const waterSupplyShortfallM3 = Math.max(0, totalBaselineWaterDemandM3 - availableWaterSupply);

  // If no shortfall, no losses
  if (waterSupplyShortfallM3 === 0) {
      return {
          totalBaselineWaterDemandM3: totalBaselineWaterDemandM3,
          waterSupplyShortfallM3: 0,
          economicLossAgri: 0,
          economicLossIndu: 0,
          economicLossResi: 0,
          totalEconomicLoss: 0,
          environmentalImpactScore: 0
      };
  }

  // 4. Distribute Shortfall Proportionally to Demand
  const shortfallRatio = waterSupplyShortfallM3 / totalBaselineWaterDemandM3;

  const agriShortfallM3 = agriDemand * shortfallRatio;
  const induShortfallM3 = induDemand * shortfallRatio;
  const resiShortfallM3 = resiDemand * shortfallRatio;

  // 5. Calculate Economic Losses by Sector
  const economicLossAgri = agriShortfallM3 * agriEconomicValuePerM3;
  const economicLossIndu = induShortfallM3 * induEconomicValuePerM3;
  const economicLossResi = resiShortfallM3 * resiEconomicValuePerM3;

  const totalEconomicLoss = economicLossAgri + economicLossIndu + economicLossResi;

  // 6. Calculate Environmental Impact Score
  // Score is proportional to the overall water deficit and the region's environmental sensitivity.
  // Normalized to a scale of 0-1000
  const environmentalImpactScore = Math.min(1000, (waterSupplyDeficitPercent / 100) * environmentalSensitivityFactor * 100);

  return {
    totalBaselineWaterDemandM3: totalBaselineWaterDemandM3,
    waterSupplyShortfallM3: waterSupplyShortfallM3,
    economicLossAgri: economicLossAgri,
    economicLossIndu: economicLossIndu,
    economicLossResi: economicLossResi,
    totalEconomicLoss: totalEconomicLoss,
    environmentalImpactScore: environmentalImpactScore
  };
}
