function calculate({ snowAccumulationInches, iceAccumulationInches, populationDensity, infrastructureResilience, publicServiceCapacity, stormDurationHours, avgCommuteTimeMinutes, essentialWorkerPercentage }) {

  // Input validation and default values for robustness
  snowAccumulationInches = Math.max(0, parseFloat(snowAccumulationInches) || 0);
  iceAccumulationInches = Math.max(0, parseFloat(iceAccumulationInches) || 0);
  populationDensity = Math.max(1, parseFloat(populationDensity) || 1); // Min 1 to avoid log(0)
  infrastructureResilience = Math.min(5, Math.max(1, parseInt(infrastructureResilience) || 3));
  publicServiceCapacity = Math.min(5, Math.max(1, parseInt(publicServiceCapacity) || 3));
  stormDurationHours = Math.max(0, parseFloat(stormDurationHours) || 0);
  avgCommuteTimeMinutes = Math.max(0, parseFloat(avgCommuteTimeMinutes) || 0);
  essentialWorkerPercentage = Math.min(100, Math.max(0, parseFloat(essentialWorkerPercentage) || 0));

  // 1. Calculate Meteorological Impact Score
  // Ice has a significantly higher disruption potential per inch than snow.
  const snowImpact = snowAccumulationInches * 0.7; // 1 inch snow adds 0.7 impact points
  const iceImpact = iceAccumulationInches * 10.0;  // 1 inch ice adds 10.0 impact points
  const metImpact = Math.min(snowImpact + iceImpact, 30); // Cap raw meteorological impact

  // 2. Calculate Socio-Economic & Infrastructural Vulnerability Factors

  // Population Factor: Logarithmic scale as impact doesn't increase linearly with density
  // Higher density means more people affected, more complex logistics
  const popFactor = Math.log10(populationDensity / 100 + 1) * 3; // e.g., 100 p/sq mi -> log10(2)*3 ~ 0.9; 1000 p/sq mi -> log10(11)*3 ~ 3.1

  // Infrastructure Resilience Factor: Lower rating (1=Very Low) means higher disruption potential
  const resilienceFactor = (6 - infrastructureResilience); // Range 1 (resilient) to 5 (vulnerable)

  // Public Service Capacity Factor: Lower rating (1=Very Low) means higher disruption potential
  const capacityFactor = (6 - publicServiceCapacity); // Range 1 (high capacity) to 5 (low capacity)

  // Storm Duration Factor: Longer duration exacerbates problems, but with diminishing returns (capped)
  const durationFactor = Math.min(stormDurationHours / 4, 3); // e.g., 4h=1, 8h=2, 12h=3, caps at 3

  // Average Commute Time Factor: Longer commutes imply higher reliance on transportation, greater disruption
  const commuteFactor = Math.min(avgCommuteTimeMinutes / 20, 3); // e.g., 20min=1, 40min=2, 60min=3, caps at 3

  // Essential Worker Factor: Lower percentage means fewer critical services maintained during disruption
  // Inverted: (100 - essentialWorkerPercentage) / 100 => higher result for lower essential worker % 
  const essentialWorkerFactor = (100 - essentialWorkerPercentage) / 100 * 2;

  // 3. Combine Factors to Compute Raw Disruption Score
  // Meteorological impact is the base, amplified by population density.
  // Other factors add to the overall vulnerability.
  let rawDisruptionScore = 
    (metImpact * (1 + popFactor * 0.5)) + 
    (resilienceFactor * 0.5) + 
    (capacityFactor * 0.5) + 
    (durationFactor * 0.8) + 
    (commuteFactor * 0.6) + 
    (essentialWorkerFactor * 0.7);

  // Apply a final scaling to fit within a 0-100 index range
  // A maximum plausible raw score (e.g., around 60 for extreme scenarios) maps to 100.
  const disruptionIndex = Math.min(Math.max(rawDisruptionScore / 0.6, 0), 100);

  // 4. Determine Disruption Category
  let impactCategory;
  if (disruptionIndex <= 20) {
    impactCategory = "Minor Disruption: Localized inconveniences, minimal widespread impact.";
  } else if (disruptionIndex <= 40) {
    impactCategory = "Moderate Disruption: Noticeable impacts on daily life, some service interruptions.";
  } else if (disruptionIndex <= 60) {
    impactCategory = "Significant Disruption: Widespread impacts, potential for travel hazards, power outages.";
  } else if (disruptionIndex <= 80) {
    impactCategory = "Severe Disruption: Major societal impacts, extensive outages, dangerous travel conditions, potential for emergencies.";
  } else {
    impactCategory = "Catastrophic Disruption: Systemic failures, prolonged and widespread impacts, significant threat to public safety and economy.";
  }

  return {
    disruptionIndex: parseFloat(disruptionIndex.toFixed(2)),
    impactCategory: impactCategory
  };
}
