const current_gross_profit = current_annual_revenue - current_annual_cogs;
const current_pre_tax_profit = current_gross_profit - current_annual_operating_expenses;
const current_tax_rate_decimal = Math.max(0, Math.min(100, current_effective_tax_rate)) / 100;
const current_tax_amount = current_pre_tax_profit > 0 ? current_pre_tax_profit * current_tax_rate_decimal : 0;
const current_net_profit = current_pre_tax_profit - current_tax_amount;

const projected_new_annual_revenue = current_annual_revenue * (1 + projected_revenue_change_percentage / 100);

const current_cogs_ratio = current_annual_revenue > 0 ? current_annual_cogs / current_annual_revenue : 0;
const projected_new_annual_cogs = projected_new_annual_revenue * current_cogs_ratio;

const projected_new_annual_operating_expenses = current_annual_operating_expenses * (1 + projected_operational_cost_increase_percentage / 100);

const projected_new_pre_tax_profit = projected_new_annual_revenue - projected_new_annual_cogs - projected_new_annual_operating_expenses;

const new_effective_tax_rate = Math.max(0, Math.min(100, current_effective_tax_rate + projected_tax_rate_change_percentage_points));
const new_tax_rate_decimal = new_effective_tax_rate / 100;

const projected_new_tax_amount = projected_new_pre_tax_profit > 0 ? projected_new_pre_tax_profit * new_tax_rate_decimal : 0;

const projected_new_net_profit = projected_new_pre_tax_profit - projected_new_tax_amount;

const projected_annual_profit_change = projected_new_net_profit - current_net_profit;

const projected_new_net_profit_margin = projected_new_annual_revenue > 0 ? (projected_new_net_profit / projected_new_annual_revenue) * 100 : 0;

const total_first_year_financial_impact = projected_annual_profit_change - one_time_implementation_cost;

return {
    projected_new_annual_revenue,
    projected_annual_profit_change,
    projected_new_net_profit,
    projected_new_net_profit_margin,
    total_first_year_financial_impact
};
