const { incidentSeverity, estimatedCasualties, distanceKm, avgTravelSpeed, availablePersonnel, availableEquipment, terrainDifficulty } = inputs;

const BASE_PERSONNEL_PER_CASUALTY = 1.5;
const BASE_EQUIPMENT_PER_CASUALTY = 2.0;

// Input validation/defaults to prevent division by zero or negative values
const effectiveAvgTravelSpeed = Math.max(avgTravelSpeed, 1); // Minimum speed to avoid division by zero
const effectiveDistanceKm = Math.max(distanceKm, 0); // Distance cannot be negative
const effectiveCasualties = Math.max(estimatedCasualties, 1); // Assume at least one casualty for base resource calculation
const effectiveSeverity = Math.max(1, Math.min(incidentSeverity, 10)); // Cap severity between 1 and 10
const effectiveTerrainDifficulty = Math.max(1, Math.min(terrainDifficulty, 3)); // Cap terrain between 1 and 3

// 1. Calculate Estimated Response Time
const travelTimeHours = effectiveDistanceKm / effectiveAvgTravelSpeed;
const estimatedResponseTime = Math.max(travelTimeHours, 0.5); // Minimum response time of 0.5 hours

// 2. Calculate Multipliers
const severityMultiplier = 0.5 + (effectiveSeverity / 10); // Ranges from 0.6 (severity 1) to 1.5 (severity 10)
const terrainMultiplier = effectiveTerrainDifficulty; // Ranges from 1 to 3

// Add a factor for very long response times (e.g., > 6 hours) to account for logistics/sustained operations
const longResponseTimeFactor = estimatedResponseTime > 6 ? 1.2 : 1; // 20% increase for prolonged response

// 3. Calculate Base Needs based on casualties
const basePersonnelNeeded = effectiveCasualties * BASE_PERSONNEL_PER_CASUALTY;
const baseEquipmentNeeded = effectiveCasualties * BASE_EQUIPMENT_PER_CASUALTY;

// 4. Calculate Adjusted Needs using all multipliers
const adjustedPersonnelNeeded = basePersonnelNeeded * severityMultiplier * terrainMultiplier * longResponseTimeFactor;
const adjustedEquipmentNeeded = baseEquipmentNeeded * severityMultiplier * terrainMultiplier * longResponseTimeFactor;

// 5. Determine Recommended Additional Resources (considering currently available resources)
const recommendedPersonnel = Math.max(0, Math.ceil(adjustedPersonnelNeeded) - availablePersonnel);
const recommendedEquipment = Math.max(0, Math.ceil(adjustedEquipmentNeeded) - availableEquipment);

return {
    recommendedPersonnel: recommendedPersonnel,
    recommendedEquipment: recommendedEquipment,
    estimatedResponseTime: estimatedResponseTime
};
