function calculate({
  baseProjectCost, 
  remotenessDifficulty,
  materialTransportDistance,
  specializedLaborPercentage,
  environmentalComplianceLevel,
  projectDurationMonths,
  localInfrastructureSupport,
  contingencyPercentage
}) {
  // Input validation and defaults
  baseProjectCost = parseFloat(baseProjectCost) || 0;
  materialTransportDistance = parseFloat(materialTransportDistance) || 0;
  specializedLaborPercentage = parseFloat(specializedLaborPercentage) || 0;
  projectDurationMonths = parseInt(projectDurationMonths) || 1;
  contingencyPercentage = parseFloat(contingencyPercentage) || 0;

  if (baseProjectCost <= 0 || projectDurationMonths <= 0) {
    return {
      adjustedBaseCost: 0,
      logisticsTransportationCost: 0,
      specializedLaborPremiumCost: 0,
      environmentalComplianceCost: 0,
      totalSubtotalCost: 0,
      contingencyAmount: 0,
      finalProjectCostEstimate: 0
    };
  }

  // --- Step 1: Define Cost Multipliers and Base Factors ---

  // Remoteness Multiplier (affects base cost, logistics, labor, env)
  let remotenessMultiplier;
  switch (remotenessDifficulty) {
    case 'minimal': remotenessMultiplier = 1.05; break;
    case 'moderate': remotenessMultiplier = 1.15; break;
    case 'challenging': remotenessMultiplier = 1.35; break;
    case 'extreme': remotenessMultiplier = 1.60; break;
    default: remotenessMultiplier = 1.10; // Default to moderate if invalid or missing
  }

  // Environmental Compliance Multiplier (applied to a portion of base cost)
  let envComplianceFactor;
  switch (environmentalComplianceLevel) {
    case 'standard': envComplianceFactor = 0.05; break; // 5% of a portion of base cost
    case 'enhanced': envComplianceFactor = 0.12; break; // 12%
    case 'strict': envComplianceFactor = 0.25; break; // 25%
    default: envComplianceFactor = 0.08; // Default to enhanced if invalid or missing
  }

  // Local Infrastructure Support Factor (reduces/increases logistics costs)
  let localInfraFactor;
  switch (localInfrastructureSupport) {
    case 'excellent': localInfraFactor = 0.8; break; // 20% reduction
    case 'adequate': localInfraFactor = 0.9; break; // 10% reduction
    case 'limited': localInfraFactor = 1.1; break; // 10% increase
    case 'non-existent': localInfraFactor = 1.3; break; // 30% increase
    default: localInfraFactor = 1.0; // No effect if invalid or missing
  }

  // --- Step 2: Calculate Individual Cost Components ---

  // Adjusted Base Cost (accounts for general remote overhead, initial site prep, etc.)
  const adjustedBaseCost = baseProjectCost * remotenessMultiplier;

  // Logistics and Material Transportation Cost
  // Base transport cost per km per $ of base project (rough proxy for material volume/weight)
  const baseTransportRatePerKm = 0.00005; // e.g., $0.05 per $1000 base cost per km
  let logisticsTransportationCost = baseProjectCost * baseTransportRatePerKm * materialTransportDistance;
  // Further adjust by remoteness and local infrastructure
  logisticsTransportationCost *= remotenessMultiplier * localInfraFactor;
  // Cap minimum logistics cost to avoid unrealistically low values
  logisticsTransportationCost = Math.max(logisticsTransportationCost, baseProjectCost * 0.05); // At least 5% of base cost as a minimum for remote logistics

  // Specialized Labor Premium & Accommodation Cost
  // This is an *additional* cost, beyond the base labor assumed in baseProjectCost.
  // This premium covers higher wages, benefits, isolation pay, and accommodation/travel for specialized remote staff.
  const laborPremiumRate = 0.015; // 1.5% of base project cost per % specialized labor per month
  let specializedLaborPremiumCost = baseProjectCost * (specializedLaborPercentage / 100) * laborPremiumRate * projectDurationMonths;
  // Adjust by remoteness for higher difficulty/isolation pay, and cap it.
  specializedLaborPremiumCost *= remotenessMultiplier;
  specializedLaborPremiumCost = Math.min(specializedLaborPremiumCost, baseProjectCost * 2.0); // Max 200% of base cost, indicative cap to prevent extreme values

  // Environmental Compliance & Mitigation Cost
  // A portion of the base project value is allocated to environmental considerations.
  const environmentalBasePercentage = 0.1; // Base 10% of project value considered for environmental impact
  let environmentalComplianceCost = adjustedBaseCost * environmentalBasePercentage * envComplianceFactor;
  // Remoteness often increases monitoring, access for studies, specific challenges. Adjusted multiplier.
  environmentalComplianceCost *= (remotenessMultiplier - 0.5); // Slightly lower influence than direct project costs, but still significant
  environmentalComplianceCost = Math.max(environmentalComplianceCost, baseProjectCost * 0.01); // Minimum 1% of base cost for environmental

  // --- Step 3: Calculate Subtotal and Contingency ---

  const totalSubtotalCost = adjustedBaseCost + logisticsTransportationCost + specializedLaborPremiumCost + environmentalComplianceCost;

  // Contingency Fund (applied to the sum of all estimated costs before final total)
  const contingencyAmount = totalSubtotalCost * (contingencyPercentage / 100);

  // --- Step 4: Final Project Cost Estimate ---

  const finalProjectCostEstimate = totalSubtotalCost + contingencyAmount;

  return {
    adjustedBaseCost: adjustedBaseCost,
    logisticsTransportationCost: logisticsTransportationCost,
    specializedLaborPremiumCost: specializedLaborPremiumCost,
    environmentalComplianceCost: environmentalComplianceCost,
    totalSubtotalCost: totalSubtotalCost,
    contingencyAmount: contingencyAmount,
    finalProjectCostEstimate: finalProjectCostEstimate
  };
}
