function calculate({ numStudents, numTeachers, avgTeacherDailyCost, dailyOperationalCostOpenSchool, dailyRemoteLearningTechSupportCost, dailyTransportationCost, dailyFoodServiceCost, percLearningLossNoInstruction, percLearningLossRemoteInstruction, valuePerDayOfLearningPerStudent }) {
    // Input validation and default values to ensure non-negative and within bounds
    numStudents = Math.max(0, numStudents || 0);
    numTeachers = Math.max(0, numTeachers || 0);
    avgTeacherDailyCost = Math.max(0, avgTeacherDailyCost || 0);
    dailyOperationalCostOpenSchool = Math.max(0, dailyOperationalCostOpenSchool || 0);
    dailyRemoteLearningTechSupportCost = Math.max(0, dailyRemoteLearningTechSupportCost || 0);
    dailyTransportationCost = Math.max(0, dailyTransportationCost || 0);
    dailyFoodServiceCost = Math.max(0, dailyFoodServiceCost || 0);
    percLearningLossNoInstruction = Math.min(100, Math.max(0, percLearningLossNoInstruction || 0));
    percLearningLossRemoteInstruction = Math.min(100, Math.max(0, percLearningLossRemoteInstruction || 0));
    valuePerDayOfLearningPerStudent = Math.max(0, valuePerDayOfLearningPerStudent || 0);

    // Step 1: Calculate Baseline Open School Day Cost
    // This represents the full cost if school were operating normally.
    const teacherSalariesOpenDay = numTeachers * avgTeacherDailyCost;
    const baselineOpenSchoolDayCost = teacherSalariesOpenDay + dailyOperationalCostOpenSchool + dailyTransportationCost + dailyFoodServiceCost;

    // Step 2: Calculate Costs and Impacts for Traditional Closure Scenario
    // Assume teachers are paid even during closure, but most other operational costs are avoided.
    // Academic loss is high due to no instruction.
    const directExpenditureTraditionalClosure = teacherSalariesOpenDay; 
    const operationalSavingsTraditionalClosure = baselineOpenSchoolDayCost - directExpenditureTraditionalClosure;
    const academicLossCostTraditionalClosure = numStudents * valuePerDayOfLearningPerStudent * (percLearningLossNoInstruction / 100);

    // Step 3: Calculate Costs and Impacts for Remote Learning Scenario
    // Assume teachers are paid and providing instruction, incurring specific remote learning costs.
    // Most physical operational costs (transport, food, primary facility use) are still avoided.
    // Academic loss is mitigated compared to no instruction, but still present.
    const directExpenditureRemoteLearning = teacherSalariesOpenDay + dailyRemoteLearningTechSupportCost;
    const operationalSavingsRemoteLearning = baselineOpenSchoolDayCost - directExpenditureRemoteLearning;
    const academicLossCostRemoteLearning = numStudents * valuePerDayOfLearningPerStudent * (percLearningLossRemoteInstruction / 100);

    // Step 4: Calculate Net Differences and Benefits between Remote Learning and Traditional Closure
    // Net Operational Cost Delta: Positive value means Remote Learning is more expensive operationally.
    const netOperationalCostDeltaRemoteVsClosure = directExpenditureRemoteLearning - directExpenditureTraditionalClosure; 

    // Academic Impact Benefit: Positive value means Remote Learning reduces academic loss (is more beneficial academically).
    const academicImpactBenefitRemoteVsClosure = academicLossCostTraditionalClosure - academicLossCostRemoteLearning; 

    // Total Net Benefit: Combines the academic benefit with the operational cost difference.
    // A positive value indicates that remote learning is financially and academically more beneficial overall.
    const totalNetBenefitRemoteVsClosure = academicImpactBenefitRemoteVsClosure - netOperationalCostDeltaRemoteVsClosure; 

    return {
        baselineOpenSchoolDayCost,
        directExpenditureTraditionalClosure,
        directExpenditureRemoteLearning,
        operationalSavingsTraditionalClosure,
        operationalSavingsRemoteLearning,
        netOperationalCostDeltaRemoteVsClosure,
        academicLossCostTraditionalClosure,
        academicLossCostRemoteLearning,
        academicImpactBenefitRemoteVsClosure,
        totalNetBenefitRemoteVsClosure
    };
}
