function calculate({ distanceToNearestMedicalFacility, transportType, terrainComplexity, weatherConditionImpact, availablePersonnelLevel, patientMedicalSeverity, communicationReliability, preparednessLevel }) {
    const distance = parseFloat(distanceToNearestMedicalFacility);
    const terrain = parseFloat(terrainComplexity);
    const weather = parseFloat(weatherConditionImpact);
    const personnel = parseFloat(availablePersonnelLevel);
    const severity = parseFloat(patientMedicalSeverity);
    const comm = parseFloat(communicationReliability);
    const prep = parseFloat(preparednessLevel);

    const transportSpeeds = {
        "Helicopter": 200, // km/h
        "Fixed-wing Aircraft": 300, // km/h (requires suitable landing strip)
        "Ground Vehicle": 30, // km/h (assumes off-road capability for remote areas)
        "Boat": 40, // km/h
        "Foot": 5 // km/h
    };

    const baseResponseDelayFactor = 0.75; // Base delay for initial call, dispatch, and crew prep (hours)
    const baseStabilizationTimes = { // Hours based on severity
        1: 0.5, // Minor: e.g., sprain, small cut
        2: 1.0, // Moderate: e.g., simple fracture, moderate laceration
        3: 1.75, // Serious: e.g., severe fracture, moderate trauma
        4: 2.75, // Severe: e.g., significant trauma, internal injury
        5: 4.0  // Critical/Life-threatening: e.g., severe head injury, massive hemorrhage
    };

    let selectedSpeed = transportSpeeds[transportType] || transportSpeeds["Ground Vehicle"]; // Default to Ground Vehicle if type not recognized

    // --- Factor Calculations ---

    // 1. Terrain Impact on Speed: Higher complexity reduces speed
    // Multiplier: 1 for terrain 1, down to 0.5 for terrain 5 (50% speed reduction)
    const terrainSpeedFactor = 1 - (0.1 * (terrain - 1)); // 1.0 for 1, 0.9 for 2, ..., 0.6 for 5
    selectedSpeed *= terrainSpeedFactor;

    // 2. Weather Impact on Speed and Initial Response Delays: Higher impact reduces speed and adds delays
    // Speed reduction: 1 for 0, down to 0.4 for 1 (60% speed reduction)
    const weatherSpeedFactor = 1 - (weather * 0.6); 
    selectedSpeed *= weatherSpeedFactor;

    // Additional weather delay for initial response (0 to 3 hours)
    const weatherResponseDelay = weather * 3;

    // 3. Communication Reliability Impact on Response Delays: Lower reliability increases delays
    // Multiplier for initial response time: 2.0 for 0, 1.0 for 1
    const communicationResponseFactor = 1 + ((1 - comm) * 1.0); // 2.0 for 0, 1.0 for 1

    // 4. Preparedness Level Impact on Initial Response & Coordination Delays: Lower preparedness increases delays
    // Multiplier for initial response time: 1.75 for 0, 1.0 for 1
    const preparednessResponseFactor = 1 + ((1 - prep) * 0.75); // 1.75 for 0, 1.0 for 1

    // 5. Patient Medical Severity Impact on Stabilization Time: Higher severity increases time
    let stabilizationTime = baseStabilizationTimes[severity] || baseStabilizationTimes[3]; // Default to 'Serious' if invalid severity

    // 6. Available Personnel Level Impact on Stabilization Time: Better personnel reduces time
    // Reduction: No reduction for 1, up to 30% reduction for 3
    const personnelStabilizationFactor = 1 - (0.15 * (personnel - 1)); // 1.0 for 1, 0.85 for 2, 0.70 for 3
    stabilizationTime *= personnelStabilizationFactor;

    // Ensure selected speed does not drop below a realistic minimum for any transport type
    selectedSpeed = Math.max(selectedSpeed, 2); // Minimum travel speed (e.g., 2 km/h for extreme foot travel)

    // --- Calculation Steps ---

    // Step 1: Calculate initial response time (dispatch, travel to scene)
    // Base delay + travel to scene + weather delay + comms delay + preparedness delay
    let estimatedResponseTimeHours = baseResponseDelayFactor; // Initial call & dispatch time

    // If distance > 0, calculate travel time to scene. If 0 (patient at base), no travel to scene.
    if (distance > 0) {
        const travelToSceneTime = distance / selectedSpeed; 
        estimatedResponseTimeHours += travelToSceneTime;
    }

    estimatedResponseTimeHours += weatherResponseDelay;
    estimatedResponseTimeHours *= communicationResponseFactor; // Apply comms factor to overall response time
    estimatedResponseTimeHours *= preparednessResponseFactor; // Apply preparedness factor to overall response time

    // Cap estimated response time for extreme scenarios, e.g., max 96 hours for initial response
    estimatedResponseTimeHours = Math.min(estimatedResponseTimeHours, 96);
    // Ensure a minimum response time for any real-world scenario
    estimatedResponseTimeHours = Math.max(estimatedResponseTimeHours, 0.5); 

    // Step 2: Calculate evacuation travel time (from scene to medical facility)
    // This is the actual transport time after patient stabilization.
    let estimatedEvacuationTravelTimeHours = 0;
    if (distance > 0) {
        estimatedEvacuationTravelTimeHours = distance / selectedSpeed;
    }
    // Cap evacuation travel time, e.g., max 120 hours
    estimatedEvacuationTravelTimeHours = Math.min(estimatedEvacuationTravelTimeHours, 120);

    // Step 3: Total estimated time
    const totalEstimatedTimeHours = estimatedResponseTimeHours + stabilizationTime + estimatedEvacuationTravelTimeHours;

    // --- Format Outputs ---
    const formatTime = (hours) => {
        const h = Math.floor(hours);
        const m = Math.round((hours - h) * 60);
        return `${h} hours ${m} minutes`;
    };

    return {
        estimatedResponseTime: formatTime(estimatedResponseTimeHours),
        estimatedEvacuationTime: formatTime(stabilizationTime + estimatedEvacuationTravelTimeHours),
        totalEstimatedTime: formatTime(totalEstimatedTimeHours)
    };
}
