function calculate({ initialHardwareSoftwareCost, installationIntegrationCost, annualConnectivityDataCost, annualMaintenanceSupportCost, annualReducedSiteVisitsSavings, annualOperationalEfficiencyGain, potentialCostOfMajorFailure, probabilityOfMajorFailure, projectLifespanYears, discountRate }) {

  // Input validation and default values
  initialHardwareSoftwareCost = Number(initialHardwareSoftwareCost) || 0;
  installationIntegrationCost = Number(installationIntegrationCost) || 0;
  annualConnectivityDataCost = Number(annualConnectivityDataCost) || 0;
  annualMaintenanceSupportCost = Number(annualMaintenanceSupportCost) || 0;
  annualReducedSiteVisitsSavings = Number(annualReducedSiteVisitsSavings) || 0;
  annualOperationalEfficiencyGain = Number(annualOperationalEfficiencyGain) || 0;
  potentialCostOfMajorFailure = Number(potentialCostOfMajorFailure) || 0;
  probabilityOfMajorFailure = Number(probabilityOfMajorFailure) || 0;
  projectLifespanYears = Number(projectLifespanYears) || 0;
  discountRate = Number(discountRate) || 0;

  // Ensure non-negative and realistic values for probabilities/lifespan
  probabilityOfMajorFailure = Math.max(0, Math.min(1, probabilityOfMajorFailure));
  projectLifespanYears = Math.max(0, Math.round(projectLifespanYears));
  discountRate = Math.max(0, discountRate);

  // 1. Calculate Total Initial Investment
  const totalInitialInvestment = initialHardwareSoftwareCost + installationIntegrationCost;

  // 2. Calculate Total Annual Operating Cost
  const totalAnnualOperatingCost = annualConnectivityDataCost + annualMaintenanceSupportCost;

  // 3. Calculate Annual Risk Reduction Value
  // This represents the expected annual savings from avoiding potential major failures.
  const annualRiskReductionValue = potentialCostOfMajorFailure * probabilityOfMajorFailure;

  // 4. Calculate Total Annual Benefit (before deducting operating costs from benefits)
  const grossAnnualBenefit = annualReducedSiteVisitsSavings + annualOperationalEfficiencyGain + annualRiskReductionValue;

  // 5. Calculate Net Annual Benefit (after deducting operating costs from gross benefits)
  const totalAnnualBenefit = grossAnnualBenefit - totalAnnualOperatingCost;

  // Initialize outputs
  let paybackPeriodYears = Infinity;
  let roiPercentage = -100; // Default to worst case
  let netPresentValue = -totalInitialInvestment; // Start NPV with initial investment

  // Edge case: If initial investment is zero, or if benefits are negative, calculations diverge.
  if (totalInitialInvestment <= 0) {
      if (totalAnnualBenefit > 0) {
          paybackPeriodYears = 0; // Immediate payback if no initial investment and positive benefit
          roiPercentage = Infinity; // Infinite ROI
      } else {
          // No initial investment, but no positive benefit or negative benefit.
          // ROI is 0 or negative infinity, payback is undefined or infinite.
          // For simplicity, let's keep it 0 or -100 for percentage and Infinity for payback.
          paybackPeriodYears = totalAnnualBenefit >= 0 ? 0 : Infinity;
          roiPercentage = totalAnnualBenefit >= 0 ? Infinity : -100;
      }
  } else if (totalAnnualBenefit <= 0) {
      paybackPeriodYears = Infinity; // Will never pay back if annual benefit is non-positive
      roiPercentage = -100; // At best -100% if no benefit, worse if negative
  } else {
      paybackPeriodYears = totalInitialInvestment / totalAnnualBenefit;
      // Cap payback period for display if it exceeds a reasonable max, e.g., 100 years
      paybackPeriodYears = Math.min(paybackPeriodYears, 9999);

      // Calculate ROI over project lifespan
      const totalBenefitOverLifespan = totalAnnualBenefit * projectLifespanYears;
      roiPercentage = ((totalBenefitOverLifespan - totalInitialInvestment) / totalInitialInvestment) * 100;
  }

  // Calculate Net Present Value (NPV)
  // If discountRate is 0, then no discounting happens
  if (projectLifespanYears > 0) {
      for (let t = 1; t <= projectLifespanYears; t++) {
          if (discountRate > 0) {
              netPresentValue += totalAnnualBenefit / Math.pow((1 + discountRate), t);
          } else { // No discounting if rate is 0
              netPresentValue += totalAnnualBenefit;
          }
      }
  }

  return {
    totalInitialInvestment: totalInitialInvestment,
    totalAnnualOperatingCost: totalAnnualOperatingCost,
    annualRiskReductionValue: annualRiskReductionValue,
    totalAnnualBenefit: totalAnnualBenefit, // This is the net annual cash flow for ROI/NPV calc
    paybackPeriodYears: paybackPeriodYears,
    roiPercentage: roiPercentage,
    netPresentValue: netPresentValue
  };
}
