const currentSalary = inputs.currentSalary;
const currentCoLI = inputs.currentCoLI;
const remoteCoLI = inputs.remoteCoLI;
const expectedRemoteSalaryOffer = inputs.expectedRemoteSalaryOffer || null;
const nonCoLAdjustment = inputs.nonCoLAdjustment || 0;

// Defensive programming: prevent division by zero or invalid CoLI
if (currentCoLI <= 0 || remoteCoLI <= 0) {
    throw new Error("Cost of Living Index must be greater than zero.");
}
if (currentSalary <= 0) {
    throw new Error("Current Annual Gross Salary must be greater than zero.");
}

// 1. Calculate the raw cost of living ratio
const costOfLivingRatio = remoteCoLI / currentCoLI;

// 2. Determine the base recommended salary to maintain equivalent purchasing power
let baseRecommendedSalary = currentSalary * costOfLivingRatio;

// 3. Apply any additional non-CoL lifestyle adjustments
let recommendedSalary = baseRecommendedSalary + nonCoLAdjustment;

// 4. Calculate Purchasing Power Parity (how much more/less your money buys in the new location)
// This indicates how many units of currency in the remote location are equivalent to one unit in the current location.
// Example: currentCoLI=100, remoteCoLI=75. Your money goes 100/75 = 1.33 times further. So 133%.
const purchasingPowerParity = (currentCoLI / remoteCoLI) * 100;

// 5. Calculate the difference if an expected offer is provided
let salaryImpactVsOffer = null;
if (expectedRemoteSalaryOffer !== null) {
    salaryImpactVsOffer = expectedRemoteSalaryOffer - recommendedSalary;
}

// 6. Calculate the overall percentage change in salary
const effectiveSalaryChange = ((recommendedSalary - currentSalary) / currentSalary) * 100;

return {
    recommendedSalary: recommendedSalary,
    purchasingPowerParity: purchasingPowerParity,
    salaryImpactVsOffer: salaryImpactVsOffer,
    effectiveSalaryChange: effectiveSalaryChange
};
