function calculateFeasibility(inputs) {
  const { 
    resourcePotentialScore,
    projectCapacityMW,
    initialCapexPerMW,
    omCostPerMWPerYear,
    regulatoryEnvironmentScore,
    gridIntegrationComplexityScore,
    landCostPerAcre,
    landUseEfficiencyAcresPerMW,
    avgAnnualPowerPriceCentsPerKWh
  } = inputs;

  // --- Constants and Assumptions ---
  const projectLifetimeYears = 25; // Standard assumption for many renewable projects
  const hoursPerYear = 8760; // Total hours in a year
  // Capacity factors (adjust based on technology type, user can infer from input values)
  // For this general calculator, we'll use a blend/average capacity factor.
  // A typical range for utility-scale solar is 18-25%, for onshore wind 25-45%.
  // Let's use an average for a 'generic' renewable project.
  const assumedCapacityFactor = 0.30; // 30% average capacity factor

  // --- Calculate LCOE (Levelized Cost of Energy) ---
  // 1. Capital Expenditure (CAPEX)
  const totalCapex = projectCapacityMW * initialCapexPerMW;

  // 2. Operational & Maintenance (O&M) Costs over lifetime
  const totalOmCostLifetime = projectCapacityMW * omCostPerMWPerYear * projectLifetimeYears;

  // 3. Land Costs (simplified for lifetime)
  const totalLandCost = projectCapacityMW * landUseEfficiencyAcresPerMW * landCostPerAcre;

  // Total Lifetime Costs (simplified, excluding financing costs for LCOE here for clarity of core components)
  const totalLifetimeCosts = totalCapex + totalOmCostLifetime + totalLandCost;

  // 4. Energy Production over lifetime
  const annualEnergyProductionMWh = projectCapacityMW * hoursPerYear * assumedCapacityFactor;
  const totalLifetimeEnergyProductionMWh = annualEnergyProductionMWh * projectLifetimeYears;

  // Edge case: Prevent division by zero if project capacity or assumed CF is zero
  if (totalLifetimeEnergyProductionMWh === 0) {
    return {
      feasibilityScore: 0,
      estimatedLCOE: Infinity,
      roiPotentialScore: 0
    };
  }

  // LCOE in $/MWh
  const lcoeDollarsPerMWh = totalLifetimeCosts / totalLifetimeEnergyProductionMWh;
  // Convert LCOE to cents/kWh
  const estimatedLCOE = lcoeDollarsPerMWh / 10; // 1 $/MWh = 0.1 cents/kWh

  // --- Calculate Feasibility Score ---
  // Normalize scores to a 0-1 range
  const normalizedResourcePotential = (resourcePotentialScore - 1) / 9; // 1-10 -> 0-1
  const normalizedRegulatoryEnvironment = (regulatoryEnvironmentScore - 1) / 9; // 1-10 -> 0-1
  // For grid integration, lower complexity is better, so invert the score
  const normalizedGridIntegration = 1 - ((gridIntegrationComplexityScore - 1) / 9); // 1-10 -> 0-1 (1=easy, 0=very complex)

  // Calculate a profitability factor based on LCOE and power price
  const profitMarginCentsPerKWh = avgAnnualPowerPriceCentsPerKWh - estimatedLCOE;
  // Cap profitability factor to ensure it doesn't inflate scores excessively from very high prices or low LCOE
  // A margin of 5 cents/kWh is considered excellent, so we cap at that for normalization to 1.
  const profitabilityFactor = Math.min(1, Math.max(0, profitMarginCentsPerKWh / 5)); 

  // Define weights for each factor (sum should be 1)
  const weightResourcePotential = 0.25;
  const weightRegulatoryEnvironment = 0.20;
  const weightGridIntegration = 0.15;
  const weightProfitability = 0.40; // This combines financial viability

  let feasibilityScore = (
    (normalizedResourcePotential * weightResourcePotential) +
    (normalizedRegulatoryEnvironment * weightRegulatoryEnvironment) +
    (normalizedGridIntegration * weightGridIntegration) +
    (profitabilityFactor * weightProfitability)
  ) * 100;

  // Cap feasibility score between 0 and 100
  feasibilityScore = Math.min(100, Math.max(0, feasibilityScore));

  // --- Calculate ROI Potential Score (simplified) ---
  // This score is a simplification, mainly reflecting the spread between LCOE and PPA price
  // Higher profit margin means higher ROI potential.
  let roiPotentialScore = Math.max(0, profitMarginCentsPerKWh) / 1; // 1 cent/kWh margin = 1 point
  roiPotentialScore = Math.min(10, Math.max(0, roiPotentialScore)); // Cap at 10

  return {
    feasibilityScore: feasibilityScore,
    estimatedLCOE: estimatedLCOE,
    roiPotentialScore: roiPotentialScore
  };
}
